        Content_120114_10_3 = {
          ContentJP = "冒険は見事に幕を閉じた。仲間たちと一緒に帰ろう。",
          TextMapId = "Content_120114_10_3"
        },
        Content_120114_10_4 = {
          ContentJP = "カサンドラが治安署の兵士を連れてきた。危機はようやく去った。彼女と話をしよう。",
          TextMapId = "Content_120114_10_4"
        },
        Content_120114_11 = {
          ContentJP = "夜は皆と家でお茶会の約束だ。準備のために帰ろう。",
          TextMapId = "Content_120114_11"
        },
        Content_120114_12 = {
          ContentJP = "ノックの音がした。扉を開けて誰が来たのか確かめよう。",
          TextMapId = "Content_120114_12"
        },
        Content_120114_13 = {
          ContentJP = "魔導書を開き、物語「香る草原へ」を完結させ、小さなナイチンゲールを召喚する魔法を学ぶ。",
          TextMapId = "Content_120114_13"
        },
        Content_120114_2 = {
          ContentJP = "約束の日が来た。ユニコーン{性别:氏|嬢}と合流して、カサンドラに会いに行こう。",
          TextMapId = "Content_120114_2"
        },
        Content_120114_3 = {
          ContentJP = "ユニコーン{性别:氏|嬢}と一緒に、カサンドラとの約束の場所へ向かう。",
          TextMapId = "Content_120114_3"
        },
        Content_120114_3_1 = {
          ContentJP = "カサンドラと口裏を合わせ、ここで彼女が友人を連れてくるのを待とう。",
          TextMapId = "Content_120114_3_1"
        },
        Content_120114_4 = {
          ContentJP = "まさかカサンドラの友人が彼だったとは……急いで帰って身を隠そう。",
          TextMapId = "Content_120114_4"
        },
        Content_120114_5 = {
          ContentJP = "子供たちの情報によれば、マークがセヴィオの行方を知っているかもしれない。尋ねてみよう。",
          TextMapId = "Content_120114_5"
        },
        Content_120114_5_1 = {
          ContentJP = "マークはセヴィオが城外に行ったのを見た。急いで追いかけよう。",
          TextMapId = "Content_120114_5_1"
        },
        Content_120114_6 = {
          ContentJP = "マークはセヴィオが城外に行ったのを見た。急いで追いかけよう。",
          TextMapId = "Content_120114_6"
        },
        Content_120114_7 = {
          ContentJP = "これまでの捜査で得た情報から、セヴィオは山賊に囚われている可能性が高い。拠点はダクリ湖の近くにあるはずだ。ユニコーン{性别:氏|嬢}と共に急ごう。",
          TextMapId = "Content_120114_7"
        },
        Content_120114_8 = {
          ContentJP = "やはりセヴィオは山賊に囚われていた。救い出さねば。",
          TextMapId = "Content_120114_8"
        },
        Content_120114_9 = {
          ContentJP = "山賊を倒し、セヴィオを救い出す。",
          TextMapId = "Content_120114_9"
        },
        Content_120114_9_1 = {
          ContentJP = "戦闘の音が穢獣を引き寄せた。そいつらも倒そう。",
          TextMapId = "Content_120114_9_1"
        },
        Content_200101_1 = {
          ContentJP = "屋敷の外に見覚えのある人影が見える。スノウにそっくりだ。",
          TextMapId = "Content_200101_1"
        },
        Content_200101_2 = {
          ContentJP = "後ろからあなたを呼ぶ声がする。誰だろう？",
          TextMapId = "Content_200101_2"
        },
        Content_200101_3 = {
          ContentJP = "声をかけてきたのは、鍛冶師を自称するルーナ族だ。\nスノウによれば、かなりのお年寄りのようだが、ルーナ族は年齢に関係なく、似たような顔立ちと体格をしている。\nルーナ族の鍛冶師を仲間にすれば、武器を製造してくれるらしい。\n彼女を連れて屋敷に戻ろう。",
          TextMapId = "Content_200101_3"
        },
        Content_200101_4 = {
          ContentJP = "ルーナ族の鍛冶師は製造の腕前にかなり自信があるようだ。\n彼女の実力を確かめよう。",
          TextMapId = "Content_200101_4"
        },
        Content_200102_1 = {
          ContentJP = "約束通り、ヒュプノスの家にサイキの様子を見に行こう。",
          TextMapId = "Content_200102_1"
        },
        Content_200102_2 = {
          ContentJP = "サイキが心を込めて育てた花がしおれてしまった。どうやら栄養が足りないようだ。\n花を元気にするためには、アイスレイク城外の洞窟から特別な土を採取する必要がある。月狩り人にとってはたやすいことだ。",
          TextMapId = "Content_200102_2"
        },
        Content_200102_3 = {
          ContentJP = "サイキによると、ダクリ湖の近くの洞窟にはその特別な土があるらしい。\nその土を集めに行こう。",
          TextMapId = "Content_200102_3"
        },
        Content_200102_4 = {
          ContentJP = "洞窟の入り口の土は、乾燥していて花の栽培に適していない。奥のほうで別の土を探そう。",
          TextMapId = "Content_200102_4"
        },
        Content_200102_5 = {
          ContentJP = "苦労した甲斐があった。ダクリ湖の洞窟の奥でついに花の栽培に適した柔らかい土を見つけた。\nこの土をヒュプノスの家に持ち帰れば、サイキはきっと喜んでくれるだろう。",
          TextMapId = "Content_200102_5"
        },
        Content_200103_1 = {
          ContentJP = "スノウが屋敷で大変だと騒いでいる。\n大したことはないのだろうが、静かにさせるためにも話を聞いてあげよう。",
          TextMapId = "Content_200103_1"
        },
        Content_200103_2 = {
          ContentJP = "アイスレイク城の外で巨大なブタが出現したらしい。\n巨大なって……どれほど大きいんだろう？実際に見に行ってみよう……",
          TextMapId = "Content_200103_2"
        },
        Content_200103_3 = {
          ContentJP = "動物好きかと思われた二人は、なんと冷血なハンターだった。そこへ巨大なブタが現れた。ブタと力を合わせ、悪党どもを蹴散らそう！",
          TextMapId = "Content_200103_3"
        },
        Content_200103_4 = {
          ContentJP = "ジェネモンハンターを片付けた後、改めてこのブタをじっくり観察してみた。\nさっき……喋ってなかった？それとも、声の主は別にいる……？",
          TextMapId = "Content_200103_4"
        },
        Content_200103_5 = {
          ContentJP = "「ジェネモン」システムが解放されました。\n餌をあげて、ジェネモンと仲良しをして、これからの旅に備えよう。",
          TextMapId = "Content_200103_5"
        },
        Content_200104_1 = {
          ContentJP = "最近、アスフォデロスでも巨大なブタの噂が広まっている。トリュフはまだどこかにいるのか？\nカミラに事情を聞いてみよう。",
          TextMapId = "Content_200104_1"
        },
        Content_200104_3 = {
          ContentJP = "やはりトリュフはまだ城外にいた。まさか、あのジェネモンハンターたちがまた現れたのか？\n城外でトリュフを見つけよう。きっと助けが必要なはずだ。",
          TextMapId = "Content_200104_3"
        },
        Content_200104_4 = {
          ContentJP = "やっぱり、あの憎たらしいジェネモンハンターどもだ。またジェネモンに手を出したらしい。\nなんて勝手な連中だ。必ず懲らしめないと！……だが、その前にまず居場所を突き止めよう。",
          TextMapId = "Content_200104_4"
        },
        Content_200104_5 = {
          ContentJP = "また下水道か……心の醜い者ほど、下水道に逃げ込むのが好きらしい。\nジェネモンたちがこれ以上、下水道の穢れに染まる前に、急いで見つけよう。",
          TextMapId = "Content_200104_5"
        },
        Content_200104_6 = {
          ContentJP = "下水道の臭いでヘーゼルナッツの嗅覚も鈍っている。だが、賢いジェネモンが前方で道を示しているようだ。\n痕跡をたどって、さらに奥へ進もう。",
          TextMapId = "Content_200104_6"
        },
        Content_200104_7 = {
          ContentJP = "下水道の奥でジェネモンハンターたちに遭遇した。過去を振り返り、未来を語っているようだ。\nやつらの過去や未来なんてどうでもいい。まずは叩きのめしてやろう。",
          TextMapId = "Content_200104_7"
        },
        Content_200104_8 = {
          ContentJP = "たくさんのジェネモン友達ができた。それはいいことだが、友達が多すぎるのも悩みの種だ。\n友達の中でも特に頼りになるカミラに相談しよう。きっと何とかしてくれる……はず！",
          TextMapId = "Content_200104_8"
        },
        Content_200104_9 = {
          ContentJP = "ジェネモンたちを連れて逃げ出そう。善意を携え、道を探せ。さあ、この暗い下水道を出て、陽の下へ。",
          TextMapId = "Content_200104_9"
        },
        Content_200201_1 = {
          ContentJP = "アイスレイク城の側門には、マッシュルームのクリームスープの香りを漂わせる親切な老婦人サリーがいる。\nサリーは最近何か悩んでいるようだ。彼女と話してみよう。",
          TextMapId = "Content_200201_1"
        },
        Content_200201_2 = {
          ContentJP = "サリーはどうやらある女性のことで悩んでいるようだ。その女性を見かけたら、話しかけてみよう。",
          TextMapId = "Content_200201_2"
        },
        Content_200201_3 = {
          ContentJP = "セリーヌ夫人は長い間、「ハーメル‐ディキンソン」というカロンの男性を孤独に待ち続けている。彼女とても思い悩んでいるようだ。セリーヌ夫人を手伝ってハーメル‐ディキンソンの手がかりを探そう。",
          TextMapId = "Content_200201_3"
        },
        Content_200201_4 = {
          ContentJP = "廃坑の奥に進み、ハーメル‐ディキンソンの手がかりを探そう。",
          TextMapId = "Content_200201_4"
        },
        Content_200201_5 = {
          ContentJP = "あなたは廃坑の奥深くで、日記帳を見つけた。書物の扉には所有者の名前、ハーメル‐ディキンソンという文字が書かれている。\nどうやら、この日記にはハーメルのことが記録されているようだ。アイスレイク城に戻り、この日記をセリーヌ夫人に渡そう。",
          TextMapId = "Content_200201_5"
        },
        Content_200201_6 = {
          ContentJP = "日記を読み終えたセリーヌ夫人は、しばらく一人でいたいと言う。\n 彼女の侍女サリーを訪ねて報酬を受け取り、昔の出来事について話を聞こう。",
          TextMapId = "Content_200201_6"
        },
        Content_200201_7 = {
          ContentJP = "あなたは日記の存在を隠した。おそらくそれが最善の選択だろう。\n セリーヌ夫人の侍女サリーを訪ね、報酬を受け取りつつ、セリーヌ夫人とハメルの物語を聞いてみよう。",
          TextMapId = "Content_200201_7"
        },
        Content_200202_1 = {
          ContentJP = "城門の近くで男性が大声を出している。何かを売っているようだ。",
          TextMapId = "Content_200202_1"
        },
        Content_200202_2 = {
          ContentJP = "自称「ゴミ拾いの王様」ことマーティンはあなたに「失せ物」を手渡し、持ち主を探すようアドバイスした。",
          TextMapId = "Content_200202_2"
        },
        Content_200202_3 = {
          ContentJP = "期限切れの切符で折られた紙の星。もしかしたらあの切符売りの「失せ物」かもしれない。\nただ……彼女はなんというか……とても礼儀正しい人のようだ。",
          TextMapId = "Content_200202_3"
        },
        Content_200203_1 = {
          ContentJP = "女性の声はしゃがれ、仕立てのいい服も薄汚れてしまっている。\n彼女は道行く人々をひとりひとり呼び止めて、何かを探しているようだ……",
          TextMapId = "Content_200203_1"
        },
        Content_200203_12 = {
          ContentJP = "あなたがあの悲しげな中年女性のもとを離れて以来、誰かがずっとあなたを見つめている。彼女は何か言いたげな様子だ。",
          TextMapId = "Content_200203_12"
        },
        Content_200203_13 = {
          ContentJP = "他の人にも何か手がかりがないか聞いてみよう。",
          TextMapId = "Content_200203_13"
        },
        Content_200203_14 = {
          ContentJP = "周囲に事情を知る者がまだいるようだ。もう一度、あの医者の行方を聞いてみよう。",
          TextMapId = "Content_200203_14"
        },
        Content_200203_2 = {
          ContentJP = "エルザ夫人の一人娘のアニーが行方不明になってしまったらしい。\nアイスレイク城を散策しながら、アニーらしき女の子がいないか探してみよう。",
          TextMapId = "Content_200203_2"
        },
        Content_200203_3 = {
          ContentJP = "街の外を探してみよう。あの医者はまだ立ち去っていないかもしれない。\n 運が良ければ会えるかも。",
          TextMapId = "Content_200203_3"
        },
        Content_200203_3_2 = {
          ContentJP = "穢獣を撃退した。女性が何か言いたそうだ。彼女と話してみよう。",
          TextMapId = "Content_200203_3_2"
        },
        Content_200203_3_3 = {
          ContentJP = "城の外で穢獣の群れが、一人の女性を囲んでいた。穢獣を倒して彼女を助けよう。",
          TextMapId = "Content_200203_3_3"
        },
        Content_200203_4 = {
          ContentJP = "残念ながらアニーはこの世を去ってしまった。しかし彼女の服を譲ってもらったおかげでアリスは生き延びた。\n雪山から生還したものの、何日も続いた高熱のせいでアリスは記憶を失ってしまった。\nこれで真相は明らかになった。つぎは……この事実を当事者にどう伝えるべきか……",
          TextMapId = "Content_200203_4"
        },
        Content_200203_5 = {
          ContentJP = "アリスは記憶を取り戻した。\nつぎは、エルザ夫人にアニーが亡くなったことを伝えなくては……",
          TextMapId = "Content_200203_5"
        },
        Content_200203_6 = {
          ContentJP = "私たちはアニーの死を隠した。\n 次は「アニー」をエルザ夫人のもとへ連れて行き、再会させてあげよう。",
          TextMapId = "Content_200203_6"
        },
        Content_200204_1 = {
          ContentJP = "月狩り人のウェルズに「アスフォデロス」で会おうと誘われた。",
          TextMapId = "Content_200204_1"
        },
        Content_200204_2 = {
          ContentJP = "月狩り人のウェルズはあなたの腕前を認め、一緒に依頼をこなそうと誘っている。",
          TextMapId = "Content_200204_2"
        },
        Content_200204_3 = {
          ContentJP = "ウェルズの誘いに同意した。\n一緒に依頼を1回完了しよう。",
          TextMapId = "Content_200204_3"
        },
        Content_200204_4 = {
          ContentJP = "ウェルズがトラブルに巻き込まれたようだ……\n義父の怒りをなだめよう。",
          TextMapId = "Content_200204_4"
        },
        Content_200204_5 = {
          ContentJP = "ウィルスの人生は、諦めの繰り返しだった。でも今はただ、笑って飲みたい。それが作り笑いだって、自分でも分かってる――だからせめて今だけは、酒を開けて、全部忘れてしまおう。",
          TextMapId = "Content_200204_5"
        },
        Content_200205_1 = {
          ContentJP = "アスフォデロスで「南国の島のお宝情報」を売っていた老人ブルネット。けれど数枚の銀貨で莫大な財宝が手に入るなんて、そんなうまい話があるわけがない。彼と話し、嘘を見破ろう。",
          TextMapId = "Content_200205_1"
        },
        Content_200205_10 = {
          ContentJP = "あなたはパーガトリー島で、ブルネットが残した本を見つけ、島の特産も釣り上げた。きっとこれで、しばらくは彼の郷愁も和らぐはずだ。またアイスレイク城に戻ったときにでも、まとめて届けてやろう。",
          TextMapId = "Content_200205_10"
        },
        Content_200205_2 = {
          ContentJP = "会話を通じて、彼もまたあなたと同じパーガトリー島の出身であることが分かった。若い頃、外の世界に憧れて漁船で帝国へ渡ったものの、故郷へ帰還する機会を永遠に失ってしまい、今では詐欺まがいの生活で日々をしのいでいる。あなたがパーガトリー島の現状を話すと、彼の胸に眠っていた故郷への想いが甦った。もし機会があるなら、彼の代わりにパーガトリー島へ戻り、彼の古い家を訪ねて何か思い出の品を持ち帰ってあげよう。",
          TextMapId = "Content_200205_2"
        },
        Content_200205_3 = {
          ContentJP = "パーガトリー島に戻ったあなたを待っていたのは、かつて村を襲った穢獣の爪痕だった。ブルネットの家は村の東端にあるはずだ。そこへ向かって、何か残されていないかを確かめよう。",
          TextMapId = "Content_200205_3"
        },
        Content_200205_4 = {
          ContentJP = "彼の家で丁寧に保管された一つの箱を見つけた。中にはきっと彼にとって大切なものが収められているだろう。だが鍵がかかっている。\n周辺で鍵を探してみよう。",
          TextMapId = "Content_200205_4"
        },
        Content_200205_5 = {
          ContentJP = "箱の近くを探していたあなたは、壁の隅で鍵穴に合う鍵を発見した。\nその鍵でブルネットの箱を開けて、中身を取り出そう。",
          TextMapId = "Content_200205_5"
        },
        Content_200205_6 = {
          ContentJP = "パーガトリー島の特産物を持って再びアイスレイク城へ戻ると、ブルネットはアスフォデロスで真っ当な方法で生きる術を見つけていた。今ではアイスレイク城漁業協会の宣伝に力を入れているようだ。\nパーガトリー島から持ち帰った思い出の品を彼に渡してみよう。彼が何を語るのか、耳を傾けてみてほしい。",
          TextMapId = "Content_200205_6"
        },
        Content_200205_7 = {
          ContentJP = "ブルネットの箱を開けることに成功した。だが、中に入っていたのはたった一冊の本だけだ。手に取った瞬間、開錠音に反応したのか、数匹の穢獣が姿を現した――本を読むのは後だ。まずはこいつらを片付けよう。",
          TextMapId = "Content_200205_7"
        },
        Content_200205_8 = {
          ContentJP = "懐かしい場所、馴染みのある戦い――忘れていた記憶が、ふいに胸を突いた。ずっと昔、ここでブルネットの孫と口論になり、ついには殴り合いにまで発展したことがあった。今では笑い話だが、細かいことはもう思い出せない。……あのとき一緒に座った屋上に行ってみよう。何か、思い出せるかもしれない。",
          TextMapId = "Content_200205_8"
        },
        Content_200205_9 = {
          ContentJP = "屋根に腰を下ろしたあなたは、かつてパーガトリー島で過ごした日々の「かけら」をまたひとつ見つけた。三つのパン、げっぷをする子ども、そして最後には握手を交わしたあの仲間たち。思えば、あの頃からブルネットとの縁は始まっていたのかもしれない――この奇妙な巡り合わせを記念して、パーガトリー島の特産の魚を一匹、彼に持ち帰ってやろう。",
          TextMapId = "Content_200205_9"
        },
        Content_20020605_3 = {
          ContentJP = "バックの家の近くで、酒の混じったミルク壺、雪に埋もれたポップコーン、そして子どもの足跡を見つけた。彼が酒を飲まないという事実をふまえれば、犯人の候補はかなり絞れそうだ。",
          TextMapId = "Content_20020605_3"
        },
        Content_20020606_5 = {
          ContentJP = "ガレア劇場のあたりで、怪しい人物を何人か見かけた。酔っ払い、少女、少年……どれも酒やポップコーンとかかわりをもっている。集めた情報を元に、誰がバックを酔わせたのか推理しよう。",
          TextMapId = "Content_20020606_5"
        },
        Content_200206_1 = {
          ContentJP = "いつも通りアスフォデロスに行って依頼を受けよう。ただし、今日のアスフォデロスはなんだか騒がしいようだ。",
          TextMapId = "Content_200206_1"
        },
        Content_200206_2 = {
          ContentJP = "興奮した客がアスフォデロスのテーブルの上に立ち、意味不明な言葉を叫んでいるが…その客からは酒の匂いはしない。\nこの興味深い客についてカミラと話してみよう。",
          TextMapId = "Content_200206_2"
        },
        Content_200206_3 = {
          ContentJP = "バック氏の会話には、豊富な修辞法が惜しみなく使われている。これは彼がプロの劇作家である証拠かもしれないが、今のバック氏にはプロの翻訳者が必要かもしれない……。彼の話を聞いてみよう。",
          TextMapId = "Content_200206_3"
        },
        Content_200206_4 = {
          ContentJP = "バック氏は夜中に家に侵入する不審者に悩んでいるようだ。月狩り人の出番だ。\nバック氏の家の近くを調査してみよう。",
          TextMapId = "Content_200206_4"
        },
        Content_200206_5 = {
          ContentJP = "不審者が家に侵入したのであれば、きっと何かしらの痕跡を残しているはずだ。周囲を捜索してみよう。",
          TextMapId = "Content_200206_5"
        },
        Content_200206_6 = {
          ContentJP = "近隣住民との会話から、バックの家に近づく子どもはいないと判明した。さらにアスフォデロスでは子どもの出入りが制限されていることから、侵入者と接点があるとすれば「ガレア劇場」しかない。劇場付近で、インスピレーション泥棒の特徴に当てはまる子どもがいないか探してみよう。",
          TextMapId = "Content_200206_6"
        },
        Content_200206_7 = {
          ContentJP = "推理を重ねた結果、酔っぱらいと少女の可能性を除外し、酒とポップコーンの両方に関わりがある少年アーノルドが最も疑わしいとわかった。アーノルドを問い詰めてみよう。",
          TextMapId = "Content_200206_7"
        },
        Content_200206_8 = {
          ContentJP = "アーノルドが夜中にバック氏の家に忍び込む理由がわかったが、この件に関して決定を下せるのはバック氏だけだ。\nアーノルドを連れて、調査結果を知らせにアスフォデロスに戻ろう。",
          TextMapId = "Content_200206_8"
        },
        Content_200206_9 = {
          ContentJP = "調査を進めた結果、どうやらバックの家に侵入して酒を飲ませたのは子どものようだ。とはいえ、まだ正体までは特定できない。まずは近所の人に話を聞いて、バックの交友関係を洗ってみよう。",
          TextMapId = "Content_200206_9"
        },
        Content_200207_1 = {
          ContentJP = "シルバーピース広場の記念碑付近で、学校の先生と生徒たちがメモリアルイベントを行っているようだ。成績優秀な生徒たちは、式典のスピーチを聞いた後で記念碑に花を手向けるようだ。\nどんな話をしているのか、聞いてみよう。",
          TextMapId = "Content_200207_1"
        },
        Content_200207_2 = {
          ContentJP = "記念碑に花を捧げる生徒のエマは、どこか上の空のようだ。一人でぼんやりしている今が、話を聞くちょうどいい機会かもしれない。",
          TextMapId = "Content_200207_2"
        },
        Content_200207_3 = {
          ContentJP = "エマはスラムに住むカロン族のおばあさんと知り合いになったようだ。しかし、今日はメモリアルイベントがあるため、エマは約束を守れなくなってしまった。\n代わりに、エマの手作りのぬいぐるみをそのおばあさんに届けることになった。",
          TextMapId = "Content_200207_3"
        },
        Content_200207_4 = {
          ContentJP = "あなたは霧の街でエマの言っていた「老婦人」を見つけた。ベリンダという霊能力者は、カロン族の人々の心の傷を言葉で癒やしているようだ。\nエマの作ったぬいぐるみを渡そう。",
          TextMapId = "Content_200207_4"
        },
        Content_200207_5 = {
          ContentJP = "ベリンダは、あなたが最近大切な人を失ったことを見抜き、いずれ再会できるだろうとほのめかした。\n 彼女があなたに贈り物をしたいと言う。そのためには、あなたの気配が染み込んだ針金が必要なようだ。グルビューム廃坑で探してみよう。",
          TextMapId = "Content_200207_5"
        },
        Content_200207_6 = {
          ContentJP = "天下御免の大泥棒から針金を取り返した。アイスレイク城に戻って、ベリンダに渡してみよう。彼女がどんな贈り物をくれるのか、楽しみだ。",
          TextMapId = "Content_200207_6"
        },
        Content_200207_7 = {
          ContentJP = "グルビューム廃坑でベリンダに頼まれた針金を見つけた――が、現れた天下御免の大泥棒にあっさり奪われてしまった。理由はどうあれ、持っていかれて黙っている筋合いはない。\n 追いかけて、ケリをつけよう。",
          TextMapId = "Content_200207_7"
        },
        Content_200208_1 = {
          ContentJP = "マギーから受け取った鍵は、かつてヘンリックが使っていた箱に使用された鍵と似たものだった。何か因縁のあるものかもしれない。「ヒュプノスの家」へ行って確かめてみよう。",
          TextMapId = "Content_200208_1"
        },
        Content_200208_2 = {
          ContentJP = "中では見知らぬ女性が掃除していた。どうやらヘンリックや「ヒュプノスの家」の子どもたちと関係がありそうだ。\n話しかけてみて、彼女の正体を確かめよう。",
          TextMapId = "Content_200208_2"
        },
        Content_200208_3 = {
          ContentJP = "レナは、以前「ヒュプノスの家」でメイドをしていたらしい。マギーから手に入れた鍵でヘンリックの重要書類が保管された箱を開けることができるようだ。\nそれを獲得して「ヒュプノスの家」の結末に立ち合おう。",
          TextMapId = "Content_200208_3"
        },
        Content_200208_4 = {
          ContentJP = "ヘンリックの箱のそばに、ひとつの精巧な木箱が置かれていた。ヘンリックの性格からして、彼自身が置いたとは考えにくい。どうやら後から誰かが置いたものらしい。やけに目立つその置き方は、まるで「見つけてくれ」と言わんばかりで、少なくともヘンリックのやり方とは思えない。では、誰が？何のために？確かめるには――開けてみるしかない。",
          TextMapId = "Content_200208_4"
        },
        Content_200209_1 = {
          ContentJP = "シルバーピース広場にいた医師ルナが何やら困っているようだ。声をかけてみよう。",
          TextMapId = "Content_200209_1"
        },
        Content_200209_2 = {
          ContentJP = "医師ルナが手にしている処方箋は、マギーからもらった手がかりに関係しているようだ。\n彼女に話しかけてみよう。",
          TextMapId = "Content_200209_2"
        },
        Content_200209_3 = {
          ContentJP = "なにか手がかりがあるかもしれない。あの「声を取り戻そう」と苦しんでいる医師に、力を貸せるなら――",
          TextMapId = "Content_200209_3"
        },
        Content_200209_4 = {
          ContentJP = "なにか手がかりがあるかもしれない。あの「声を取り戻そう」と苦しんでいる医師に、力を貸せるなら――",
          TextMapId = "Content_200209_4"
        },
        Content_200209_5 = {
          ContentJP = "ヒュプノスの家を捜索すれば、何か手がかりや役立つ情報が見つかるかもしれない。運が良ければ処方箋さえ見つかるかも。",
          TextMapId = "Content_200209_5"
        },
        Content_200209_6 = {
          ContentJP = "処方箋を手にしたことだし、ルナに知らせに行こう。たとえ、結果がどうなろうとも――",
          TextMapId = "Content_200209_6"
        },
        Content_200209_7 = {
          ContentJP = "ルナを困らせているチンピラどもを追おう。",
          TextMapId = "Content_200209_7"
        },
        Content_200210_1 = {
          ContentJP = "闇市に入ったあなたは、悪どい顔つきで、言動が怪しげな商人に注目した。声をかけてみよう。",
          TextMapId = "Content_200210_1"
        },
        Content_200210_2 = {
          ContentJP = "闇市の商人ハーロウの依頼通り、約束の場所へ向かい依頼を遂行しよう。ダグリ湖の近くだ。",
          TextMapId = "Content_200210_2"
        },
        Content_200210_3 = {
          ContentJP = "目標地点の近くに多くの穢獣が現れた。撃破しよう。",
          TextMapId = "Content_200210_3"
        },
        Content_200210_4 = {
          ContentJP = "穢獣をすべて倒した。ハーロウから約束の報酬を受け取ろう。",
          TextMapId = "Content_200210_4"
        },
        Content_200210_7 = {
          ContentJP = "アイスレイク城の下水道に秘は密の闇市場があり、秘密の取引が日々行われているという噂がある。\n優秀で警戒心の強い月狩りの一人として、その真相を突き止める必要がありそうだ。",
          TextMapId = "Content_200210_7"
        },
        Content_200211_1 = {
          ContentJP = "アイスレイク城に、自分をカロン族だと思い込んでいるソラの民の少女がいるらしい。何が起きているのか確かめに行こう。",
          TextMapId = "Content_200211_1"
        },
        Content_200211_2 = {
          ContentJP = "クラスメートにいじめられたフェイは耳を塞いで逃げ出してしまった。傷ついた彼女を見つけ、慰めてあげよう。",
          TextMapId = "Content_200211_2"
        },
        Content_200211_3 = {
          ContentJP = "フェイとの短い会話から、彼女のクラスメートによるいじめの深刻さを知った。\nフェイには、自分を守る力と自信を身につけてほしい。さあ、あのいじめっ子たちに仕返ししてやろう。",
          TextMapId = "Content_200211_3"
        },
        Content_200211_4 = {
          ContentJP = "フェイとの短い会話から、彼女のクラスメートによるいじめの深刻さを知った。\nフェイには、自分を守る力と自信を身につけてほしい。さあ、あのいじめっ子たちに仕返ししてやろう。",
          TextMapId = "Content_200211_4"
        },
        Content_200211_5 = {
          ContentJP = "あなたの助けで、フェイはついに勇気を出して自分をいじめていたクラスメイトに反撃した。ところが相手は逆上し、仲間を呼び寄せて殴りかかってきた。\n だが即席で集められたその連中は、あなたの敵ではなかった。連中を追い払ったあと、フェイと話してみよう。",
          TextMapId = "Content_200211_5"
        },
        Content_200212_2 = {
          ContentJP = "アイスレイク城の近くで、不安そうな鉱夫を見つけた。彼の足元の麻袋には、不気味な血の跡がついている。\n何かおかしい。アイスレイク城の門にいる警備隊員のケビンに報告するのが賢明だろう。",
          TextMapId = "Content_200212_2"
        },
        Content_200212_3 = {
          ContentJP = "案の定、ケビンもこの件の通報を受けていた。熱心なアイスレイク城の市民として、ケビンと一緒にラリーの元へ戻り、事情を調べよう。",
          TextMapId = "Content_200212_3"
        },
        Content_200212_4 = {
          ContentJP = "警備部隊を見ると、ラリーは動揺して、激昂してあなたたちめがけて攻撃してきた。\nラリーを撃破し、彼を冷静にさせよう。",
          TextMapId = "Content_200212_4"
        },
        Content_200212_5 = {
          ContentJP = "ラリーを倒し、彼はようやく落ち着きを取り戻した。\n事の真相を明らかにするため、ケビンと一緒にラリーから事情を聞こう。",
          TextMapId = "Content_200212_5"
        },
        Content_200212_6 = {
          ContentJP = "月ノ石には血の跡がついており、ラリーの挙動もどこかおかしい。言ってることと身なりがまるで噛み合っていない。――この石、どうにも胡散臭い。いっそ、自分の目で確かめに行ったほうが早そうだ。",
          TextMapId = "Content_200212_6"
        },
        Content_200212_7 = {
          ContentJP = "あの袋入りの月ノ石は、もともと出どころの怪しい代物だ。どうせ、表に出せるような代物じゃない――ならばくすねてもいいのでは？ラリーのもとへ戻ろう。",
          TextMapId = "Content_200212_7"
        },
        Content_200212_8 = {
          ContentJP = "ラリーは正気を保てなくなっていた。安っぽい自尊心と後ろめたさに押し潰され、夢見た未来もすでに手の届かないものとなった。現実を受け入れられず、あなたに襲いかかってきたラリーを止めて、彼に現実を突きつけよう。",
          TextMapId = "Content_200212_8"
        },
        Content_200213_1 = {
          ContentJP = "アイスレイク城に来てからしばらくの時が過ぎた。相変わらず寒さは厳しいが、スラム街では、いつも変わらぬ心温まる光景が見られる――マギーが貧しい人々に物資を分け与える姿だ。\nそういえば、この前下水道で泥棒を捕まえて以来、彼女とはゆっくり話をしていない。挨拶がてら様子を見に行こう。",
          TextMapId = "Content_200213_1"
        },
        Content_200213_2 = {
          ContentJP = "マギーが言うように、一見無価値に見えるものこそ、時に最も貴重な意味を秘めている。街のあちこちに転がるゴミ箱には、語られざる物語が潜んでいるかもしれない。\nマギーから受け取った「処方箋」、「折り紙の星」、「鍵」。これらは、それぞれが目的地への道しるべとなっているようだ。さあ、これらの手がかりを追い、隠された物語の真相に迫ろう！",
          TextMapId = "Content_200213_2"
        },
        Content_200213_3 = {
          ContentJP = "ついにマギーから受け取った3つの手がかりの謎を解き明かした。アイスレイク城に対する見方が、少しずつ変わり始めている。\nスラム街に戻り、マギーが何をしているのか見に行こう。",
          TextMapId = "Content_200213_3"
        },
        Content_200213_4 = {
          ContentJP = "調査の末、3つの手がかりの謎を解き明かした。さあ、マギー話してみよう。",
          TextMapId = "Content_200213_4"
        },
        Content_200215_1 = {
          ContentJP = "会話の中で、マーカスが革新的な料理を追求するシェフであることがわかった。ダクリ湖の近くにちょうど良い釣り場がある。そこへ行ってみて、料理の材料になりそうな魚を釣ってこよう。",
          TextMapId = "Content_200215_1"
        },
        Content_200215_2 = {
          ContentJP = "マーカスのためにツキワギョを釣ろう。今回彼にはより明確な目標がある。今度こそ料理が成功するよう願おう。",
          TextMapId = "Content_200215_2"
        },
        Content_200215_3 = {
          ContentJP = "漬け物の材料がやっと見つかった。マーカスに渡そう。",
          TextMapId = "Content_200215_3"
        },
        Content_200215_4 = {
          ContentJP = "マーカスの料理は、正直なところ、相変わらずとても食べられたものではない。それでも、あなたは彼の料理の助手を買って出ることにした。\nさて、彼の次の計画を聞いてみよう。",
          TextMapId = "Content_200215_4"
        },
        Content_200215_41 = {
          ContentJP = "信じがたいが、自分で釣り上げた新鮮な魚だ。仕方ない、マーカスの腕前を試してみましょう。",
          TextMapId = "Content_200215_41"
        },
        Content_200215_5 = {
          ContentJP = "素晴らしい、釣りの腕なら誰にも負けないぞ！釣り上げたツキワギョをマーカスに渡そう。",
          TextMapId = "Content_200215_5"
        },
        Content_200215_6 = {
          ContentJP = "素晴らしい、釣りの腕なら誰にも負けないぞ！釣り上げたツキワギョをマーカスに渡そう。",
          TextMapId = "Content_200215_6"
        },
        Content_200215_7 = {
          ContentJP = "マーカスとはしばらく会っていない。あの情熱的な料理人の独創料理は、どうなっているだろうか。\n最近、アイスレイク城の門前でよく見かけるという噂を耳にした。様子を見に行ってみよう。",
          TextMapId = "Content_200215_7"
        },
        Content_200215_8 = {
          ContentJP = "マーカスはアイスレイクの城門のベンチに座っている。話しかけてみよう",
          TextMapId = "Content_200215_8"
        },
        Content_200216_1 = {
          ContentJP = "アイスレイク城の外に怪しげな宝箱があるそうだ。その宝箱の中身を確認してみよう。",
          TextMapId = "Content_200216_1"
        },
        Content_200216_2 = {
          ContentJP = "道端で宝箱を見つけたが、特別なものではなさそうだ。\nあまり深く考えずに開けてみよう。",
          TextMapId = "Content_200216_2"
        },
        Content_200216_3 = {
          ContentJP = "城外の荒れ地で、不審な宝箱を見つけた。開けようとした瞬間、「帝国三兄弟」と名乗る3人の男が突如現れ、宝箱は自分たちのものだと主張してきた。\n見た目の印象では、彼らとの話し合いは無意味だ。拳で片付けるしかない。",
          TextMapId = "Content_200216_3"
        },
        Content_200216_4 = {
          ContentJP = "やっと「帝国三兄弟」を懲らしめた。彼らは何か話があるようだ。",
          TextMapId = "Content_200216_4"
        },
        Content_200217_1 = {
          ContentJP = "アスフォデロスの外にいる酒商人のディーンは、カミラに対して何か不満を抱えているようだ。どんな事情があるのか、話を聞いてみよう。",
          TextMapId = "Content_200217_1"
        },
        Content_200218_1 = {
          ContentJP = "あなたは再びここを訪れた。この荒涼とした地に立つと、あなたの耳にはいまなお少女の去り際の震える声が蘇る。\n謎めいた女性が、まるで死者の言葉のように低く静かに祈りを捧げていた。それは母なる神の慈しみのごとく響き、氷の崖に宿る哀しき魂も安らぎを得たかのようだった……",
          TextMapId = "Content_200218_1"
        },
        Content_200219_1 = {
          ContentJP = "アイスレイク城を出入りする列車は、果てしなく行き交う。その轟音は遠方の旅人を迎え、また勇敢なる旅人を送り出す。\nそして今日、町の外にある切符売り場で何か騒ぎが聞こえた。様子を見に行こう。",
          TextMapId = "Content_200219_1"
        },
        Content_200219_2 = {
          ContentJP = "この異国商人は何かトラブルに遭ったようだ。声をかけてみよう。",
          TextMapId = "Content_200219_2"
        },
        Content_200219_3 = {
          ContentJP = "勇敢で親切な月狩り人として、切符が買えずに困っている異国人を放っておくわけにはいかない。あの態度が悪い切符売りと話し合ってみよう。",
          TextMapId = "Content_200219_3"
        },
        Content_200219_4 = {
          ContentJP = "あのごみ拾いの老人はずっとあなたを見つめている。何か言いたそうだ。話しかけてみてもいいかもしれない。",
          TextMapId = "Content_200219_4"
        },
        Content_200219_5 = {
          ContentJP = "異国の商人と共に折り紙を完成させよう。自分の手で美しい願いを仕上げるのは素敵なことだ。",
          TextMapId = "Content_200219_5"
        },
        Content_200220_1 = {
          ContentJP = "勤勉で仕事熱心な月狩り人として、今日もアスフォデロスに依頼を受け取りに行くつもりだ。どんな面白くて新しい依頼があるのか、見に行ってみよう。",
          TextMapId = "Content_200220_1"
        },
        Content_200220_2 = {
          ContentJP = "アスフォデロスの掲示板の前でウェイターが何かをつぶやいている。話しかけてみよう。",
          TextMapId = "Content_200220_2"
        },
        Content_200220_3 = {
          ContentJP = "あなたはアウトサイダーと月狩り人チームを結成した。チーム名の決定をめぐって、スノウはアウトサイダーとの勝負を提案した。",
          TextMapId = "Content_200220_3"
        },
        Content_200220_4 = {
          ContentJP = "あなたはアウトサイダーと月狩り人チームを結成した。チーム名の決定をめぐって、スノウはアウトサイダーとの勝負を提案した。\nスノウの情報によると、屋敷の近くにいるルーナ族の探検家が、勝負の詳細を知っているらしい。彼女に話を聞きに行こう。",
          TextMapId = "Content_200220_4"
        },
        Content_200220_5 = {
          ContentJP = "ルーナ族の探検家の立会いのもとで、まもなく第1回の挑戦が始まる！\nルーナ族の探検家がマークした場所に向かい、挑戦を開始しよう。",
