        PartyReward_Description_110101 = {
          ContentJP = "渋くて不味い皮のなかに、甘酸っぱい思い出が一粒一粒包まれている。",
          TextMapId = "PartyReward_Description_110101"
        },
        PartyReward_Description_110102 = {
          ContentJP = "折れた羽根。潮の香りと、かすかな血の匂いを帯びている。少女の未熟さと勇気の証でもある。",
          TextMapId = "PartyReward_Description_110102"
        },
        PartyReward_Description_110103 = {
          ContentJP = "たとえ牢獄に囚われていても、なお遠くを見つめる者がいる。",
          TextMapId = "PartyReward_Description_110103"
        },
        PartyReward_Description_110104 = {
          ContentJP = "あの短く幸せだった日々はもう戻らない。それでも、私たちにはまだ明日がある。",
          TextMapId = "PartyReward_Description_110104"
        },
        PartyReward_Description_110301 = {
          ContentJP = "サイズや形は精巧に設計され、角をちょうど隠せるようになっている。少年が人間を演じるために作られたもの。",
          TextMapId = "PartyReward_Description_110301"
        },
        PartyReward_Description_110302 = {
          ContentJP = "記者は華やかな言葉で大富豪の伝説を綴り、彼の賢明さ、優雅さ、寛大さを惜しみなく称賛した。まるで金色に輝く藁を振りかざすように。",
          TextMapId = "PartyReward_Description_110302"
        },
        PartyReward_Description_110303 = {
          ContentJP = "冷たい金属に刻まれた数字「47」は既に磨り減り、判別が難しい状態だ。その持ち主の本当の名前を知る者は、もはやいないだろう。",
          TextMapId = "PartyReward_Description_110303"
        },
        PartyReward_Description_110304 = {
          ContentJP = "明るく、暖かく、柔らかな光が、帰りの遅い子どもを呼んでいる。それは、暗闇に堕ちた獣にはもう二度と手に届かない光だ。",
          TextMapId = "PartyReward_Description_110304"
        },
        PartyReward_Description_150201 = {
          ContentJP = "透明な破片には、すでに凝り固まった深紅の液体がこびりついている。それはかつて、少女が必死に追い求めた奇跡の残骸だった。",
          TextMapId = "PartyReward_Description_150201"
        },
        PartyReward_Description_150202 = {
          ContentJP = "青年と少女の叶わなかった理想が描かれている。彼らが後世に残した宝物であり、百年を経てもなお、輝きを失うことなく光り続けている。",
          TextMapId = "PartyReward_Description_150202"
        },
        PartyReward_Description_150203 = {
          ContentJP = "新鮮な獣肉と野菜をじっくり煮込んだスープ。寒い夜に凍った心を温めてくれる。",
          TextMapId = "PartyReward_Description_150203"
        },
        PartyReward_Description_150204 = {
          ContentJP = "夢の中では、過酷な生も、悲しい死もなく、すべての後悔や過ちはまだ訪れていない。彼女は今もなお、陽光と小川の音に包まれている。",
          TextMapId = "PartyReward_Description_150204"
        },
        PartyReward_Description_180101 = {
          ContentJP = "積み重ねた薬莢に想像の羽をつけると、ピストルや大砲、飛行船、そしてクールなロボットに変わる。それはまた、夢を追いかける旅の扉を開く鍵にもなる。",
          TextMapId = "PartyReward_Description_180101"
        },
        PartyReward_Description_180102 = {
          ContentJP = "おとぎ話はいつも幸せな結末を迎えるわけではない。かつては鳥だった燃える星は、夜空で涙を流すことがあるのだろうか？",
          TextMapId = "PartyReward_Description_180102"
        },
        PartyReward_Description_180103 = {
          ContentJP = "冷たい雨が降り止まない。幸いなことに、彼女はいまでも誰かの手のひらから温もりを感じることができる。",
          TextMapId = "PartyReward_Description_180103"
        },
        PartyReward_Description_180104 = {
          ContentJP = "この物語が美しい結末を迎えるために、彼女はどれほど多くの道を歩まなければならないのだろうか？",
          TextMapId = "PartyReward_Description_180104"
        },
        PartyReward_Description_210101 = {
          ContentJP = "さあ、飲んで。その苦味を飲み干して、愛の抱擁を迎えましょう。",
          TextMapId = "PartyReward_Description_210101"
        },
        PartyReward_Description_210102 = {
          ContentJP = "グラスの中のクラゲは明かりの下でたゆたい、静かに深海へと沈み、海底に広がっていく。そこでは、ひとりの仲間にもめぐり逢うことはなかった。",
          TextMapId = "PartyReward_Description_210102"
        },
        PartyReward_Description_210103 = {
          ContentJP = "人と人の関係はこの飲料の味わいに似ている。最初の一口は熱烈で、やがてほろ苦さが広がり、最後はひんやりとした後味がのこる。水と火が決して相容れないように。",
          TextMapId = "PartyReward_Description_210103"
        },
        PartyReward_Description_210104 = {
          ContentJP = "この飲料は、シンプルな味わいで見た目も素朴だ。なぜなら、真の愛は決して隠したり、飾り立てるものではなく、ただ愛する人の瞳に満ちあふれるものだから。",
          TextMapId = "PartyReward_Description_210104"
        },
        PartyReward_Description_230101 = {
          ContentJP = "一気に飲み干すと、甘酸っぱい風味がパチパチと舌の上で弾ける。まるで屋根の下で弾けながら広がっていく子供たちの笑い声のように。",
          TextMapId = "PartyReward_Description_230101"
        },
        PartyReward_Description_230102 = {
          ContentJP = "甘さとしょっぱさは決して対立するものではなく、無邪気さとずる賢さのように、共存できる。",
          TextMapId = "PartyReward_Description_230102"
        },
        PartyReward_Description_230103 = {
          ContentJP = "最初の一口で酸っぱさが口いっぱいに広がって、顔をしかめるかもしれないが、舌がその繊細な甘さに慣れてくると、まったく新しい味わいが広がる。世界に対する認識が変わる時のように。私たちは希望を手に入れる前に、困難を味わわなければならない。",
          TextMapId = "PartyReward_Description_230103"
        },
        PartyReward_Description_230104 = {
          ContentJP = "%*$^&!@#%*^^Y&!#(%^%？？？！！",
          TextMapId = "PartyReward_Description_230104"
        },
        PartyReward_Description_240101 = {
          ContentJP = "一八九〇種の薬物を記した古文書。うち半数は薬草。筆者は「フーフォン」と署名されている。",
          TextMapId = "PartyReward_Description_240101"
        },
        PartyReward_Description_240102 = {
          ContentJP = "憶夢草にきわめてよく似た植物。強い幻覚作用を持つ。もし幻覚が「覚めない夢」であるのなら——この二つの草が似通っているのも、道理というべきか。",
          TextMapId = "PartyReward_Description_240102"
        },
        PartyReward_Description_240103 = {
          ContentJP = "奇妙な姿をした生き物。だが、華胥においては「長寿の象徴」として知られている。",
          TextMapId = "PartyReward_Description_240103"
        },
        PartyReward_Description_240104 = {
          ContentJP = "——今宵、銀灯を手に残し照らす。再び会う夢が幻でありませんように。",
          TextMapId = "PartyReward_Description_240104"
        },
        PartyReward_Description_310101 = {
          ContentJP = "辛さと刺激を求める人にぴったり。ひと口で食欲がそそられ、二口目には気分が良くなる。知らない人との距離を縮めるには、まさに最適なドリンクだ。",
          TextMapId = "PartyReward_Description_310101"
