        },
        PartyTopic_Description_110101 = {
          ContentJP = "不思議な食材を鍋に入れてかき混ぜよう。心の底の、不器用で優しい心をほぐすように。",
          TextMapId = "PartyTopic_Description_110101"
        },
        PartyTopic_Description_110102 = {
          ContentJP = "あなたが、私が逃げられない理由。",
          TextMapId = "PartyTopic_Description_110102"
        },
        PartyTopic_Description_110103 = {
          ContentJP = "此岸が、私の世界のすべて。そして、私を閉じこめるために世界が築いた牢獄。",
          TextMapId = "PartyTopic_Description_110103"
        },
        PartyTopic_Description_110104 = {
          ContentJP = "すべてが終わり、すべてが始まる。",
          TextMapId = "PartyTopic_Description_110104"
        },
        PartyTopic_Description_110301 = {
          ContentJP = "ある劇作家がこう言った。「物語の主人公が動き出すためには、まずそのすべての希望を奪え」と。その言葉を聞いたとき、僕はただ願った。現実が、そうならないことを。",
          TextMapId = "PartyTopic_Description_110301"
        },
        PartyTopic_Description_110302 = {
          ContentJP = "希望が打ち砕かれ、退く道を断たれた時、主人公はもう元の生活には戻れない。この瞬間を少しでも遅らせようと僕は試みた。",
          TextMapId = "PartyTopic_Description_110302"
        },
        PartyTopic_Description_110303 = {
          ContentJP = "観客はどんでん返しやサプライズを好む。故に作家が生み出す物語は必ず起伏に富むものだ。だが、僕の物語は、ひたすら下へ、下へ、下へと……果てしなく落ち続けていた。",
          TextMapId = "PartyTopic_Description_110303"
        },
        PartyTopic_Description_110304 = {
          ContentJP = "ハッピーエンドとは無縁の物語の主役として、結末を選ぶ権利くらいはあるはずだ。",
          TextMapId = "PartyTopic_Description_110304"
        },
        PartyTopic_Description_150201 = {
          ContentJP = "歪んだ希望は、時に絶望よりも残酷だ。昨日の苦しみを、進むべき未来へと引きずり込んでしまうから。",
          TextMapId = "PartyTopic_Description_150201"
        },
        PartyTopic_Description_150202 = {
          ContentJP = "たとえ明日の風がすべての痕跡を消し去ったとしても、私たちの輝かしい瞬間は、永遠に記憶に刻まれる。",
          TextMapId = "PartyTopic_Description_150202"
        },
        PartyTopic_Description_150203 = {
          ContentJP = "私たちは崖へと続く道を選んだ。その道中に咲き誇る美しい花々を見るために――",
          TextMapId = "PartyTopic_Description_150203"
        },
        PartyTopic_Description_150204 = {
          ContentJP = "一つ一つの物語が繋がり、彼女の果てしない人生を紡いでいる。",
          TextMapId = "PartyTopic_Description_150204"
        },
        PartyTopic_Description_180101 = {
          ContentJP = "魔法の願いを、彼女は友達に託す。",
          TextMapId = "PartyTopic_Description_180101"
        },
        PartyTopic_Description_180102 = {
          ContentJP = "彼方にある古き夢を、彼女は星々に託す。",
          TextMapId = "PartyTopic_Description_180102"
        },
        PartyTopic_Description_180103 = {
          ContentJP = "埋もれた記憶を、彼女は彼方に託す。",
          TextMapId = "PartyTopic_Description_180103"
        },
        PartyTopic_Description_180104 = {
          ContentJP = "まだ訪れていない明日を、彼女は白いページに託す。",
          TextMapId = "PartyTopic_Description_180104"
        },
        PartyTopic_Description_210101 = {
          ContentJP = "トゥクントゥクン……聞こえるかしら？愛が流れる音が。",
          TextMapId = "PartyTopic_Description_210101"
        },
        PartyTopic_Description_210102 = {
          ContentJP = "愛には試練がつきものなの。甘い誓いよりも、一滴の毒液の方がずっと公平だと思わない？生死がふたりを分かつか、それともずっと一緒にいられるか、試してみない？",
          TextMapId = "PartyTopic_Description_210102"
        },
        PartyTopic_Description_210103 = {
          ContentJP = "化け物にも弱点がある。彼らは血も涙もない悪魔ではなく、ただ群れのなかの異端者に過ぎない。",
          TextMapId = "PartyTopic_Description_210103"
        },
        PartyTopic_Description_210104 = {
          ContentJP = "愛は幾重にも重なる障害を断ち切る鋭い刃となる。たとえ万里の隔たりがあろうとも、一滴の致命的な毒があろうとも。",
          TextMapId = "PartyTopic_Description_210104"
        },
        PartyTopic_Description_230101 = {
          ContentJP = "偽装は身を守る手段であり、擬態は悪者を罰する能力だ。",
          TextMapId = "PartyTopic_Description_230101"
        },
        PartyTopic_Description_230102 = {
          ContentJP = "善悪は他人によって定義される。それが正しいか間違っているかは自分で決める。",
          TextMapId = "PartyTopic_Description_230102"
        },
        PartyTopic_Description_230103 = {
          ContentJP = "目を覆う木の葉を払いのければ、もっと広い世界が見える。",
          TextMapId = "PartyTopic_Description_230103"
        },
        PartyTopic_Description_230104 = {
          ContentJP = "混沌の坩堝（るつぼ）のなかでも、わずかなぬくもりが感じられる。",
          TextMapId = "PartyTopic_Description_230104"
        },
        PartyTopic_Description_240101 = {
          ContentJP = "——百年の眠りから目覚めた医師が、あなたの「眠れぬ夜」に静かに寄り添おうとしている。",
          TextMapId = "PartyTopic_Description_240101"
        },
        PartyTopic_Description_240102 = {
          ContentJP = "——たった一枚の葉が視界を覆うとき、目に映るのは、すべて過ぎ去りし日の夢ばかり。",
          TextMapId = "PartyTopic_Description_240102"
        },
        PartyTopic_Description_240103 = {
          ContentJP = "——あなたは、彼女の姿をこの目で見た。一人の医師。一人の「規を外れし者」。そして——一人の姉。",
          TextMapId = "PartyTopic_Description_240103"
        },
        PartyTopic_Description_240104 = {
          ContentJP = "枯栄閣には誰の姿もない。だが、古き記憶が息づくかぎり、ここはきっと、彼女が懐かしむ故郷なのだろう。",
          TextMapId = "PartyTopic_Description_240104"
        },
        PartyTopic_Description_310101 = {
          ContentJP = "生きるためには食べなければならない。食べるために生きるのではなく、生きるために食べるといく認識が大切だ。",
          TextMapId = "PartyTopic_Description_310101"
        },
        PartyTopic_Description_310102 = {
          ContentJP = "高電圧にはなにか不思議な魔力が宿っているようだ。少しだけなら大丈夫だろうと思って、つい危険な境界を探ってしまう。",
          TextMapId = "PartyTopic_Description_310102"
        },
        PartyTopic_Description_310103 = {
          ContentJP = "故郷を失うことよりも辛いのは、家はすぐそこにあるのに、越えられない壁がそびえ立っていることだ。",
          TextMapId = "PartyTopic_Description_310103"
        },
        PartyTopic_Description_310201 = {
          ContentJP = "日常生活のあらゆる面に気を配るのはメイドの職務である。時には血を浴びることもやむを得ない。",
          TextMapId = "PartyTopic_Description_310201"
        },
        PartyTopic_Description_310202 = {
          ContentJP = "「第一印象が重要だ」とよく言われるが、その最初の印象を決めるのは服装である。そして、コーディネートは永遠に答えのでない学問だ。",
          TextMapId = "PartyTopic_Description_310202"
        },
        PartyTopic_Description_310203 = {
          ContentJP = "パチン、パチン。花の枝を切り落とし、悩みを断ち切る。",
          TextMapId = "PartyTopic_Description_310203"
        },
        PartyTopic_Description_310204 = {
          ContentJP = "どんなに目立たない片隅に身を隠したとしても、「成長」はいずれ私たちに追いつき、私たちを見出す。",
          TextMapId = "PartyTopic_Description_310204"
        },
        PartyTopic_Description_310301 = {
          ContentJP = "何度も爆発って言葉を繰り返すなんて、正に炸裂したオープニングトークだ。",
          TextMapId = "PartyTopic_Description_310301"
        },
        PartyTopic_Description_310302 = {
          ContentJP = "突然の訪問、矛盾する対話。",
          TextMapId = "PartyTopic_Description_310302"
        },
        PartyTopic_Description_310303 = {
          ContentJP = "相容れない「敵」であると同時に、一生離れられない「分身」でもある。",
          TextMapId = "PartyTopic_Description_310303"
        },
        PartyTopic_Description_310304 = {
          ContentJP = "暗闇に囚われた囚人同士が、互いに触れ合うその温もりこそ、闇を晴らす灯火となる。",
          TextMapId = "PartyTopic_Description_310304"
        },
        PartyTopic_Description_320101 = {
          ContentJP = "幽霊屋敷、別の顔を持つ酒客、そして中途半端なバーテンダー。",
          TextMapId = "PartyTopic_Description_320101"
        },
        PartyTopic_Description_320102 = {
          ContentJP = "実際には、植物のエキスを濃縮して作られた高アルコールの酒であり、「ビターズ」と呼ばれてはいるものの、その味わいは単純な苦味にとどまらず、より複雑で多彩な風味を持つ。",
          TextMapId = "PartyTopic_Description_320102"
        },
        PartyTopic_Description_320103 = {
          ContentJP = "甘いものを求めるのは本能だ。人は無意識のうちに、楽しかった記憶や甘美な思い出にすがろうとする。……たとえ、それが身体にとって危うい代物だとしても。",
          TextMapId = "PartyTopic_Description_320103"
        },
        PartyTopic_Description_320104 = {
          ContentJP = "まるで刃のように喉を滑り、飲んだ者だけがその先に何を切り裂いたのかを知る。",
          TextMapId = "PartyTopic_Description_320104"
        },
        PartyTopic_Description_330101 = {
          ContentJP = "良い気分が仕事の効率を大幅に向上させる。その秘訣はタイムリーな自己心情管理にある。",
          TextMapId = "PartyTopic_Description_330101"
        },
        PartyTopic_Description_330102 = {
          ContentJP = "他人が敬遠したり、捨てたりするような廃品でさえ、新たな命を宿す可能性がある。",
          TextMapId = "PartyTopic_Description_330102"
        },
        PartyTopic_Description_330103 = {
          ContentJP = "父親と娘は、習慣や職業、血脈、世界の見方だけでなく、「バカ」という称号もよく似ている。",
          TextMapId = "PartyTopic_Description_330103"
        },
        PartyTopic_Description_330104 = {
          ContentJP = "人にも猫にも、それぞれの道がある。笑顔で別れて、次の再会を楽しみにしよう。",
          TextMapId = "PartyTopic_Description_330104"
        },
        PartyTopic_Description_410101 = {
          ContentJP = "見て見ぬふりは最も残酷なこと。だから彼女は言い聞かせる。小さなことでも、何かをせねばと。",
          TextMapId = "PartyTopic_Description_410101"
        },
        PartyTopic_Description_410102 = {
          ContentJP = "時には立ち止まることも必要だ。そうしてこそ道を見定め、より遠くへ行ける。",
          TextMapId = "PartyTopic_Description_410102"
        },
        PartyTopic_Description_410103 = {
          ContentJP = "もう他人の刻んだ譜に合わせる必要はない。白紙の楽章を開けばいい。",
          TextMapId = "PartyTopic_Description_410103"
        },
        PartyTopic_Description_420201 = {
          ContentJP = "知的な会話は常に効率を求め、明確な目的がある。ある意味では誠実とも言えるだろう。",
          TextMapId = "PartyTopic_Description_420201"
        },
        PartyTopic_Description_420202 = {
          ContentJP = "一人の紳士の誕生には、優れた教育と環境、そして家族のちょっとした「気遣い」が欠かせない。",
          TextMapId = "PartyTopic_Description_420202"
        },
        PartyTopic_Description_420203 = {
          ContentJP = "あ、お父さん！",
          TextMapId = "PartyTopic_Description_420203"
        },
        PartyTopic_Description_420204 = {
          ContentJP = "鐘が鳴る前に、一緒に踊ろう。",
          TextMapId = "PartyTopic_Description_420204"
        },
        PartyTopic_Description_430101 = {
          ContentJP = "頭上に揺れる、いつ落ちるか分からない鋭い刃こそ、忠誠を試す唯一の手段だ。",
          TextMapId = "PartyTopic_Description_430101"
        },
        PartyTopic_Description_430102 = {
          ContentJP = "戦争そのものが消えることはない。それは別の形で存在し続ける。",
          TextMapId = "PartyTopic_Description_430102"
        },
        PartyTopic_Description_430103 = {
          ContentJP = "人類が森や荒野にいた頃から、生きるための道具は存在していた。今日に至ってもその役目は変わっていない――道具が創造に使われるにせよ、破壊に使われるにせよ。",
          TextMapId = "PartyTopic_Description_430103"
        },
        PartyTopic_Description_510101 = {
          ContentJP = "食べることはすべての生き物にとって最も単純で直接的な欲求だが、食べることの奥深さは単純ではない。",
          TextMapId = "PartyTopic_Description_510101"
        },
        PartyTopic_Description_510102 = {
          ContentJP = "「無駄使いはいけません」というのは、子供の頃によく耳にする教えだが、大人になるにつれて、それを守るのは難しくなる。",
          TextMapId = "PartyTopic_Description_510102"
        },
        PartyTopic_Description_510103 = {
          ContentJP = "ヘーゼルナッツは世界にとって特別な存在ではないが、トリュフにとっては唯一無二のヘーゼルナッツなのだ。",
          TextMapId = "PartyTopic_Description_510103"
        },
        PartyTopic_Description_510104 = {
          ContentJP = "幸せの定義は人それぞれ。しかし、どんな場合でも、まずはお腹いっぱい食べることで、幸せを追求するエネルギーが生まれるのだ！",
          TextMapId = "PartyTopic_Description_510104"
        },
        PartyTopic_Description_510201 = {
          ContentJP = "本当の友達ってのはさ、見返りなんか求めないものだ。質素な一杯と、簡単な挨拶――それだけで十分だ。",
          TextMapId = "PartyTopic_Description_510201"
        },
        PartyTopic_Description_510202 = {
          ContentJP = "どんなに面倒なルールも、時間が経つにつれて慣れていく。だが、面倒な人間は、どれだけ努力しても、生活は糸のほつれた布のように乱れていくばかりだ。",
          TextMapId = "PartyTopic_Description_510202"
        },
        PartyTopic_Description_510203 = {
          ContentJP = "秘密を少し打ち明ければ、距離は縮まり、心の荷も少し軽くなる。けれど中には、墓まで持っていかなければならない秘密もある――二度と陽の目を見せてはならないものが。",
          TextMapId = "PartyTopic_Description_510203"
        },
        PartyTopic_Description_510204 = {
          ContentJP = "ああ、これは俺の本名じゃねぇんだ。だが、「アウトサイダー」って名のほうが案外気に入ってんだよ。",
          TextMapId = "PartyTopic_Description_510204"
        },
        PartyTopic_Description_530101 = {
          ContentJP = "命の価値は時間の長さではなく、その時間をいかに生きるかによって決まる。",
          TextMapId = "PartyTopic_Description_530101"
        },
        PartyTopic_Description_530102 = {
          ContentJP = "怖いのは死ぬことではなくて、忘れ去られること。",
          TextMapId = "PartyTopic_Description_530102"
        },
        PartyTopic_Description_530103 = {
          ContentJP = "明日と偶然、どちらが先に来るかは分からない。でも、明日のために、私たちは次々と訪れる偶然に立ち向かい続ける。",
          TextMapId = "PartyTopic_Description_530103"
        },
        PartyTopic_Description_540101 = {
          ContentJP = "長く続く痛みと、一瞬の死。どちらがより残酷か、私にはわかりません。でも、もしかすると、私たちには前者に打ち勝つ機会がまだ残されているのかもしれません。",
          TextMapId = "PartyTopic_Description_540101"
        },
        PartyTopic_Description_540102 = {
