        Pet_IpDes_401 = {
          ContentJP = "戦場、廃墟、墓地によく現れるジェネモン。死骸を食らい、角をもつその姿から、多くの地で不吉の象徴とされる。",
          TextMapId = "Pet_IpDes_401"
        },
        Pet_IpDes_402 = {
          ContentJP = "大気中に漂い、水を好むジェネモン。普段は半透明の体を丸めており、晴れの日に見かけることはほとんどない。雨が降るときだけ体を伸ばし、花のように空中で咲き誇る。",
          TextMapId = "Pet_IpDes_402"
        },
        Pet_IpDes_403 = {
          ContentJP = "体の大半が発熱体で構成されているジェネモン。口を開けると体から熱波が湧き出る。",
          TextMapId = "Pet_IpDes_403"
        },
        Pet_IpDes_404 = {
          ContentJP = "高地に生息するジェネモン。その毛が持つ優れた導電性から、かつては人間たちの狩猟対象となっていた。そのため、野外で人間に出くわすと、丸まって猛スピードで転がり、逃げ去ってしまう。",
          TextMapId = "Pet_IpDes_404"
        },
        Pet_IpDes_405 = {
          ContentJP = "キノコに寄生するジェネモン。成長とともに宿主のキノコを完全に操るようになる。移動時に発する「プップッ」という音は、本体とキノコの間にある空洞を通る空気音だ。",
          TextMapId = "Pet_IpDes_405"
        },
        Pet_IpDes_406 = {
          ContentJP = "動きが素早く、いたずら好きで欲張りなジェネモン。この子に出くわしたら、キラキラした持ち物が盗まれていないかただちに確認すべし。",
          TextMapId = "Pet_IpDes_406"
        },
        Pet_IpDes_407 = {
          ContentJP = "内向的で腐食性を持つジェネモン。手当たり次第あらゆる物を溜め込み、その一部を食料とし、残りは巣作りの材料として箱の中に詰め込んでいく。",
          TextMapId = "Pet_IpDes_407"
        },
        Pet_IpDes_408 = {
          ContentJP = "驚くほどすばしっこいジェネモン。大きな耳は移動の邪魔になるどころか、むしろ疾走に適した完璧な流線型フォルムを生み出している。",
          TextMapId = "Pet_IpDes_408"
        },
        Pet_IpDes_409 = {
          ContentJP = "のんびり屋で驚くほど長命なジェネモン。調査結果によると、なんと一生の98%以上もの時間を眠って過ごすらしい。もしかするとそれが長寿の秘訣なのかもしれない。",
          TextMapId = "Pet_IpDes_409"
        },
        Pet_IpDes_411 = {
          ContentJP = "古城や遺跡など、人の気配のない建物に現れるジェネモン。遠くから見ると、宙に浮かぶ幽かな火のようだ。",
          TextMapId = "Pet_IpDes_411"
        },
        Pet_IpDes_412 = {
          ContentJP = "プリンのようにぷるぷると柔らかいジェネモン。その可愛らしい姿で、かつて「砂糖うさぎ」としてアトラシア大陸で大流行したこともある。しかし、イーザーのある学者が「その黒い点は目ではなく内臓だ」と言い出してからというもの、人気は急降下した。",
          TextMapId = "Pet_IpDes_412"
        },
        Pet_IpDes_413 = {
          ContentJP = "全身が炎に包まれ、灼熱の地に棲むジェネモン。そのため、どこか香ばしい焦げた肉の匂いが漂っている。",
          TextMapId = "Pet_IpDes_413"
        },
        Pet_IpDes_414 = {
          ContentJP = "全身に電気をまとい、他のジェネモンから嫌われている哀れな存在。本当の姿は誰も知らず、いつも見えるのは、玩具の車の後ろに隠れた一対の寂しげな瞳だけだ。",
          TextMapId = "Pet_IpDes_414"
        },
        Pet_IpDes_415 = {
          ContentJP = "落ち着きのない小さなジェネモン。どこにも留まることができず、綿草の種を握ってふわふわと漂いながら、存在しない旅の終着点を探し続けている。",
          TextMapId = "Pet_IpDes_415"
        },
        Pet_IpDes_416 = {
          ContentJP = "眩しく輝くジェネモン。ときには旅人の道標や光源としても役立つ。ただし周期的に強い閃光を放つため、見つめ続けるのは禁物。",
          TextMapId = "Pet_IpDes_416"
        },
        Pet_IpDes_421 = {
          ContentJP = "非常に珍しいジェネモン。黒曜石のような輝きを帯びており、かつては吟遊詩人の歌にしか存在しない幻想の生き物だと思われていた。",
          TextMapId = "Pet_IpDes_421"
        },
        Pet_IpDes_422 = {
          ContentJP = "非常に珍しいジェネモン。アイオライトのような輝きを帯びており、かつては吟遊詩人の歌にしか存在しない幻想の生き物だと思われていた。",
          TextMapId = "Pet_IpDes_422"
        },
        Pet_IpDes_423 = {
          ContentJP = "非常に珍しいジェネモン。赤メノウのような輝きを帯びており、かつては吟遊詩人の歌にしか存在しない幻想の生き物だと思われていた。",
          TextMapId = "Pet_IpDes_423"
        },
        Pet_IpDes_424 = {
          ContentJP = "非常に珍しいジェネモン。アメシストのような輝きを帯びており、かつては吟遊詩人の歌にしか存在しない幻想の生き物だと思われていた。",
          TextMapId = "Pet_IpDes_424"
        },
        Pet_IpDes_425 = {
          ContentJP = "非常に珍しいジェネモン。トルマリンのような輝きを帯びており、かつては吟遊詩人の歌にしか存在しない幻想の生き物だと思われていた。",
          TextMapId = "Pet_IpDes_425"
        },
        Pet_IpDes_426 = {
          ContentJP = "非常に珍しいジェネモン。オパールのような輝きを帯びており、かつては吟遊詩人の歌にしか存在しない幻想の生き物だと思われていた。",
          TextMapId = "Pet_IpDes_426"
        },
        Pet_IpDes_491 = {
          ContentJP = "治療ドローンを模倣するジェネモン。学者たちはその模倣の原理すら解明できていないが、当のジェネモンはすでに工学錬金院の環境に完全適応しており、生存に最適な特性へと進化している。",
          TextMapId = "Pet_IpDes_491"
        },
        Pet_IpDes_492 = {
          ContentJP = "磁気ドローンのような形をしたジェネモン。ジェネモンマニアと錬金術師の気まぐれから生まれた。\n\nこのジェネモンから魔力を吸収すると、ジェネモンサポートにより周りの敵にダメージを与える。",
          TextMapId = "Pet_IpDes_492"
        },
        Pet_IpDes_493 = {
          ContentJP = "土の隙間をすり抜ける腐食性を持つジェネモン。養分を吸収する一方で、周囲の土壌を改善する役目も果たしている。",
          TextMapId = "Pet_IpDes_493"
        },
