        QuestDesc_10020101 = {
          ContentJP = "あの日、パーガトリー島で、幸運にもあなたは生き延びた。意識を取り戻した時、眼の前に広がるのは見覚えのない雪原、そしてあなたの傍らにはベレニカの姿はなかった。\n「カミラ」と名乗る赤い衣装の女性と、「スノウ」と名乗る少女が、あなたを「アイスレイク城」へと誘う。\nしばらく迷ったが、あなたは彼女たちに同行することを決めた……",
          TextMapId = "QuestDesc_10020101"
        },
        QuestDesc_10020102 = {
          ContentJP = "あの日、パーガトリー島で、幸運にもあなたは生き延びた。意識を取り戻した時、眼の前に広がるのは見覚えのない雪原、そしてあなたの傍らにはベレニカの姿はなかった。\n「カミラ」と名乗る赤い衣装の女性と、「スノウ」と名乗る少女が、あなたを「アイスレイク城」へと誘う。\nしばらく迷ったが、あなたは彼女たちに同行することを決めた……",
          TextMapId = "QuestDesc_10020102"
        },
        QuestDesc_10020103 = {
