        },
        QuestDesc_10020104 = {
          ContentJP = "野獣を撃退した後、あなたとスノウはアイスレイク城へと向かって再び出発する。",
          TextMapId = "QuestDesc_10020104"
        },
        QuestDesc_10020105 = {
          ContentJP = "通行許可証の検査をする衛兵に遭遇したが、カミラの機転と取り成しのおかげで、ようやくアイスレイク城に入れた。",
          TextMapId = "QuestDesc_10020105"
        },
        QuestDesc_10020106 = {
          ContentJP = "カミラから「ヒュプノスの家」に宿泊するよう勧められた。\nその宿屋には恐ろしいうわさ話があるらしいが、あなたはその真相を確かめてみることにした……",
          TextMapId = "QuestDesc_10020106"
        },
        QuestDesc_10020107 = {
          ContentJP = "カミラから「ヒュプノスの家」に宿泊するよう勧められた。\nその宿屋には恐ろしいうわさ話があるらしいが、あなたはその真相を確かめてみることにした……",
          TextMapId = "QuestDesc_10020107"
        },
        QuestDesc_10020108 = {
          ContentJP = "「ヒュプノスの家」の主人、ヘンリックは陰気で冷徹な男性だ。\n彼はあなたを追い返そうとしたが、明るい少女の取り計らいで、一晩だけ泊まることを許してくれた。\nその少女が言っていた邸宅に向かおう。",
          TextMapId = "QuestDesc_10020108"
        },
        QuestDesc_10020109 = {
          ContentJP = "「ヒュプノスの家」の主人、ヘンリックは陰気で冷徹な男性だ。\n彼はあなたを追い返そうとしたが、明るい少女の取り計らいで、一晩だけ泊まることを許してくれた。\nその少女が言っていた邸宅に向かおう。",
          TextMapId = "QuestDesc_10020109"
        },
        QuestDesc_10020110 = {
          ContentJP = "貴重な休息時間だ。少し休んで、疑問と現状を整理しょう。",
          TextMapId = "QuestDesc_10020110"
        },
        QuestDesc_10020111 = {
          ContentJP = "サイキに聞きたいことがある。",
          TextMapId = "QuestDesc_10020111"
        },
        QuestDesc_10020112 = {
          ContentJP = "ベレニカを探す旅に出る前に、旅費と通行許可証を準備する必要がある。\nサイキから、カミラが経営する酒場「アスフォデロス」では、依頼を受けて報酬を得ることができ、通行許可証も必要ないと聞いた。",
          TextMapId = "QuestDesc_10020112"
        },
        QuestDesc_10020201 = {
          ContentJP = "ベレニカを探す旅に出る前に、旅費と通行許可証を準備する必要がある。\nサイキから、カミラが経営する酒場「アスフォデロス」では、依頼を受けて報酬を得ることができ、通行許可証も必要ないと聞いた。",
          TextMapId = "QuestDesc_10020201"
        },
        QuestDesc_10020202 = {
          ContentJP = "ベレニカを探す旅に出る前に、旅費と通行許可証を準備する必要がある。\nサイキから、カミラが経営する酒場「アスフォデロス」では、依頼を受けて報酬を得ることができ、通行許可証も必要ないと聞いた。",
          TextMapId = "QuestDesc_10020202"
        },
        QuestDesc_10020203 = {
          ContentJP = "広場で、特殊な角と脊椎を持つ「カロン族」に対する暴行を目撃したが、これは彼らにとってもはや日常的なことのようだ。\nカミラに迷惑をかけないよう、ひとまず我慢するしかない。\n「アスフォデロス」へと向かおう。",
          TextMapId = "QuestDesc_10020203"
        },
        QuestDesc_10020204 = {
          ContentJP = "廃品回収で生計を立てる少女、マギーが困っているようだ。\n話を聞いてみよう。",
          TextMapId = "QuestDesc_10020204"
        },
        QuestDesc_10020205 = {
          ContentJP = "廃品回収で生計を立てる少女、マギーが困っているようだ。\n話を聞いてみよう。",
          TextMapId = "QuestDesc_10020205"
        },
        QuestDesc_10020206 = {
          ContentJP = "マギーは泥棒に財布を奪われたそうだ。\n彼女に協力して財布を取り戻そう。これは人助けだけでなく、カミラの試練を乗り越えるためでもある。",
          TextMapId = "QuestDesc_10020206"
        },
        QuestDesc_10020207 = {
          ContentJP = "マギーという廃品回収の少女が泥棒に財布を盗まれた。\n「アスフォデロス」から出て、広場で泥棒を見かけた人がいないか聞いてみよう。",
          TextMapId = "QuestDesc_10020207"
        },
        QuestDesc_10020208 = {
          ContentJP = "広場の近くで老人のバディが泥棒を見たと言う。\nバディの話に出た、ゴミ箱3号の周りを調べてみよう。",
          TextMapId = "QuestDesc_10020208"
        },
        QuestDesc_10020209 = {
          ContentJP = "ゴミ箱の近くで泥棒の足跡が見つかった。その足跡は屋上に向かっているようだ。\n屋上にあがってみよう。",
          TextMapId = "QuestDesc_10020209"
        },
        QuestDesc_10020210 = {
          ContentJP = "屋上にたどり着いた。泥棒の足跡を調べてみよう。",
          TextMapId = "QuestDesc_10020210"
        },
        QuestDesc_10020211 = {
          ContentJP = "屋上でマギーの財布を奪った犯人をついに追い詰めた。しばらくの対峙の末、彼はくるりと身をひるがえして逃げてしまった。\nすぐに追跡しなければ！ん？下水道のマンホール付近に何か不審な点があるようだ。",
          TextMapId = "QuestDesc_10020211"
        },
        QuestDesc_10020212 = {
          ContentJP = "泥棒の足跡を追って下水道に入ったが、泥棒は追跡の妨げとなるトラップを仕込んでいた。\nこれらのバルブの仕掛けを見破り、泥棒の追跡を続けよう。",
          TextMapId = "QuestDesc_10020212"
        },
        QuestDesc_10020213 = {
          ContentJP = "ついにバルブの仕掛けを解き、水位を下げることに成功した。\nこのまま、泥棒を追い続けよう。",
          TextMapId = "QuestDesc_10020213"
        },
        QuestDesc_10020213_1 = {
          ContentJP = "盗賊が仕掛けた罠に不意を突かれ、マギーとスノウがはまってしまった。近くを捜索し、足取りを追おう。",
          TextMapId = "QuestDesc_10020213_1"
        },
        QuestDesc_10020214 = {
          ContentJP = "持ち歩いていた道具を利用し、泥棒は煙幕の中に姿を消し、うまく逃げ去った。先に進むには、予備の鍵を探す必要がある。",
          TextMapId = "QuestDesc_10020214"
        },
        QuestDesc_10020215 = {
          ContentJP = "やっとまた泥棒を発見した。今度は絶対に逃がさない。\n泥棒を倒して捕まえよう。",
          TextMapId = "QuestDesc_10020215"
        },
        QuestDesc_10020221 = {
          ContentJP = "マギーの依頼を完了した。「アスフォデロス」に戻り、報酬を受け取ろう",
          TextMapId = "QuestDesc_10020221"
        },
        QuestDesc_10020222 = {
          ContentJP = "月狩り人への依頼は「アスフォデロス」の掲示板に貼り出される。確認してみよう。",
          TextMapId = "QuestDesc_10020222"
        },
        QuestDesc_10020223 = {
          ContentJP = "月狩り人のウェルズは少し気まずい誤解をしていたようだ。お詫びとして、あなたに温かい飲み物をおごることに……",
          TextMapId = "QuestDesc_10020223"
        },
        QuestDesc_10020224 = {
          ContentJP = "「ジェネモン」システムが解放されました。ジェネモンと仲良くし、これからの旅に備えよう。",
          TextMapId = "QuestDesc_10020224"
        },
        QuestDesc_10020301 = {
          ContentJP = "「ヒュプノスの家」の主人、ヘンリックはグルビューム廃坑で「群星のジェム」を見つけるよう月狩り人に依頼した。\nその報酬は高額で、南方への旅費を賄うことができる。あなたは冒険を決意した……",
          TextMapId = "QuestDesc_10020301"
        },
        QuestDesc_10020302 = {
          ContentJP = "サイキが同行して、グルビューム廃坑へ案内をしてくれることになった。\nサイキと共にグルビューム廃坑へ向かおう。",
          TextMapId = "QuestDesc_10020302"
        },
        QuestDesc_10020303 = {
          ContentJP = "サイキは何かを決意したようで、あなたたちと共に「冒険」することにした。サイキと一緒にグルビューム廃坑へ向かおう。",
          TextMapId = "QuestDesc_10020303"
        },
        QuestDesc_10020304 = {
          ContentJP = "グルビューム廃坑の入口はかなり昔に封鎖されている。\nほかの入り口を探してみよう。",
          TextMapId = "QuestDesc_10020304"
        },
        QuestDesc_10020305 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。\nこの廃坑を探索し、ヘンリックが依頼した群星のジェムを見つけよう。",
          TextMapId = "QuestDesc_10020305"
        },
        QuestDesc_10020306 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。\nこの廃坑を探索し、ヘンリックが依頼した群星のジェムを見つけよう。",
          TextMapId = "QuestDesc_10020306"
        },
        QuestDesc_10020307 = {
          ContentJP = "無事にグルビューム廃坑に入れたが、閉ざされた鉱山の扉が行く手を阻んでいる。周辺を少し調べてみれば、鉱山の扉を開く方法が見つかるかもしれない。\n\n……\n月狩り人の誰かが、引き灯や標識について筆記に書き残していたようだ。余裕があれば、これらの灯りや標識を修理して後から来る人々のための道しるべを残そう。",
          TextMapId = "QuestDesc_10020307"
        },
        QuestDesc_10020308 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。\n廃坑内には穢獣が住みついている。行く手をさえぎる穢獣を倒して、先に進もう。",
          TextMapId = "QuestDesc_10020308"
        },
        QuestDesc_10020309 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。\nトラップを解除して扉を開き、廃坑を探索してヘンリックが依頼した群星のジェムを見つけよう。",
          TextMapId = "QuestDesc_10020309"
        },
        QuestDesc_10020310 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。\nこの廃坑を探索し、ヘンリックが依頼した群星のジェムを見つけよう。",
          TextMapId = "QuestDesc_10020310"
        },
        QuestDesc_10020311 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。そこかしこに散乱する落石や奇妙な結晶状の棘は、何らかの危険を示している。この廃坑を探索し、ヘンリックが依頼した群星のジェムを見つけよう。",
          TextMapId = "QuestDesc_10020311"
        },
        QuestDesc_10020312 = {
          ContentJP = "廃棄されたパイプラインを通って、グルビューム廃坑内部に入った。\nかつては巨大な天上樹が山を貫いていたが、今では完全に掘り尽くされ、わずかな根っこだけが残されている。残された鉱脈がかつての栄華を物語っているようだ。そこかしこに散乱する落石や奇妙な結晶状の棘は、何らかの危険を示している。この廃坑を探索し、ヘンリックが依頼した群星のジェムを見つけよう。",
          TextMapId = "QuestDesc_10020312"
        },
        QuestDesc_10020313 = {
          ContentJP = "あなたたちの到着で、廃坑の奥に潜んでいた巨大な穢獣が目を覚ました。\nこうなったからには、まずはヤツを倒さなければならない。",
          TextMapId = "QuestDesc_10020313"
        },
        QuestDesc_10020314 = {
          ContentJP = "巨大な穢獣の恐ろしい死骸の下には、星の光を浮かべる宝石がある。\nそれを持ち帰ろう。",
          TextMapId = "QuestDesc_10020314"
        },
        QuestDesc_10020315 = {
          ContentJP = "グルビューム鉱山での最初の冒険は無事に幕を閉じた。――「アウトサイダー」という不愉快な出来事を除けば。\n群星のジェムはアウトサイダーが持ち去ってしまった。\n彼はあなたに何か伝えたいことがあるようで、自宅を訪ねる約束をした。",
          TextMapId = "QuestDesc_10020315"
        },
        QuestDesc_10020316 = {
          ContentJP = "「アウトサイダー」という予期せぬ出来事があったが、グルビュームでの最初の冒険が無事に終わった。\n群星のジェムはアウトサイダーが持ち去ってしまった。\n彼はあなたに何か伝えたいことがあるようで、自宅を訪ねる約束をした。\n同じ頃、アイスレイク城の城門にはあなたを待ち構える怒った人物の姿もあった……",
          TextMapId = "QuestDesc_10020316"
        },
        QuestDesc_10020401 = {
          ContentJP = "アウトサイダーはあなた一人だけで訪ねてくるよう頼んだので、スノウとは別行動になった。\nアウトサイダーの家はスラム街にあり、彼が言うには、玄関のドアには鳥が描かれているらしい。",
          TextMapId = "QuestDesc_10020401"
        },
        QuestDesc_10020402 = {
          ContentJP = "アウトサイダーと彼の妹フィーナと共に、質素ながらも心温まる「お茶会」を楽しんだ。\n食後、アウトサイダーはフィーナを避けて、あなたと重要な話をしたがっているようだ。",
          TextMapId = "QuestDesc_10020402"
        },
        QuestDesc_10020403 = {
          ContentJP = "アウトサイダーと彼の妹フィーナと共に、質素ながらも心温まる「お茶会」を楽しんだ。\n食後、アウトサイダーはフィーナを避けて、あなたと重要な話をしたがっているようだ。",
          TextMapId = "QuestDesc_10020403"
        },
        QuestDesc_10020404 = {
          ContentJP = "通行許可証と引き換えに、アウトサイダーから「ヒュプノスの家」について調査を依頼された。「群星のジェム」を渡す名目で調査を行うことになっている。\nその前に、その前に、しっかりと休息しよう。",
          TextMapId = "QuestDesc_10020404"
