        },
        QuestDesc_10030113 = {
          ContentJP = "あなたはリズムから魚油を受け取った。猫がそれに反応し、尻尾を振りながら鳴いてあなたたちを先導し始めた。このまま猫について行ってみよう。",
          TextMapId = "QuestDesc_10030113"
        },
        QuestDesc_10030114 = {
          ContentJP = "猫の導きで、挙動不審な漁師と出会った。あなたたちが話しかけるより先に、彼は泣きながら地面に膝をつき、「全部話す、話すから！」と懇願してきた。どうやら確実に何かを知っているようだ。しっかりと問い詰めよう。",
          TextMapId = "QuestDesc_10030114"
        },
        QuestDesc_10030201 = {
          ContentJP = "あなたたちは工場近くの下水道へと足を踏み入れた。さて、次はどちらへ進むべきか──まずはリズムの意見を聞いてみよう。",
          TextMapId = "QuestDesc_10030201"
        },
        QuestDesc_10030202 = {
          ContentJP = "下水道に入って間もなく、アウトサイダーが前方から奇妙な物音を聞き取った。慎重に近づいて様子をうかがってみよう。",
          TextMapId = "QuestDesc_10030202"
        },
        QuestDesc_10030203 = {
          ContentJP = "前方から助けを求める声が聞こえてきた。状況を確認する間もなく、リズムは真っ先に飛び出していった。急いであとを追おう！",
          TextMapId = "QuestDesc_10030203"
        },
        QuestDesc_10030204 = {
          ContentJP = "一人目の市民を無事に救出し、閉ざされた扉も開けた。さあ、この調子で次の市民のもとへ向かおう。",
          TextMapId = "QuestDesc_10030204"
        },
        QuestDesc_10030205 = {
          ContentJP = "市民の話によれば、仲間がすぐ先に取り残されているという。急いで向かおう！",
          TextMapId = "QuestDesc_10030205"
        },
        QuestDesc_10030206 = {
          ContentJP = "2人目の市民の声が聞こえた。どうやらすぐ近くにいるようだ。様子を見に行こう。",
          TextMapId = "QuestDesc_10030206"
        },
        QuestDesc_10030207 = {
          ContentJP = "無事、2人の市民を下水道から救出することができた。リズムと合流して、彼らの様子について話を聞いてみよう。",
          TextMapId = "QuestDesc_10030207"
        },
        QuestDesc_10030208 = {
          ContentJP = "排水が終わると、奥から響く犯人の咆哮がいっそうはっきりと聞こえてきた。気をつけながら、先へ進もう。",
          TextMapId = "QuestDesc_10030208"
        },
        QuestDesc_10030210 = {
          ContentJP = "誰もが予想しなかった展開──カロン狩りの犯人は、なんと2人だった。あなたを守るためにリズムは重傷を負ってしまう。この先の捜査と犯人の捕獲は、帝国軍の増援に任せよう。「ノクトボイジャー」の面々は相談の末、カロン狩りの報酬と彼女に救われたことに感謝するため、まずは軍立病院へ見舞いに向かうことを決めた。",
          TextMapId = "QuestDesc_10030210"
        },
        QuestDesc_10030211 = {
          ContentJP = "誰もが予想しなかった展開──カロン狩りの犯人は、なんと2人だった。あなたを守るためにリズムは重傷を負ってしまう。この先の捜査と犯人の捕獲は、帝国軍の増援に任せよう。「ノクトボイジャー」の面々は相談の末、カロン狩りの報酬と彼女に救われたことに感謝するため、まずは軍立病院へ見舞いに向かうことを決めた。",
          TextMapId = "QuestDesc_10030211"
        },
        QuestDesc_10030212 = {
          ContentJP = "帝国軍からは護衛が派遣されるはずだったが、現地に着いても人影はない。代わりに目に入ったのは、壁に無数刻まれた銃痕。どうやら逃走した犯人と兵士たちがここで交戦していたらしい。下水道の出口を目指す中、その痕跡をたどってみよう。",
          TextMapId = "QuestDesc_10030212"
        },
        QuestDesc_10030213 = {
          ContentJP = "下水道内で、犯人が逃走に使ったパイプを発見した。その先は出口の方へと続いている。慎重に近づこう。",
          TextMapId = "QuestDesc_10030213"
        },
        QuestDesc_10030214 = {
          ContentJP = "出口に近づくにつれ、辺りには不自然な静けさが広がっていた。明らかに何かがあった場所だ。用心して前へ進もう。",
          TextMapId = "QuestDesc_10030214"
        },
        QuestDesc_10030215 = {
          ContentJP = "下水道の出口で見たのは、惨劇そのものだった。兵士たちの遺体には、あってはならない毒の痕跡が──カロン狩りに新たな影を落とす出来事だった。この場の報告はアウトサイダーに任せ、あなたとスノウは予定通り、リズムの様子を見に軍立病院へ向かうことにした。",
          TextMapId = "QuestDesc_10030215"
        },
        QuestDesc_10030216 = {
          ContentJP = "数日後、リズムはようやく目を覚ました。あなたは彼女に、下水道での出来事の続きを語った。感謝の気持ちを込めて、リズムはある頼みごとをしてくる。「私の名前入りの銀色の印鑑を、秘密基地で探してきてくれない？寝たままでも何通か手紙を書いて、親しい友人にベレニカのことを尋ねたいの」と。",
          TextMapId = "QuestDesc_10030216"
        },
        QuestDesc_10030217 = {
          ContentJP = "数日後、リズムはようやく目を覚ました。あなたは彼女に、下水道での出来事の続きを語った。感謝の気持ちを込めて、リズムはある頼みごとをしてくる。「私の名前入りの銀色の印鑑を、秘密基地で探してきてくれない？寝たままでも何通か手紙を書いて、親しい友人にベレニカのことを尋ねたいの」と。",
          TextMapId = "QuestDesc_10030217"
        },
        QuestDesc_10030218 = {
          ContentJP = "あなたたちはリズムの秘密基地に到着した。彼女の言っていた、名前入りの銀色の印鑑を探してみよう。",
          TextMapId = "QuestDesc_10030218"
        },
        QuestDesc_10030219 = {
          ContentJP = "あなたたちはリズムの秘密基地に到着した。彼女の言っていた、名前入りの銀色の印鑑を探してみよう。",
          TextMapId = "QuestDesc_10030219"
        },
        QuestDesc_100302sp101 = {
          ContentJP = "この市民とその仲間は、下水道で化け物に襲われ閉じ込められていた。その化け物こそ「カロン狩り」の犯人である可能性が高い。つまり、ここが正しい場所だったということだ。いまは化け物の姿が見えない。まずは扉のロックを解除し、市民たちを救出しよう。",
          TextMapId = "QuestDesc_100302sp101"
        },
        QuestDesc_100302sp102 = {
          ContentJP = "制御装置を復旧させるには、このエリアの水路をすべて通さなければならない。だが排水を開始した途端、管の奥から大量の穢獣が現れた！市民を守り抜け！",
          TextMapId = "QuestDesc_100302sp102"
        },
        QuestDesc_100302sp103 = {
          ContentJP = "詰まっていた水路はすべて解消された。今なら扉を開けられるはずだ。試してみよう。",
          TextMapId = "QuestDesc_100302sp103"
        },
        QuestDesc_100302sp104 = {
          ContentJP = "制御装置を復旧させるには、このエリアの水路をすべて通さなければならない。だが排水を開始した途端、管の奥から大量の穢獣が現れた！市民を守り抜け！",
          TextMapId = "QuestDesc_100302sp104"
        },
        QuestDesc_100302sp201 = {
          ContentJP = "2人目の市民は、下水道の防御装置の先で動けなくなっていた。制御装置を操作して装置を止め、彼を助け出そう。",
          TextMapId = "QuestDesc_100302sp201"
        },
        QuestDesc_100302sp202 = {
          ContentJP = "このエリアの制御装置も、例によって水路の詰まりで動作が止まっている。バルブを開き、排水を進めて機能を回復させよう。",
          TextMapId = "QuestDesc_100302sp202"
        },
        QuestDesc_100302sp203 = {
          ContentJP = "2人目の市民は、下水道の防御装置の先で動けなくなっていた。制御装置を操作して装置を止め、彼を助け出そう。",
          TextMapId = "QuestDesc_100302sp203"
        },
        QuestDesc_100302sp301 = {
          ContentJP = "「カロン狩り」の犯人がついに咆哮を上げて現れた。今こそ決着をつける時だ。事件を終わらせよう。",
          TextMapId = "QuestDesc_100302sp301"
        },
        QuestDesc_10030301 = {
          ContentJP = "リズムの秘密基地で、あなたたちは彼女の印鑑だけでなく、過去の思い出が詰まった小さな品々もいくつか発見した。一つひとつに目を通すうち、あなたは少しだけリズムという人物を理解できた気がした。もしかすると、最初からあなたたちは、決して相容れない存在というわけではなかったのかもしれない。この印鑑を持ち帰り、ついでに彼女の話も聞いてみよう。",
          TextMapId = "QuestDesc_10030301"
        },
        QuestDesc_10030302 = {
          ContentJP = "トラブルに巻き込まれないため、今は優先に十三軍団のアイスレイク城での行動に協力することにしよう。",
          TextMapId = "QuestDesc_10030302"
        },
        QuestDesc_10030303 = {
          ContentJP = "カロン狩り事件は新たな段階を迎えた。あなたたちはクレフからの命令を受け、シルバーピース広場の地下にある下水道エリアへと向かうことになった。現地に到着したら、再びクレフと連絡を取ってみよう。",
          TextMapId = "QuestDesc_10030303"
        },
        QuestDesc_10030304 = {
          ContentJP = "リズムと人探しの件を話し終えたそのとき、十三軍団のリーン長官が突然病室へと現れ、カロン狩り事件の指揮権を正式に接収すると宣言した。幸いなことに、彼女はまだあなたとシビルの関係には気づいていないようだった。さらに彼女は、十三軍団への協力を打診してくる。今は表向きだけでも協力姿勢を見せて、穏便に対応するのが得策だろう。",
          TextMapId = "QuestDesc_10030304"
        },
        QuestDesc_100303sp101 = {
          ContentJP = "あなたは協力者として「カロン狩り」の調査を継続することを決意した。リーンもすぐさまあなたに任務を言い渡す。まずはアイスレイク城の外でクレフ長官と合流し、十三軍団が必要とする物資の受け渡しを行おう。",
          TextMapId = "QuestDesc_100303sp101"
        },
        QuestDesc_100303sp102 = {
          ContentJP = "数日間の任務を終えたあなたたちは集合地点に集まったが、そこにリズムの姿はなかった。何かあったのだろうか？近くを探してみよう。",
          TextMapId = "QuestDesc_100303sp102"
        },
        QuestDesc_100303sp103 = {
          ContentJP = "十三軍団による影響で困窮する市民たちのため、リズムは生活物資の手配を引き受けていた。あなたたちも仲間として、それを手伝おう。",
          TextMapId = "QuestDesc_100303sp103"
        },
        QuestDesc_100303sp104 = {
          ContentJP = "魚の調達を終え、すべての物資がそろった。それらを市民たちのもとへ届けに行こう。",
          TextMapId = "QuestDesc_100303sp104"
        },
        QuestDesc_100303sp105 = {
          ContentJP = "物資をマギーに渡し終えると、すぐにクレフから新たな指示が下った。どうやら十三軍団が動き出すようだ。至急、そちらに向かおう。",
          TextMapId = "QuestDesc_100303sp105"
        },
        QuestDesc_100303sp201 = {
          ContentJP = "下水道のあちこちに十三軍団の兵士たちが配置され、それぞれが持ち場を担当していた。狭く湿った空間に張り詰めた空気が漂う。クレフが「ノクトボイジャー」に割り当てたエリアへ、急いで向かおう。",
          TextMapId = "QuestDesc_100303sp201"
        },
        QuestDesc_100303sp202 = {
          ContentJP = "下水道のあちこちに十三軍団の兵士たちが配置され、それぞれが持ち場を担当していた。狭く湿った空間に張り詰めた空気が漂う。クレフが「ノクトボイジャー」に割り当てたエリアへ、急いで向かおう。",
          TextMapId = "QuestDesc_100303sp202"
        },
        QuestDesc_100303sp203 = {
          ContentJP = "クレフが割り当てたエリアには、今はまだ入れない。またしても廃水のせいで、制御装置が作動しないようだ。まずは廃水を排出してロックを解除し、それから指定のエリアへ向かおう。",
          TextMapId = "QuestDesc_100303sp203"
        },
        QuestDesc_100303sp204 = {
          ContentJP = "いくつもの障害を越え、ようやく割り当てられたエリアへ入れるようになった。すぐに向かおう。",
          TextMapId = "QuestDesc_100303sp204"
        },
        QuestDesc_100303sp205 = {
          ContentJP = "周囲の確認を終え、クレフに報告しようとするも、リズムの通信機が故障しているようで、クレフの声が聞き取れなかった。修理できるか試してみよう。",
          TextMapId = "QuestDesc_100303sp205"
        },
        QuestDesc_100303sp206 = {
          ContentJP = "通信機が直ると、犯人が十三軍団の封鎖を突破してこちらに向かっていることが判明した。間一髪のところで、リーンの援軍が間に合った。いまこそ犯人を倒し、事件に終止符を打とう。",
          TextMapId = "QuestDesc_100303sp206"
        },
        QuestDesc_10030401 = {
          ContentJP = "カロン狩り事件は、十三軍団による一方的な「解決宣言」のもと、名目上の幕引きを迎えた。彼らは勝利と栄光を手に帝都へと凱旋したが、あなたとリズムにはわかっていた。この事件が、本当はまだ終わってなどいないことを。",
          TextMapId = "QuestDesc_10030401"
        },
        QuestDesc_10030402 = {
          ContentJP = "集めた手がかりをもとに、犯人の正体は「オペラ」と何らかの関係があると推測される。彼が口ずさんでいた旋律は、おそらくオペラの一節だ。それが何の曲か判明すれば、調査の手がかりになるはずだ。リズムの弟・ブルースもまたオペラ好きで、秘密基地には彼の残した大量の録音テープが保管されているという。その中に、犯人の口ずさんだ旋律に近いものがあるかもしれない。調べてみよう。",
          TextMapId = "QuestDesc_10030402"
        },
        QuestDesc_10030403 = {
          ContentJP = "ここからは、あなたの耳と記憶力の出番だ。ブルースが残した録音をすべて聞き、あの旋律に似たものを探し出そう。",
          TextMapId = "QuestDesc_10030403"
        },
        QuestDesc_10030404 = {
          ContentJP = "時間はあっという間に過ぎていった。あなたが録音を聞き続ける中、リズムは外の空気を吸いに出かけていた。馴染みある旋律を探す作業には時間も体力も要る。休憩次いでに、彼女を探しに行こう。",
          TextMapId = "QuestDesc_10030404"
        },
        QuestDesc_10030501 = {
          ContentJP = "あの「桂冠カロン」にふさわしいとされた栄誉など、最初から存在しなかった。内城に足を踏み入れたその先に待っていたのは、冷たい錬金院の改造台だけだった。今や「化け物」として命じられるまま動くだけの存在――それが、自分に残された唯一の役目だ。",
          TextMapId = "QuestDesc_10030501"
        },
        QuestDesc_10030502 = {
          ContentJP = "化け物として、生きる術はただひとつ。命令に従い、敵を倒すこと。",
          TextMapId = "QuestDesc_10030502"
        },
        QuestDesc_10030503 = {
          ContentJP = "化け物として、生きる術はただひとつ。命令に従い、実験場へ赴き、敵を倒せ。",
          TextMapId = "QuestDesc_10030503"
        },
        QuestDesc_10030504 = {
          ContentJP = "化け物として、生きる術はただひとつ。命令に従い、実験場へ赴き、敵を倒せ。",
          TextMapId = "QuestDesc_10030504"
        },
        QuestDesc_10030505 = {
          ContentJP = "化け物として、生きる術はただひとつ。命令に従い、実験場へ赴き、敵を倒せ。",
          TextMapId = "QuestDesc_10030505"
        },
        QuestDesc_10030506 = {
          ContentJP = "あの能天気が本当にやってのけるとは……もう後戻りはできない。逃げるんだ、人間として――生き延びるために。",
          TextMapId = "QuestDesc_10030506"
        },
        QuestDesc_10030507 = {
          ContentJP = "あの能天気が本当にやってのけるとは……もう後戻りはできない。逃げるんだ、人間として――生き延びるために。",
          TextMapId = "QuestDesc_10030507"
        },
        QuestDesc_10030508 = {
          ContentJP = "あの能天気が本当にやってのけるとは……もう後戻りはできない。逃げるんだ、人間として――生き延びるために。",
          TextMapId = "QuestDesc_10030508"
        },
        QuestDesc_10030601 = {
          ContentJP = "「桂冠カロン」なんて称号は、全部でっち上げだった。こんな嘘のために帝国軍に残るなんてリズムにとって残酷すぎる。錬金術院で見たすべてを彼女に伝えたうえで──最後の一歩を踏み出そう。",
          TextMapId = "QuestDesc_10030601"
        },
        QuestDesc_10030602 = {
          ContentJP = "銀燭祭りに向けた公演が、ガレア劇場で進行中だ。仮面をかぶり、奇抜な衣装をまとった人々が行き交うこの場所は、化け物の姿を隠すにはうってつけの環境だ。さあ、約束した相手に会いに行こう。",
          TextMapId = "QuestDesc_10030602"
        },
        QuestDesc_10030603 = {
          ContentJP = "十三軍団が去ってから数日が経ち、リズムも「ブルースが十三軍団に殺された」という悲しみから、少しだけ立ち直ったようだ。彼女には、まだあなたに伝えたいことがあるらしい。会いに行こう。",
          TextMapId = "QuestDesc_10030603"
        },
        QuestDesc_10030604 = {
          ContentJP = "カロン狩り事件は終了し、フォーセイクン同盟によるコキュートス地区への侵攻も未遂に終わった。だが――まだ解決していないことが、たくさんある。",
          TextMapId = "QuestDesc_10030604"
        },
        QuestDesc_1003plus01 = {
          ContentJP = "痛みと別れが交錯する、果てしなく長い一日がようやく終わりを告げた。\nしかしこんな夜に眠れるはずがない。あなたは一人で夜の散歩に出かけた。",
          TextMapId = "QuestDesc_1003plus01"
        },
        QuestDesc_1003plus02 = {
          ContentJP = "アウトサイダーとスノウから、あなたが5日前に「カロン狩り」と呼ばれる連続殺人事件に巻き込まれていたことを知らされた。だが事件の詳細を聞く間もなく、突然リズムが現れて話は中断される。逃げ場はない──ならば、彼女の話を聞いてみよう。",
          TextMapId = "QuestDesc_1003plus02"
        },
        QuestDesc_1003plus03 = {
          ContentJP = "あなたは「カロン狩り」と関わりのある手がかりをいくつか発見した。単体では意味がつかめなくても、複数を組み合わせれば新たな発見に繋がるかもしれない。",
          TextMapId = "QuestDesc_1003plus03"
        },
        QuestDesc_1003plus04 = {
          ContentJP = "話し合いの末、あなたはリズムと協力し、カロン狩り事件の調査にあたることにした。次の行動は、現場を調べて犯人に繋がる手がかりを見つけ、調査の方針を定めることだ。",
          TextMapId = "QuestDesc_1003plus04"
        },
        QuestDesc_1003plus05 = {
          ContentJP = "ガレア劇場に到着すると、巨大なポスターがいくつも目に飛び込んできた。そこから何かわかることがあるかもしれない。近づいて確認しよう。",
          TextMapId = "QuestDesc_1003plus05"
        },
        QuestDesc_1003plus06 = {
          ContentJP = "道具師は、事件現場で見つかった舞台幕は劇団のものではないと主張した。劇団の道具はすべて道具師に厳しく管理されており、紛失報告はないという。彼女の発言を裏付ける複数人が記入した道具リストが近くにある。内容を確認してみよう。",
          TextMapId = "QuestDesc_1003plus06"
        },
        QuestDesc_1003plus07 = {
