        },
        RESOURCE_DESC_10004 = {
          ContentJP = "華胥製の巨大で重い矢。荒れ狂う嵐さえもその進路を阻めない。どのような人が射るのか想像し難いが、流星のごとく空を裂くその光景は、観る者の心に深く刻まれるだろう。",
          TextMapId = "RESOURCE_DESC_10004"
        },
        RESOURCE_DESC_10005 = {
          ContentJP = "砂漠のトカゲの鱗。特殊な構造により高温にも耐えうる。この特性が、「トカゲと竜は近い種族である」という説の一つの証拠となるだろうが、残念ながら竜は童話や幻想のなかの存在に過ぎない。",
          TextMapId = "RESOURCE_DESC_10005"
        },
        RESOURCE_DESC_10006 = {
          ContentJP = "消えることのない純粋な炎。この炎が燃え続ける限り、ケメトは決して滅びない。砂漠の民がこの火を贈るのは、永遠の友情を象徴している。",
          TextMapId = "RESOURCE_DESC_10006"
        },
        RESOURCE_DESC_10007 = {
          ContentJP = "長時間鍛えられた特殊な鎖。簡単に溶けることはない。この鎖は、最も手に負えない囚人を縛るために使われる。",
          TextMapId = "RESOURCE_DESC_10007"
        },
        RESOURCE_DESC_10008 = {
          ContentJP = "冷えつつある溶岩。外側は冷えて黒い岩に覆われ、内部には冷え始めたマグマがうっすら見える。",
          TextMapId = "RESOURCE_DESC_10008"
        },
        RESOURCE_DESC_10009 = {
          ContentJP = "ルッカの特産品、瑠璃色の真珠。群青色の貝から採れる真珠は不規則な形が多く、完全に丸いものはめったに見つからない。ルッカを訪れる際は、安価な染色された偽物を購入しないよう注意しよう。",
          TextMapId = "RESOURCE_DESC_10009"
        },
        RESOURCE_DESC_1001 = {
          ContentJP = "時間と記憶がつまった砂時計。移ろいゆく歳月が、無数の花を彩り、無数の時を刻む。",
          TextMapId = "RESOURCE_DESC_1001"
        },
        RESOURCE_DESC_10010 = {
          ContentJP = "ルッカ特産の瑠璃色の真珠。深い青色の貝から取り出されるものの多くは不規則な形をしており、完全な丸形のものは珍しい。ルッカを訪れる際には、安価の染色された偽珠を誤って購入しないよう注意しよう。",
          TextMapId = "RESOURCE_DESC_10010"
        },
        RESOURCE_DESC_10011 = {
          ContentJP = "ヒュペリア帝国製のコンパクトな浄水装置。帝国遠征軍の標準装備。行軍中に水を浄化し、兵士たちの健康を守るために役立つ。",
          TextMapId = "RESOURCE_DESC_10011"
        },
        RESOURCE_DESC_10012 = {
          ContentJP = "極北から採掘された希少な氷。水晶のように透明で、露のように輝く。溶けては再び凍るその様子は、まるで創造主の涙のように美しい。",
          TextMapId = "RESOURCE_DESC_10012"
        },
        RESOURCE_DESC_10013 = {
          ContentJP = "華胥から伝わった、雷雨を地下へ導く装置。ここの国の空への渇望は、大地に生きるすべての存在の想像を超えている。",
          TextMapId = "RESOURCE_DESC_10013"
        },
        RESOURCE_DESC_10014 = {
          ContentJP = "雷雨の夜に落ちてくる特別な鉱石。鏡のような面に紫色の電流がかすかに流れている。",
          TextMapId = "RESOURCE_DESC_10014"
        },
        RESOURCE_DESC_10015 = {
          ContentJP = "神話に登場するような希少な金色の羊毛。金色の羊は実際に存在し、もし財力があればその羊毛でセーターを編むこともできる。しかし、この羊毛は静電気を帯びやすく、動くたびに電光がちらつく。",
          TextMapId = "RESOURCE_DESC_10015"
        },
        RESOURCE_DESC_10016 = {
          ContentJP = "華胥の人々によって「捕らえられた」雷。瓶の中でも活発に動いている。この技術は200年前、独りで雷鳴平原に足を踏み入れた「狩人」の二つ名を持つ祭司から始まった。",
          TextMapId = "RESOURCE_DESC_10016"
        },
        RESOURCE_DESC_10017 = {
          ContentJP = "精巧な鎖が付いた単眼鏡。イーザーの高名な学者たちによって愛用され、「啓発のレンズ」として知られている。",
          TextMapId = "RESOURCE_DESC_10017"
        },
        RESOURCE_DESC_10018 = {
          ContentJP = "エリュシオン礼賛会の装飾品。徳の高い少数の者だけが身に着けることが許されている。巻きつけられた花と葉は、篤い信仰と服従を象徴し、その信仰はまるで黄金のように純粋である。",
          TextMapId = "RESOURCE_DESC_10018"
        },
        RESOURCE_DESC_10019 = {
          ContentJP = "エリュシオン礼賛会の古典。詩の形式で歌われ、内容は歴代総主教が記録した神の教えを伝えている。これは何年も前に印刷された古い本である。",
          TextMapId = "RESOURCE_DESC_10019"
        },
        RESOURCE_DESC_1002 = {
          ContentJP = "スノウの言葉を借りれば、お婆ちゃんのお婆ちゃんのお婆ちゃんくらいの年齢のはずだが、ルーナ族の鍛冶師はなぜかおもちゃのハンマーを好んで使う。ハンマーなら何でもいいのだろうか？\nおもちゃのハンマーを渡すと、製造スケジュールを繰り上げてもらえるらしい。「これは年長者への礼儀であって、賄賂ではないぞ、絶対に！」（本人談）",
          TextMapId = "RESOURCE_DESC_1002"
        },
        RESOURCE_DESC_10020 = {
          ContentJP = "正義の女神の象徴であり、平等と公正を意味する天秤。エリュシオン礼賛会の儀式に欠かせないもの。神の恵みが移ろえば天秤が傾くと言われるが、幸いにも九神の加護が変わったことはない。",
          TextMapId = "RESOURCE_DESC_10020"
        },
        RESOURCE_DESC_10021 = {
          ContentJP = "錆びついた匕首。精巧な模様がその持ち主のかつての高貴さを示しているが、その人物がどの戦場で幽魂となり果てたのかはわからない。",
          TextMapId = "RESOURCE_DESC_10021"
        },
        RESOURCE_DESC_10022 = {
          ContentJP = "イーザーでは、黒曜石は夜に生まれる霊感を象徴する。イーザーのカロン学者は卒業時にこの鍵を授かり、その深い意味を彼らだけが理解している。",
          TextMapId = "RESOURCE_DESC_10022"
        },
        RESOURCE_DESC_10023 = {
          ContentJP = "九神に背を向け、「深淵」を崇拝する闇月派の理念は、当然ながら禁忌とされている。しかし、禁忌には魅力的がある。求める心さえあれば、新しい世界への扉はいつだって開かれている。",
          TextMapId = "RESOURCE_DESC_10023"
        },
        RESOURCE_DESC_10024 = {
          ContentJP = "ヒュペリア帝国製の培養プレート。穢獣の細胞液を封じ込めおり、開けてみないと何が起こるかわからない。計画通りにいかないかもしれないが、新たな発見や恐怖をもたらす可能性もある。",
          TextMapId = "RESOURCE_DESC_10024"
        },
        RESOURCE_DESC_1003 = {
          ContentJP = "時間と記憶がつまった砂時計。移ろいゆく歳月が、無数の花を彩り、無数の時を刻む。",
          TextMapId = "RESOURCE_DESC_1003"
        },
        RESOURCE_DESC_1006 = {
          ContentJP = "どんな平凡な武器でも、匠人の幾度もの鍛錬を経れば、束縛を打ち破り、鋭き輝きを取り戻せる。",
          TextMapId = "RESOURCE_DESC_1006"
        },
        RESOURCE_DESC_101 = {
          ContentJP = "各国で流通している基本通貨。\n価値はそれほど高くないが、日常生活のさまざまな取引に使用される。",
          TextMapId = "RESOURCE_DESC_101"
        },
        RESOURCE_DESC_10100 = {
          ContentJP = "これまで行った長い行く道を振り返ると、すべての思い出が星々のカーペットを織りなした。\nそこに含められた喜びも悲しみも、だんだん成長の証となっていた。",
          TextMapId = "RESOURCE_DESC_10100"
        },
        RESOURCE_DESC_10101 = {
          ContentJP = "かつて恐れたすべてに直面しなければ、うまく使いこなせない。",
          TextMapId = "RESOURCE_DESC_10101"
        },
        RESOURCE_DESC_10102 = {
          ContentJP = "かつて恐れたすべてに直面しなければ、うまく使いこなせない。",
          TextMapId = "RESOURCE_DESC_10102"
