        RESOURCE_DESC_5110500 = {
          ContentJP = "ルーナ族の鍛冶師が手がけた釣り竿。付属の水面監視者「すみっコ」は、魚がかかった際に鳴き声で合図してくれる。ただし、あまりに愛らしく見入ってしまい、肝心のタイミングを逃す者も少なくないとか。",
          TextMapId = "RESOURCE_DESC_5110500"
        },
        RESOURCE_DESC_5210100 = {
          ContentJP = "魚を引き寄せるための一般的なアイテム。ただし、あくまで誘引効果があるだけで、実際に釣れるかどうかは運と腕次第。",
          TextMapId = "RESOURCE_DESC_5210100"
        },
        RESOURCE_DESC_5210200 = {
          ContentJP = "魚が上釣るまでの時間を短縮できる特製餌。水中でひねるようにうねりながら動くため、周囲の魚たちの視線を一身に集める。その奇妙な動きに、水底の住人たちは思わず近づいてしまうようだ。",
          TextMapId = "RESOURCE_DESC_5210200"
        },
        RESOURCE_DESC_5210300 = {
          ContentJP = "変異種の食いつき率を高める特別な餌。見た目はただの金属片だが、その光沢のせいか、変異種の目にはたまらなく魅力的に映るらしい。",
          TextMapId = "RESOURCE_DESC_5210300"
        },
        RESOURCE_DESC_5210400 = {
          ContentJP = "変異種を釣り上げる確率を高める特別な餌。見た目は地味だが、原材料は一級品。ひとたび味わえば、どんな偏食の魚でも完食せずにはいられない。",
          TextMapId = "RESOURCE_DESC_5210400"
        },
        RESOURCE_DESC_6000001 = {
          ContentJP = "シリュウ傘下の商会「百年春」が発行した信用通貨。",
          TextMapId = "RESOURCE_DESC_6000001"
        },
        RESOURCE_DESC_99 = {
          ContentJP = "天上樹から採取された結晶で、アトラシア大陸では広く利用されている鉱物。\n満月のようなやわらかな輝きを放つことから、この名がついた。",
          TextMapId = "RESOURCE_DESC_99"
        },
        RESOURCE_DESC_CANCELPHANTOM = {
          ContentJP = "召喚を撤回するための印。\nつかのまの邂逅だとしても、想いは途絶えない。きっとすぐにまた、再会できる日が来るのだから。",
          TextMapId = "RESOURCE_DESC_CANCELPHANTOM"
        },
        RESOURCE_DESC_GENERALSKIN_1 = {
          ContentJP = "使用するとベレニカ、ファンタジオ、リズベル、ニフル夫人、フィーナ、レベッカ、タビテ、リーン、イェール＆オリバー、ヘルファイア、マギー、リズム、ランディー、シビル、トリュフ&ヘーゼルナッツ、アウトサイダー、サイキ、ダフネの中からキャラクターの外見一式を一つ選択して獲得できます。",
          TextMapId = "RESOURCE_DESC_GENERALSKIN_1"
        },
        RESOURCE_DESC_MUSIC = {
          ContentJP = "一枚の楽譜――音符が物語と軌跡を記す。",
          TextMapId = "RESOURCE_DESC_MUSIC"
        },
        RESOURCE_DESC_OPTWEAPON_1 = {
          ContentJP = "使用するとリストから任意の武器を1つ選んで入手できます。",
          TextMapId = "RESOURCE_DESC_OPTWEAPON_1"
        },
        RESOURCE_DESC_PHANTOM = {
          ContentJP = "戦友を召喚するための印。それは、肩を並べて戦う決意であり、手をとりあって歩む信頼であり、無言の誓いである。\n祈りが思いとなり伝わったとき、望み通りその人は現れるだろう。あなたの傍らに。",
          TextMapId = "RESOURCE_DESC_PHANTOM"
        },
        RESOURCE_DESC_ROOT = {
