        TextMap_AnswerDetail_100003 = {
          ContentJP = "舞台幕が、疑いの目をガレア劇場へと向けさせた。そして、犯人が口ずさんでいた不気味な歌声もまた、その推測を裏付けるようだ。いずれにせよ、劇場は調査する価値がある場所だ。",
          TextMapId = "TextMap_AnswerDetail_100003"
        },
        TextMap_AnswerDetail_100004 = {
          ContentJP = "犯人は南西方向へ逃走した。その方面で大量のイコル液と接触できる場所は、工場しかない。どうあれ、工場は捜査の手を伸ばすべき場所だろう。",
          TextMapId = "TextMap_AnswerDetail_100004"
        },
        TextMap_AnswerDetail_100005 = {
          ContentJP = "舞台の小道具で隠れ家を飾り、襲撃の際にはオペラの旋律を口ずさむ。これらの行動から、犯人はオペラを好む人物だと推測できる。ならば、オペラの旋律を手がかりに、犯人に繋がる糸口が見つかるかもしれない。",
          TextMapId = "TextMap_AnswerDetail_100005"
        },
        TextMap_AnswerDetail_100101 = {
          ContentJP = "少し記憶を辿るだけで、襲われた瞬間、月がどのあたりにあったかが思い出された。その位置から推測するに、襲撃があったのは午前4時から5時の間と考えられる。",
          TextMapId = "TextMap_AnswerDetail_100101"
        },
        TextMap_AnswerDetail_100201 = {
          ContentJP = "新品の風見鶏──おそらく犯人があの夜、屋根に飛び乗った際に折ってしまったのだろう。ほかの手がかりと組み合わせれば、さらに情報が引き出せるかもしれない。",
          TextMapId = "TextMap_AnswerDetail_100201"
        },
        TextMap_AnswerDetail_100202 = {
          ContentJP = "風見鶏の台座は南西の方角へと曲がっていた。これも他の証拠と合わせれば、より明確な情報が見えてくるはずだ。",
          TextMapId = "TextMap_AnswerDetail_100202"
        },
        TextMap_AnswerDetail_100203 = {
          ContentJP = "風見鶏と台座を見比べた結果、欠けた部分と折れ跡がぴたりと一致していた。このことから、それは犯人が現場から逃げる際、屋根を飛び移ろうとしてうっかり壊してしまったものだとわかる。\nそして、台座の曲がった方向から、犯人は南西へ逃走したことが推測される。",
          TextMapId = "TextMap_AnswerDetail_100203"
        },
        TextMap_AnswerDetail_100301 = {
          ContentJP = "あの夜、犯人はしゃがれた声で、どこか旋律のような、不気味な歌を口ずさんでいた……もしかすると、歌が好きなのか、それとも得意なのか。",
          TextMapId = "TextMap_AnswerDetail_100301"
        },
        TextMap_AnswerDetail_100302 = {
          ContentJP = "ベルベット地の舞台幕──近くでこれと関係がありそうな場所は、ガレア劇場ただひとつだ。この劇場は事件と何か関わりがあるのだろうか？他の手がかりでこの疑いを補強できないだろうか？",
          TextMapId = "TextMap_AnswerDetail_100302"
        },
        TextMap_AnswerDetail_100303 = {
          ContentJP = "舞台幕がガレア劇場への疑いを強め、犯人の歌声がそれをさらに裏付けている。どうあれ、ガレア劇場は調べておくべき場所だ。",
          TextMapId = "TextMap_AnswerDetail_100303"
        },
        TextMap_AnswerDetail_100401 = {
          ContentJP = "犯人が逃げた南西方面には、工場、住宅、学校など多くの建物がある。あまりに広範囲のせいか、リズムの調査もなかなか進んでいないようだ。何か他の手がかりで、絞り込む必要があるだろう。",
          TextMapId = "TextMap_AnswerDetail_100401"
        },
        TextMap_AnswerDetail_100402 = {
          ContentJP = "現場周辺ではイコル液の漏出は確認されていない。ならば、この雪の上に落ちていた数滴のイコル液はどこから来たのだろうか？事件当夜の詳細について、リズムと話せば、さらに手がかりが得られるかもしれない。",
          TextMapId = "TextMap_AnswerDetail_100402"
        },
        TextMap_AnswerDetail_100403 = {
          ContentJP = "あの晩、犯人からは強いイコル液の匂いが漂っていた。つまり、かなりの量が体についていたということだ。アイスレイク城でそれほどのイコル液と接触できる場所は限られている。別の手がかりと組み合わせれば、犯人がどこに現れたか、さらに絞り込めるかもしれない。",
          TextMapId = "TextMap_AnswerDetail_100403"
        },
        TextMap_AnswerDetail_100404 = {
          ContentJP = "犯人が逃げた南西方面で、大量のイコル液と接触できる場所は工場だけだ。そこが犯人の隠れ場所なのだろうか？いずれにしても、工場は調べる価値がある。",
          TextMapId = "TextMap_AnswerDetail_100404"
        }
      }
    end
  },
  {
    MinKey = "TextMap_AnswerDetail_100501",
    MaxKey = "Title_20012",
    Loader = function()
      return {
        TextMap_AnswerDetail_100501 = {
          ContentJP = "下水道で見つかった舞台幕に、丁寧に配置されたマネキンたち……。犯人と「舞台」との間に、何らかのつながりがあるのかもしれない。この手がかりを他の情報と組み合わせれば、犯人の正体に近づける新たな視点が見つかるはずだ。",
          TextMapId = "TextMap_AnswerDetail_100501"
        },
        TextMap_AnswerDetail_100502 = {
          ContentJP = "あの夜、犯人はしゃがれた声で、どこか旋律のような、不気味な歌を口ずさんでいた……もしかすると、歌が好きなのか、それとも得意なのか。",
          TextMapId = "TextMap_AnswerDetail_100502"
        },
        TextMap_AnswerDetail_100503 = {
          ContentJP = "舞台の小道具で隠れ家を飾り、襲撃の際にはオペラの旋律を口ずさむ。これらの行動から、犯人はオペラを好む人物だと推測できる。ならば、オペラの旋律を手がかりに、犯人に繋がる糸口が見つかるかもしれない。",
          TextMapId = "TextMap_AnswerDetail_100503"
        },
        TextMap_AnswerDetail_200101 = {
          ContentJP = "バックさんはアスフォデロスではジュースと牛乳しか飲まない。酒はまったく飲めず、少しでも口にすれば即座に眠ってしまう。",
          TextMapId = "TextMap_AnswerDetail_200101"
        },
        TextMap_AnswerDetail_200102 = {
          ContentJP = "誰かがこっそりバックさんのミルクに酒を混ぜ、彼を眠らせようとしたようだ。よく見ると、ミルクポットにはベタベタした小さな手形が残っている。",
          TextMapId = "TextMap_AnswerDetail_200102"
        },
        TextMap_AnswerDetail_200103 = {
          ContentJP = "子どもの足跡が屋外からバックさんの窓辺まで続いており、誰かが窓から家に侵入して何かをしたことが分かる。",
          TextMapId = "TextMap_AnswerDetail_200103"
        },
        TextMap_AnswerDetail_200104 = {
          ContentJP = "ポップコーンが数粒、足跡の周囲に散らばっている。侵入者の身体に付いていたものが、窓を越える際に雪の中に落ちたのだろう。",
          TextMapId = "TextMap_AnswerDetail_200104"
        },
        TextMap_AnswerDetail_200105 = {
          ContentJP = "寒さで震えながら通行人に商品を売り歩くカロンの少年。ビールやポップコーンのほか、ガレア劇場のチケットや記念ポスターも売っている。",
          TextMapId = "TextMap_AnswerDetail_200105"
        },
        TextMap_AnswerDetail_200106 = {
          ContentJP = "バックさんがお酒を飲めないことを知っている子どもが、彼の家に侵入し、ミルクに酒を混ぜて眠らせている。これがバックさんが毎晩早々に眠ってしまう理由だ。",
          TextMapId = "TextMap_AnswerDetail_200106"
        },
        TextMap_AnswerDetail_200107 = {
          ContentJP = "ゴミ箱のそばで酔いつぶれている男。どれだけの酒を飲んだかわからないほどの酒好きで、侵入者が子どもだという手がかりとも一致しないし、人の飲み物にわざわざ酒を入れるタイプにも見えない。",
          TextMapId = "TextMap_AnswerDetail_200107"
        },
        TextMap_AnswerDetail_200108 = {
          ContentJP = "父親にポップコーンを買ってほしいとせがんでいる女の子。子どもではあるが、父親の厳しい管理下にあるため、バックさんの窓辺に落ちていたポップコーンが彼女の落とし物だという可能性はないだろう。",
          TextMapId = "TextMap_AnswerDetail_200108"
        },
        TextMap_AnswerDetail_200201 = {
          ContentJP = "ドナートの親友だったシグが十六年前に誘拐事件を計画した。だが、被害者のエノラが事故死し、最終的にシグも死刑となった。",
          TextMapId = "TextMap_AnswerDetail_200201"
        },
        TextMap_AnswerDetail_200202 = {
          ContentJP = "月狩り人ジゼルは元々、警備部隊に所属していたが、とある事件の判決を巡り上層部と対立し、辞職した。",
          TextMapId = "TextMap_AnswerDetail_200202"
        },
        TextMap_AnswerDetail_200203 = {
          ContentJP = "十六年前にアイスレイク城を離れたシグの妹・ヴェルレッタが戻ってきたらしい。兄の墓前に彼女のものと思しき紫色のリボンが残されていた。",
          TextMapId = "TextMap_AnswerDetail_200203"
        },
        TextMap_AnswerDetail_200204 = {
          ContentJP = "ヴィオレッタは公演のたびに紫色のリボンを身に着けている。それは、大切な友人からもらった思い出の品だと彼女は語っている。",
          TextMapId = "TextMap_AnswerDetail_200204"
        },
        TextMap_AnswerDetail_200205 = {
          ContentJP = "マグナス公爵の娘・ロザリンが誘拐された疑いがある。事件の前、公爵夫妻は娘のために音楽の先生を雇っていたらしい。",
          TextMapId = "TextMap_AnswerDetail_200205"
        },
        TextMap_AnswerDetail_200206 = {
          ContentJP = "ヴィオレッタの教え子のひとりが誘拐された。その脅迫状には、十六年前の事件を思わせる内容が書かれていた。",
          TextMapId = "TextMap_AnswerDetail_200206"
        },
        TextMap_AnswerDetail_200207 = {
          ContentJP = "ジゼルは当時、事件の捜査に携わっており、その判決を巡る失望から警備部隊を去った。",
          TextMapId = "TextMap_AnswerDetail_200207"
        },
        TextMap_AnswerDetail_200208 = {
          ContentJP = "リボンの色も形も一致している。名前の響きも似ており、ふたりの間には深い関係があると見て間違いなさそうだ。",
          TextMapId = "TextMap_AnswerDetail_200208"
        },
        TextMap_AnswerDetail_200209 = {
          ContentJP = "ロザリンはヴィオレッタの生徒だったと見られ、脅迫状に十六年前の事件が示唆されていることから、ヴィオレッタも関わっている可能性がある。",
          TextMapId = "TextMap_AnswerDetail_200209"
        },
        TextMap_AnswerDetail_200210 = {
          ContentJP = "ロザリン誘拐の動機は、過去の事件に起因しており、ヴィオレッタはその内情を知っている。",
          TextMapId = "TextMap_AnswerDetail_200210"
        },
        TextMap_AnswerDetail_200211 = {
          ContentJP = "十六年前の事件には真相が隠されており、ヴィオレッタはその鍵を握っていた。ジゼルは彼女をおびき出すため、生徒を誘拐した。",
          TextMapId = "TextMap_AnswerDetail_200211"
        },
        TextMap_AnswerDetail_200301 = {
          ContentJP = "窓辺に置かれた告蒼花。朝露がまだついており、摘まれてから間もないことがわかる。",
          TextMapId = "TextMap_AnswerDetail_200301"
        },
        TextMap_AnswerDetail_200302 = {
          ContentJP = "百年春の静かな一角。リンリンの足跡以外に他人の痕跡は見当たらず、特に気になる場所もない。",
          TextMapId = "TextMap_AnswerDetail_200302"
        },
        TextMap_AnswerDetail_200303 = {
          ContentJP = "入口に落ちていたアルバム。中には家族写真がたくさん収められている。その一枚には、小さなリンリンが告蒼花を抱え、祖母と一緒にこの家の前にいる姿が写っていた。",
          TextMapId = "TextMap_AnswerDetail_200303"
        },
        TextMap_AnswerDetail_200304 = {
          ContentJP = "手紙にはリンリンの一家が仕事の都合で皓京煙津渡の実家へ戻り、かつて暮らしていた宿舎に一時的に身を寄せているとあった。",
          TextMapId = "TextMap_AnswerDetail_200304"
        },
        TextMap_AnswerDetail_200305 = {
          ContentJP = "足跡を残さず、この花を届けに来た者がいる。",
          TextMapId = "TextMap_AnswerDetail_200305"
        },
        TextMap_AnswerDetail_200306 = {
          ContentJP = "「幽霊」はこの古い住まいを熟知しており、アルバムに残る思い出に強く縛られている。もしかすると、それもまたこの家の一員だったのかもしれない。",
          TextMapId = "TextMap_AnswerDetail_200306"
        },
        TextMap_AnswerDetail_200307 = {
          ContentJP = "アルバムに残された家族の記憶、告蒼花への愛着、古い住まいへの親しさ。それらすべてが、「帰ってきた故人」を指し示していた。",
          TextMapId = "TextMap_AnswerDetail_200307"
        },
