        UI_Accessory_Desc_30004 = {
          ContentJP = "工学錬金院が開発したイコル液専用の保管容器。中身が空でもファンには売れる。安定・安全・謎の魅力。",
          TextMapId = "UI_Accessory_Desc_30004"
        },
        UI_Accessory_Desc_30005 = {
          ContentJP = "この灯りがある限り、どんな闇も怖くない。",
          TextMapId = "UI_Accessory_Desc_30005"
        },
        UI_Accessory_Desc_30006 = {
          ContentJP = "迷った子の足元を照らす、ただの照明。でもそれだけで十分なんだ。",
          TextMapId = "UI_Accessory_Desc_30006"
        },
        UI_Accessory_Desc_30007 = {
          ContentJP = "理由はないけど、ついブンブン振りたくなる。魔性のスティック。",
          TextMapId = "UI_Accessory_Desc_30007"
        },
        UI_Accessory_Desc_30008 = {
          ContentJP = "光と影、陽と陰、天と地。すべてを一つに抱く玉。",
          TextMapId = "UI_Accessory_Desc_30008"
        },
        UI_Accessory_Desc_30009 = {
          ContentJP = "大勝利か、全ロスか。振るまでは、誰にも分からない。",
          TextMapId = "UI_Accessory_Desc_30009"
        },
        UI_Accessory_Desc_30010 = {
          ContentJP = "特別イベントで獲得可能",
          TextMapId = "UI_Accessory_Desc_30010"
        },
        UI_Accessory_Desc_30016 = {
          ContentJP = "満月の下で鍛えられた名刀。わずかに揺らすだけで、刃には流れる月光が映り込む。",
          TextMapId = "UI_Accessory_Desc_30016"
        },
        UI_Accessory_Desc_30017 = {
          ContentJP = "絵本を閉じたその瞬間、ほんとうの魔法が始まる——物語がくれた勇気を胸に、現実の世界を抱きしめよう。",
          TextMapId = "UI_Accessory_Desc_30017"
        },
        UI_Accessory_Desc_40001 = {
          ContentJP = "月狐の尻尾は、ほんの些細な湿度の変化も感知できるという。経験豊富な旅人は、月狐が雨の夜に踊っているかどうかで、いつ雲が晴れて月明かりが戻るかを判断している。",
          TextMapId = "UI_Accessory_Desc_40001"
        },
        UI_Accessory_Desc_40002 = {
          ContentJP = "刀とは、身を守るものであり、傷つけるものでもある。歴代の持ち主たちの尊厳と信念を宿しつつ、長い年月を超えて過去の教訓を今に伝えている。",
          TextMapId = "UI_Accessory_Desc_40002"
        },
        UI_Accessory_Desc_40003 = {
          ContentJP = "鉄の棒さえあれば、あらゆる車輪を回せる。俺はそう信じてる。",
          TextMapId = "UI_Accessory_Desc_40003"
        },
        UI_Accessory_Desc_40004 = {
          ContentJP = "いい鍋は料理にも武器にも使える。",
          TextMapId = "UI_Accessory_Desc_40004"
        },
        UI_Accessory_Desc_40005 = {
          ContentJP = "この風に乗れば、どこまでだって飛んでいける。",
          TextMapId = "UI_Accessory_Desc_40005"
        },
        UI_Accessory_Desc_40006 = {
          ContentJP = "光の魔王様に「最強のバックパック」と称えられた。それ以上の説明は、いらないよね？",
          TextMapId = "UI_Accessory_Desc_40006"
        },
        UI_Accessory_Desc_40007 = {
          ContentJP = "この風に乗れば、どこまでだって飛んでいける。",
          TextMapId = "UI_Accessory_Desc_40007"
        },
        UI_Accessory_Desc_40008 = {
          ContentJP = "一部の華胥の人々は、ある古くてしぶとい節足動物に不思議な愛着を抱いているらしく、このコスチュームからその一端をうかがえる。",
          TextMapId = "UI_Accessory_Desc_40008"
        },
        UI_Accessory_Desc_40009 = {
          ContentJP = "たとえ頼りない翼でも、子どもたちを凍てつく冬の向こうへ運べる。",
          TextMapId = "UI_Accessory_Desc_40009"
        },
        UI_Accessory_Desc_40010 = {
          ContentJP = "一枚の古いショールには、かつての「英雄」にまつわるすべての幻想が託されている。いつか再び日の目を見るかもしれないし、永遠に見ないかもしれない。",
          TextMapId = "UI_Accessory_Desc_40010"
        },
        UI_Accessory_Desc_50004 = {
          ContentJP = "{空格}",
          TextMapId = "UI_Accessory_Desc_50004"
        },
        UI_Accessory_Desc_60001 = {
          ContentJP = "{空格}",
          TextMapId = "UI_Accessory_Desc_60001"
        },
        UI_Accessory_Desc_70001 = {
          ContentJP = "蝶の飛びが生んだ波紋は、仲夏のすべてを目覚めさせた。",
          TextMapId = "UI_Accessory_Desc_70001"
        },
        UI_Accessory_Desc_80001 = {
          ContentJP = "蝶が三色スミレをくわえ、主の到来を待ち、舞踏会の序章を奏でる。",
          TextMapId = "UI_Accessory_Desc_80001"
        },
        UI_Accessory_Desc_90001 = {
          ContentJP = "ベレニカが愛用するマント。風に吹かれると、海鳥が翼を広げるような音をたてる。",
          TextMapId = "UI_Accessory_Desc_90001"
        },
        UI_Accessory_Desc_90002 = {
          ContentJP = "薄い布地に防風効果はないが、レベッカは全身がふわふわと包まれる感覚が気に入っている。",
          TextMapId = "UI_Accessory_Desc_90002"
        },
        UI_Accessory_Desc_90003 = {
          ContentJP = "背中の腕足は少女の腕の延長であり、大盗賊にとって器用な助っ人だ。\nそれは少女と共に、あらゆる街の夜空の下で自由に戯れている。",
          TextMapId = "UI_Accessory_Desc_90003"
        },
        UI_Accessory_Desc_90004 = {
          ContentJP = "編み込まれた革はまるで鎖のよう。\nそれを背負い、すべての罪を裁きに行こう。",
          TextMapId = "UI_Accessory_Desc_90004"
        },
        UI_Accessory_Desc_90005 = {
          ContentJP = "静水に跡なく心は定まり、流れる歳月の中でも志は揺るがない。",
          TextMapId = "UI_Accessory_Desc_90005"
        },
        UI_Accessory_Desc_90006 = {
          ContentJP = "帝国錬金院が優秀な錬金術師たちのために特別に仕立てたショールで、最高の栄誉と承認を象徴している。",
          TextMapId = "UI_Accessory_Desc_90006"
        },
        UI_Accessory_Desc_90007 = {
          ContentJP = "アウトサイダーが何度も繕ったマント。穴だらけのデザインは、いつしか霧の街の子どもたちにとってカッコよさとクールさの象徴となった。",
          TextMapId = "UI_Accessory_Desc_90007"
        },
        UI_Accessory_Desc_90008 = {
          ContentJP = "台本一冊をまるごと書き写す代わりに、少年は友人に頼んで劇場の古びたベルベットの幕を使って、自分だけの最初のショールを縫ってもらった。それは決して華やかではなかったが、彼に脚本の中で威風堂々とした騎士の姿を思い起こさせた……だが、それも今ではもう、遠い昔の話だ。",
          TextMapId = "UI_Accessory_Desc_90008"
        },
        UI_Accessory_Desc_90009 = {
          ContentJP = "「これはただのショールさ。難しいことなんて何もない。きれいだし、気に入ったから、そのまま羽織っただけさ。」",
          TextMapId = "UI_Accessory_Desc_90009"
        },
        UI_Accessory_Desc_90010 = {
          ContentJP = "一枚の古いショールには、かつての「英雄」にまつわるすべての幻想が託されている。リーンはそれを箱の底に置き鍵をかけた——いつか再び日の目を見るかもしれないし、永遠に見ないかもしれない。",
          TextMapId = "UI_Accessory_Desc_90010"
        },
        UI_Accessory_Desc_90011 = {
          ContentJP = "第六軍団特製のショール——リズムが無数の汗で勝ち取った報い。それは彼女が夢へ踏み出した決定的一歩であり、同時に最後の一歩でもあった。",
          TextMapId = "UI_Accessory_Desc_90011"
        },
        UI_Accessory_Desc_90012 = {
          ContentJP = "冷たい風が裾を鳴らし、思わずパーガトリー島のやわらかな白砂、そして手を取り合い、波しぶきを踏んだ二人を思い起こさせる。",
          TextMapId = "UI_Accessory_Desc_90012"
        },
        UI_Accessory_Desc_91001 = {
          ContentJP = "ベレニカの髪飾り\n「願い事？別に……。毎朝、あなたの笑顔が見られるだけで十分」",
          TextMapId = "UI_Accessory_Desc_91001"
        },
        UI_Accessory_Desc_91002 = {
          ContentJP = "タビテの髪飾り\n「……おい……おーい、遊びに連れてってくれよ！ヒマすぎて触手が固まりそうだ！」",
          TextMapId = "UI_Accessory_Desc_91002"
        },
        UI_Accessory_Desc_91003 = {
          ContentJP = "ヒルダの髪飾り\n「私の目には、目障りに飛び回るハエも穢獣も、掃いて捨てるべき汚れにしか映りません。戦闘は、私にとって掃除という愉快な作業にすぎないのです」",
          TextMapId = "UI_Accessory_Desc_91003"
        },
        UI_Accessory_Desc_91004 = {
          ContentJP = "フーシュの髪飾り\n「杏の花影にて、薬を作らば春秋巡る」\n「枯荣閣の庭にある杏の老木……今年もきれいな花を咲かせているのでしょうか。いつまで咲いてくれるのでしょうか。杏を摘みにくる子供たちが怪我をしていなければよいのですが……」",
          TextMapId = "UI_Accessory_Desc_91004"
        },
        UI_Accessory_Desc_91005 = {
          ContentJP = "ユーミンの髪飾り\n「あんたは俺にどんな役割を求めている？――友人？ それとも、あいつらのように自由に使える刃とでも？」",
          TextMapId = "UI_Accessory_Desc_91005"
        },
        UI_Accessory_Desc_91006 = {
          ContentJP = "サイキの髪飾り\n「外の世界には私が見たことがないお花がいっぱい咲いているのでしょ？本物のちょうちょが花から花へ飛んでいるかも……」",
          TextMapId = "UI_Accessory_Desc_91006"
        },
        UI_Accessory_Desc_91007 = {
          ContentJP = "レベッカの髪飾り\n「いろんな香りが愛を確かめながら、離れたくないって絡みあうの。そして長く満ちあふれた最後には、ひとつになる。『愛』って……そうあるべきじゃない？」",
          TextMapId = "UI_Accessory_Desc_91007"
        },
        UI_Accessory_Desc_91008 = {
          ContentJP = "トリュフ&ヘーゼルナッツの髪飾り\n「強くなるにはいっぱいご飯を食べないと、です。腹が減ってはバトルはできぬ！」",
          TextMapId = "UI_Accessory_Desc_91008"
        },
        UI_Accessory_Desc_91009 = {
          ContentJP = "リーンの髪飾り\n「あの女は上司であって、アタシは命令が来たらやる。ただそれだけだ」",
          TextMapId = "UI_Accessory_Desc_91009"
        },
