        },
        UI_Skin_Des_1101 = {
          ContentJP = "黒は夜の色。大地を包み、安らぎと静けさをもたらす。黒は影の色。寄り添い、最も大切な人を守り続ける。ベレニカは、この色を好んでいる。",
          TextMapId = "UI_Skin_Des_1101"
        },
        UI_Skin_Des_1103 = {
          ContentJP = "一つの歩く影、一人芝居の下手な役者、一人の愚者が語る物語。",
          TextMapId = "UI_Skin_Des_1103"
        },
        UI_Skin_Des_1104 = {
          ContentJP = "高級で豪奢なオーダーメイドの金箔衣装は、単なる衣ではなく、地位と身分の象徴でもある。",
          TextMapId = "UI_Skin_Des_1104"
        },
        UI_Skin_Des_1501 = {
          ContentJP = "礼賛会の銘文は服に刻まれるだけでなく、魂にまで刻み込まれる。それは少女が怒りから何度も脱皮し、真の忍耐へと至る過程を見届けている。",
          TextMapId = "UI_Skin_Des_1501"
        },
        UI_Skin_Des_1502 = {
          ContentJP = "薄暮と極光が織り成すヴェールは、涙に満ちた川を越え、すべてが新生する霧海へと魂を導く。",
          TextMapId = "UI_Skin_Des_1502"
        },
        UI_Skin_Des_1503 = {
          ContentJP = "舞い散る風雨は衣の裾を伝い、剣先で止まり、そして江湖に散った。",
          TextMapId = "UI_Skin_Des_1503"
        },
        UI_Skin_Des_1601 = {
          ContentJP = "白い衣に身を包み、剣を持って、夜の海を越える。夜明けの果てにまた会おう。",
          TextMapId = "UI_Skin_Des_1601"
        },
        UI_Skin_Des_1801 = {
          ContentJP = "ナイチンゲールが彼女に紡いだ物語は白き羽を授かり、小さな翼に夢を載せて雲の彼方へ舞い上がる。",
          TextMapId = "UI_Skin_Des_1801"
        },
        UI_Skin_Des_2101 = {
          ContentJP = "クラゲの触手が深海に垂れ下がり、泳ぐドレスの裾のように、夜の中で恋人の心を揺さぶる。",
          TextMapId = "UI_Skin_Des_2101"
        },
        UI_Skin_Des_210102 = {
          ContentJP = "幾重にも重なる紗の奥、囁きで愛を語り、死によって永遠に触れ合う。",
          TextMapId = "UI_Skin_Des_210102"
        },
        UI_Skin_Des_2301 = {
          ContentJP = "暗闇で蠢いているのは何だ？陰謀？混沌？犯罪？――違う、それは触手だ！",
          TextMapId = "UI_Skin_Des_2301"
        },
        UI_Skin_Des_2401 = {
          ContentJP = "薬草の香りは糸となって白衣の袖を伝い、枯草が蘇るかのような囁きを告げる。",
          TextMapId = "UI_Skin_Des_2401"
        },
        UI_Skin_Des_3101 = {
          ContentJP = "マットな制服に残る火薬の余韻は、熱き鼓動と共にいつまでも消えない。",
          TextMapId = "UI_Skin_Des_3101"
        },
        UI_Skin_Des_3103 = {
          ContentJP = "燃え盛る赤と白は狂人の爆破幻想を閉じ込め、少年の純粋な夢を縫い合わせる。",
          TextMapId = "UI_Skin_Des_3103"
        },
        UI_Skin_Des_3201 = {
          ContentJP = "背負った鎖と棘は罪を罰するためのものにとどまらず、心に宿る正義の炎を縛るものでもある。",
          TextMapId = "UI_Skin_Des_3201"
        },
        UI_Skin_Des_3301 = {
          ContentJP = "鍋から作られた多機能ヘルメットは、ゴミ拾いの少女の堅牢な甲冑だ。",
          TextMapId = "UI_Skin_Des_3301"
        },
        UI_Skin_Des_4101 = {
          ContentJP = "制服に散る青白い星光は守護の意思であり、未来への憧憬でもある。",
          TextMapId = "UI_Skin_Des_4101"
        },
        UI_Skin_Des_4102 = {
          ContentJP = "朝陽が金箔を溶かし、絢爛な錦に降り注ぐ。変革の意志と共に、その金色の瞳に燃え上がる。",
          TextMapId = "UI_Skin_Des_4102"
        },
        UI_Skin_Des_4201 = {
          ContentJP = "応天の雷は全身を駆け巡る。暴風の意志を貫き、罪人は逃げられず、彼自身も逃げられない。",
          TextMapId = "UI_Skin_Des_4201"
        },
        UI_Skin_Des_4202 = {
          ContentJP = "勲章に刻まれたのは天才の魂。軍服の下に隠されているのは凡人の心。",
          TextMapId = "UI_Skin_Des_4202"
        },
        UI_Skin_Des_4301 = {
          ContentJP = "孤高の者に鎧や冠は不要。その存在自体が威圧の轟きだからだ。",
          TextMapId = "UI_Skin_Des_4301"
        },
        UI_Skin_Des_5101 = {
          ContentJP = "地面に揺れる白い羽飾りは、麦芽の香りを運び、万家の灯と共に、飢えた者たちの心へ飛んでいく。",
          TextMapId = "UI_Skin_Des_5101"
        },
        UI_Skin_Des_510101 = {
          ContentJP = "ぽかぽか日差しと、丹念な磨き。惜しみなく振りまけ、すべては光のために！",
          TextMapId = "UI_Skin_Des_510101"
        },
        UI_Skin_Des_5102 = {
          ContentJP = "質素な衣は裏路地の赤い嗚咽を防げはしないが、荒野の中で小さな星明かりを守るには十分だ。",
          TextMapId = "UI_Skin_Des_5102"
        },
        UI_Skin_Des_5301 = {
          ContentJP = "少女の腕に憩う蝶は、いくつもの冬を越える夢を届けてくれる。",
          TextMapId = "UI_Skin_Des_5301"
        },
        UI_Skin_Des_530101 = {
          ContentJP = "真夏の夜、星降る森で――三色菫と蝶の羽が織り成すステップ。それは、彼女の見知らぬ景色だった。",
