        UI_WEAPON_DESC_10199 = {
          ContentJP = "ありふれた片手剣。安価な金属で作られているので、壊れても惜しくない。",
          TextMapId = "UI_WEAPON_DESC_10199"
        },
        UI_WEAPON_DESC_10201 = {
          ContentJP = "くちづけを、芯まで深く唇を重ねたいの。明日が来る前に、乙女の心が老いる前に、死がふたりを分かつ前に。",
          TextMapId = "UI_WEAPON_DESC_10201"
        },
        UI_WEAPON_DESC_10202 = {
          ContentJP = "星織人たちが故郷の華胥から携えた長柄武器。刃の造形と色は華胥に自生している「柳」という木に由来し、華胥においては「故郷」の意味合いを持つ。アトラシアのあらゆる土地で星空を編み出している星織人たちも、いつかは柳の生い茂る故郷に帰るのだ。",
          TextMapId = "UI_WEAPON_DESC_10202"
        },
        UI_WEAPON_DESC_10203 = {
          ContentJP = "日が昇り月が沈み、季節は巡り、人は老いる。",
          TextMapId = "UI_WEAPON_DESC_10203"
        },
        UI_WEAPON_DESC_10204 = {
          ContentJP = "私は凍土に種を蒔いた。いつ来るとも知れぬ収穫の日を、迎えられるよう祈って。",
          TextMapId = "UI_WEAPON_DESC_10204"
        },
        UI_WEAPON_DESC_10205 = {
          ContentJP = "エリュシオン礼賛会の聖アイリス騎士団が使用する鎌――聖なるかな！私は神の慈悲がこの世に輝きをもたらし、愚者たちの長きにわたる無知を打ち砕くのを見た。",
          TextMapId = "UI_WEAPON_DESC_10205"
        },
        UI_WEAPON_DESC_10206 = {
          ContentJP = "アトラシアの狩人が愛用する鎌。小柄な穢獣であれば一撃で仕留められる威力を持つ。",
          TextMapId = "UI_WEAPON_DESC_10206"
        },
        UI_WEAPON_DESC_10207 = {
          ContentJP = "青い水晶の中には、海の荒波が封じ込められている。敵の命乞いは不協和音。発せられる前に、この波音にかき消されるべきだ。",
          TextMapId = "UI_WEAPON_DESC_10207"
        },
        UI_WEAPON_DESC_10208 = {
          ContentJP = "平和を守るために戦う白い鳩は、その羽根一枚一枚を平和の証として捧げることを誓う。",
          TextMapId = "UI_WEAPON_DESC_10208"
        },
        UI_WEAPON_DESC_10209 = {
          ContentJP = "血に染まった刃先が心臓に狙いを定めているが、熱い溶岩が冷める前に、まだ逃げる時間はある。",
          TextMapId = "UI_WEAPON_DESC_10209"
        },
        UI_WEAPON_DESC_10298 = {
          ContentJP = "最新技術で仕上げられた長槍。堅実な衛兵たちに愛用されている。",
