        },
        voice_ch_char_NanzhuLight_vo_victory = {
          ContentJP = "また一歩、前へ進んだ。こうして一歩ずつ重ねていけば、いつかきっと......",
          TextMapId = "voice_ch_char_NanzhuLight_vo_victory"
