local CssCode = "@charset \"utf-8\";\n\n* {\n    margin-top:0.015rem;\n    margin-bottom:0.015rem;\n    letter-spacing: 0.015rem;\n    line-height:1.3;\n}\n\nbody{\n    transition: all 0.2s;\n    overflow-x: hidden;\n    overflow-y: hidden;\n    opacity:0;\n}\n\n.content-container {\n    display: flex;\n    width: 100vw;\n    height: 100%;\n}\n\n.right-space {\n    width: 3vw;\n    background: transparent;\n}\n\n.MainContent {\n    width: 97vw;\n    font-size:0.169216rem;\n    font-family: \"GameFont\";\n    color: #999999;\n    scroll-behavior: smooth;\n    user-select: none;\n    cursor: default;\n    position: relative;\n}\n\n.MainContent img {\n    width: 94vw;\n    height: 100%;\n    display:block;\n    margin-left:auto;\n    margin-right:auto;\n    object-fit: contain;\n    border-top-left-radius: 2.4vw;\n    border-bottom-right-radius: 2.4vw;\n    justify-content: center;\n    -webkit-user-drag: none;\n    -moz-user-drag: none;\n    -ms-user-drag: none;\n    margin-top:0.05rem;\n    margin-bottom:0.05rem;\n}\n.MainContent a {\n    color:#8ab3f5;\n}\n.MainContent ul {\n    padding-inline-start: 4vw;\n    margin:1vw;\n}\n.MainContent ol {\n    padding-inline-start: 4vw;\n    margin:1vw;\n}\n.MainContent p {\n    margin: 1vw;\n}\n.MainContent h3{\n    margin: 1vw;\n}\n\n.before {\n    content: \"\";\n    position: fixed;\n    top: 0;\n    width: 100%;\n    height: 3vw;\n    background-image: linear-gradient(180deg, rgba(21,21,21,0.9) 0%, rgba(255, 255, 255, 0) 100%);\n    pointer-events: none;\n    user-select: none;\n    margin-top:0rem;\n    margin-bottom:0rem;\n}\n\n.after {\n    content: \"\";\n    position: fixed;\n    bottom: 0;\n    width: 100%;\n    height: 3vw;\n    background-image: linear-gradient(180deg, rgba(255, 255, 255, 0) 0%, rgba(21,21,21,0.9) 100%);\n    pointer-events: none;\n    user-select: none;\n    margin-top:0rem;\n    margin-bottom:0rem;\n}\n\n\n::-webkit-scrollbar-thumb {\n    margin-top:3vw;\n    /*滚动条里面小方块*/\n    border-top-left-radius: 6px;\n    border-bottom-right-radius: 6px;\n    box-shadow : inset 0 0 1px rgba(0, 0, 0, 0.2);\n    background : #7C736E;\n    margin-bottom:1.6vw;\n}\n::-webkit-scrollbar-track {\n    margin-top:3vw;\n    /*滚动条里面轨道*/\n    box-shadow : inset 0 0 1px rgba(0, 0, 0, 0.2);\n    border-top-left-radius: 6px;\n    border-bottom-right-radius: 6px;\n    background : #292929;\n    margin-bottom:1.6vw;\n}\n\n.TitleBar{\n    object-fit: contain;\n    border-top-width: 1vw ;\n    border-bottom-width: 1vw ;\n    border-right-width: 3vw ;\n    border-left-width: 3vw ;\n    border-style: solid;\n    border-image-source: url(\"../Image/TitleBg.png\");\n    border-image-slice: 15 15 15 15 fill;\n    border-image-width: 2.5vw;\n    font-size: 0.27074992rem;\n    color: #ffffff;\n    margin-bottom:0.05rem;\n}\n"
return CssCode
