        Content_110101_1 = {
          ContentFR = "Une voix vous appelle, lointaine et voilée, quelque part au-delà du sable...",
          TextMapId = "Content_110101_1"
        },
        Content_110101_2 = {
          ContentFR = "Ne vous laissez pas tromper par cette voix étrangère. Avancez — quittez ce cauchemar.",
          TextMapId = "Content_110101_2"
        },
        Content_110102_1 = {
          ContentFR = "Vous sortez en sursaut du cauchemar — face à vous, les crocs acérés d’un Souillé.",
          TextMapId = "Content_110102_1"
        },
        Content_110102_2 = {
          ContentFR = "Votre escouade a été prise d’assaut par de puissants Souillés. Vainquez-les et rejoignez vos compagnons.",
          TextMapId = "Content_110102_2"
        },
        Content_110103_1 = {
          ContentFR = "Vous avez éliminé les Souillés apparus soudainement, rétablissant un court répit. Rejoignez le poste de secours pour retrouver votre unité.",
          TextMapId = "Content_110103_1"
        },
        Content_110103_2 = {
          ContentFR = "Votre escouade a subi de lourdes pertes. L’infirmière Daphne vous demande de soutenir les survivants tandis qu’elle s’apprête à chercher des fournitures médicales dans l’entrepôt.\nPar mesure de sécurité, vous décidez d’y aller à sa place.",
          TextMapId = "Content_110103_2"
        },
        Content_110103_3 = {
          ContentFR = "Une horde de Souillés déferle depuis le couloir. Les blessés sont incapables de se battre : en tant que capitaine, vous êtes leur dernier rempart.\nTenez la ligne et protégez-les.",
          TextMapId = "Content_110103_3"
        },
        Content_110103_3_2 = {
          ContentFR = "Avar arrive en renfort et renverse le cours du combat. Profitez de cette occasion pour vaincre le chef des Souillés et mettre fin à la bataille !",
          TextMapId = "Content_110103_3_2"
        },
        Content_110103_4 = {
          ContentFR = "Vous et Avar avez repoussé les Souillés. Revenez au poste de secours pour planifier la suite des opérations.",
          TextMapId = "Content_110103_4"
        },
        Content_110104_1 = {
          ContentFR = "Avar ordonne à Daphne et aux blessés de rester en arrière, sous la protection de l’escouade des Loups Blancs.\nQuant à vous, vous l’accompagnez vers l’intérieur de la forteresse pour poursuivre la mission.",
          TextMapId = "Content_110104_1"
        },
        Content_110104_2 = {
          ContentFR = "Après avoir analysé la situation, Avar conclut que les Déchus ont anticipé les mouvements de l’Empire.\nPour renverser la situation, il décide de saboter le réservoir de Phoxichor dans la salle des machines afin de désactiver les mécanismes de la forteresse et faciliter le sauvetage des soldats piégés.",
          TextMapId = "Content_110104_2"
        },
        Content_110104_3 = {
          ContentFR = "Après avoir analysé la situation, Avar conclut que les Déchus ont anticipé les mouvements de l’Empire.\nPour renverser la situation, il décide de saboter le réservoir de Phoxichor dans la salle des machines afin de désactiver les mécanismes de la forteresse et faciliter le sauvetage des soldats piégés.",
          TextMapId = "Content_110104_3"
        },
        Content_110105_1 = {
          ContentFR = "Vous retrouvez deux membres de l’escouade des Loups Blancs mais l’un d’eux, Vic, est piégé dans la salle des machines. Coopérez avec Avar pour couper l’alimentation et secourir votre camarade.",
          TextMapId = "Content_110105_1"
        },
        Content_110105_2 = {
          ContentFR = "Vous avez atteint l’intérieur de la salle des machines. Coupez l’alimentation.",
          TextMapId = "Content_110105_2"
        },
        Content_110106_1 = {
          ContentFR = "Vic est tombé au combat. Le champ de bataille est impitoyable, mais la mission doit se poursuivre. Avec Avar, désactivez les dispositifs de la salle des machines, tout en restant vigilant face aux Souillés qui rôdent.",
          TextMapId = "Content_110106_1"
        },
        Content_110106_2 = {
          ContentFR = "Vous avez détruit le noyau énergétique, mais cela a déclenché la séquence d’autodestruction du réservoir de Phoxichor. Le compte à rebours est lancé — fuyez la salle des machines avant qu’il ne soit trop tard !",
          TextMapId = "Content_110106_2"
        },
        Content_110106_3 = {
          ContentFR = "Le commandant Fulvis ordonne de poursuivre l’assaut, mais au vu de la situation, une attaque frontale serait suicidaire.\nFaites confiance au jugement d’Avar : partez d’abord secourir vos alliés de l’escouade des Griffons.",
          TextMapId = "Content_110106_3"
        },
        Content_110106_4 = {
          ContentFR = "Vous retrouvez Simon, encerclé par des Souillés. Éliminez-les avant qu’il ne soit trop tard.",
          TextMapId = "Content_110106_4"
        },
        Content_110106_5 = {
          ContentFR = "Vous trouvez Simon encerclé par les Souillés — son état semble critique.",
          TextMapId = "Content_110106_5"
        },
        Content_110107_1 = {
          ContentFR = "Vous arrivez trop tard — Simon n’a pas survécu. Ses derniers mots vous révèlent que d’autres camarades sont encore piégés dans la zone ferroviaire.\nAvancez, sauvez ceux qu’il reste à sauver.",
          TextMapId = "Content_110107_1"
        },
        Content_110107_2 = {
          ContentFR = "Des Souillés surgissent sur la route ! Éliminez-les rapidement, ne perdez pas de temps.",
          TextMapId = "Content_110107_2"
        },
        Content_110107_3 = {
          ContentFR = "Un bref contact radio avec Daphne vous rassure un instant. Continuez d’avancer.",
          TextMapId = "Content_110107_3"
        },
        Content_110108_1 = {
          ContentFR = "La voix de Daphne au communicateur vous redonne courage. Avancez sans faiblir.",
          TextMapId = "Content_110108_1"
        },
        Content_110108_2 = {
          ContentFR = "Vous entendez vos coéquipiers vous appeler, mais les Souillés bloquent la voie.\nÉliminez-les pour pouvoir les rejoindre au plus vite.",
          TextMapId = "Content_110108_2"
        },
        Content_110108_3 = {
          ContentFR = "Les Souillés sont tombés. Rejoignez vos coéquipiers sans tarder.",
          TextMapId = "Content_110108_3"
        },
        Content_110108_4 = {
          ContentFR = "Votre équipe rencontre deux Charons.\nLa jeune femme affirme qu’ils ne sont que de simples villageois réduits en esclavage par les Déchus, tandis que l’aîné vous propose un raccourci.\nBien que vous ne puissiez vérifier leurs dires, vous décidez de suivre les indications de l’homme avec prudence, espérant rejoindre Avar au plus vite.",
          TextMapId = "Content_110108_4"
        },
        Content_110109_2 = {
          ContentFR = "La tourelle se met en mouvement sur les rails. Protégez-la aux côtés d’Avar contre les Souillés jusqu’à ce qu’elle atteigne sa position.",
          TextMapId = "Content_110109_2"
        },
        Content_110109_2_1 = {
          ContentFR = "Pour ouvrir la porte de la salle de commande centrale, vous devez déplacer la tourelle. Activez le mécanisme pour la faire glisser le long des rails.",
          TextMapId = "Content_110109_2_1"
        },
        Content_110110_1 = {
          ContentFR = "Utilisez la tourelle pour ouvrir la voie vers la salle de commande centrale.",
          TextMapId = "Content_110110_1"
        },
        Content_110110_2 = {
          ContentFR = "Après le bombardement, la porte secondaire de la salle de commande centrale s’ouvre. Rejoignez Avar pour enquêter.",
          TextMapId = "Content_110110_2"
        },
        Content_110110_3 = {
          ContentFR = "Katya tente soudain de vous poignarder dans le dos, mais Avar réagit à temps et déjoue sa manœuvre. Discutez avec lui de la situation.",
          TextMapId = "Content_110110_3"
        },
        Content_110110_4 = {
          ContentFR = "Vous marquez une courte pause pour souffler.\nPendant qu’Avar se repose, interrogez Katya pour découvrir les raisons de son attaque. Il semblerait aussi qu’Avar ait perdu une photo durant la lutte — peut-être pourriez-vous la retrouver.",
          TextMapId = "Content_110110_4"
        },
        Content_110110_5 = {
          ContentFR = "Vous interrogez Katya, mais elle refuse obstinément de dire la vérité.\nSes provocations font résonner cette voix familière dans votre tête... Vous peinez à garder le contrôle.\nAllez retrouver Avar pour décider de la suite.",
          TextMapId = "Content_110110_5"
        },
        Content_110110_6 = {
          ContentFR = "Avar comptait affronter seul le danger dans la salle de commande, mais vos camarades refusent de le laisser partir seul.\nSous votre insistance, il accepte finalement d’y aller avec vous.",
          TextMapId = "Content_110110_6"
        },
        Content_110110_7 = {
          ContentFR = "Sur le chemin, un Souillé d’une puissance inouïe, surnommé le Maître de guerre, vous barre la route.\nFaites confiance à votre mentor — ensemble, vous le vaincrez.",
          TextMapId = "Content_110110_7"
        },
        Content_110110_8 = {
          ContentFR = "Le monstre est tombé. Avancez vers la salle de commande centrale.",
          TextMapId = "Content_110110_8"
        },
        Content_110111_1 = {
          ContentFR = "Vous atteignez la salle de commande centrale — vide. Ni ennemis, ni pièges.\nInspectez les lieux et cherchez des indices.",
          TextMapId = "Content_110111_1"
        },
        Content_110111_2 = {
          ContentFR = "Avar lève soudain son arme dans votre direction.",
          TextMapId = "Content_110111_2"
        },
        Content_110111_3 = {
          ContentFR = "Vous reprenez connaissance. Vous êtes {性别:vivant|vivante}.\nExaminez les alentours et tentez de comprendre ce qui s’est passé.",
          TextMapId = "Content_110111_3"
        },
        Content_110111_4 = {
          ContentFR = "Avar... ne respire plus.\nÀ la radio, la Légion annonce la mise en accusation de Daphne pour espionnage — une accusation que vous savez fausse.\nFuyez la salle de contrôle et retrouvez-la au plus vite.",
          TextMapId = "Content_110111_4"
        },
        Content_110111_5 = {
          ContentFR = "Vous retrouvez Daphne — mais, étrangement, elle vous met en joue.",
          TextMapId = "Content_110111_5"
        },
        Content_110111_6 = {
          ContentFR = "Vous avez retrouvé Daphne. Malgré la panique, elle garde son calme et agit en professionnelle. La forteresse n’est plus sûre.\nÉvadez-vous avec elle par les égouts.",
          TextMapId = "Content_110111_6"
        },
        Content_110111_7 = {
          ContentFR = "Récupérer la clé",
          TextMapId = "Content_110111_7"
        },
        Content_110112_1 = {
          ContentFR = "Le réseau d’égouts est un labyrinthe. Progressez prudemment et trouvez une issue.",
          TextMapId = "Content_110112_1"
        },
        Content_110112_2 = {
          ContentFR = "L’ennemi tente de noyer les tunnels pour vous piéger. Activez les systèmes d’évacuation et continuez vers la sortie.",
          TextMapId = "Content_110112_2"
        },
        Content_110113_1 = {
          ContentFR = "À la Forteresse de Lonza, vous et Daphne cherchez un espoir de survie en approchant la Commandante Sibylle. Mais vous êtes bientôt arrêtés et transférés à la Prison Militaire Royale d’Hyperborea, accusés d’espionnage.\nPour protéger Daphne, et pour que la justice défendue par Avar ne s’éteigne pas, vous devez convaincre la Geôlière de votre innocence.",
          TextMapId = "Content_110113_1"
        },
        Content_110113_2 = {
          ContentFR = "La Geôlière vous administre un sédatif étrange. Le monde se brouille... Vous vous réveillez dans un espace irréel, semblable à la Forteresse de Lonza.\nL’interrogatoire ne fait que commencer.",
          TextMapId = "Content_110113_2"
        },
        Content_110113_3 = {
          ContentFR = "Affrontez les flammes de la Geôlière et prouvez votre innocence.",
          TextMapId = "Content_110113_3"
        },
        Content_110113_4 = {
          ContentFR = "Vous racontez à la Geôlière ce qui s’est réellement passé dans la salle de commande : Avar a feint sa trahison pour protéger les preuves, au prix de sa vie. Mais ces preuves ont été emportées par Falsi, l’adjudante de Fulvis.\nConvaincu par votre récit, la Geôlière accepte d’enquêter plus avant.",
          TextMapId = "Content_110113_4"
        },
        Content_120101_1 = {
          ContentFR = "Après une longue traversée, vous accostez enfin sur les rivages de Huaxu.\nLe paysage paisible qui s’étend à perte de vue vous rappelle que vous êtes bien loin de chez vous.\nDiscutez avec Zhiliu pour en apprendre davantage sur ce royaume.",
          TextMapId = "Content_120101_1"
        },
        Content_120101_10 = {
          ContentFR = "Après avoir découvert les Neuf Chapitres, vous avez franchi la première étape de votre intégration à Huaxu.\nSuivez Luno, la carpe, jusqu’à la guilde marchande Éternel Printemps pour retrouver Zhiliu.",
          TextMapId = "Content_120101_10"
        },
        Content_120101_11 = {
          ContentFR = "En route, le sol se met soudain à trembler. Un Exécuteur rencontré plus tôt vous attaque sans raison apparente. Sa silhouette, altérée, diffère de celle des Bi’an ordinaires. Grâce à l’aide d’une marchande itinérante, vous parvenez à le neutraliser.\nDiscutez avec elle pour comprendre ce qu’il s’est passé.",
          TextMapId = "Content_120101_11"
        },
        Content_120101_12 = {
          ContentFR = "D’après Yusheng, la protection qui vous a {性别:sauvé|sauvée} serait liée à une substance appelée Encens, que l’on peut raviver au Temple du Dragon d’Argent, près d’Éternel Printemps.\nRejoignez Luno, la carpe, et rendez-vous à la guilde pour retrouver Zhiliu.",
          TextMapId = "Content_120101_12"
        },
        Content_120101_2 = {
          ContentFR = "À peine le navire a-t-il accosté qu’une voix appelle Zhiliu depuis le quai.\nDescendez pour voir de quoi il s’agit.",
          TextMapId = "Content_120101_2"
        },
        Content_120101_3 = {
          ContentFR = "À Huaxu, tout semble nouveau, étrangement vivant. Les quais résonnent de cris de marchands et de voyageurs.\nPrenez un moment pour explorer les environs avant de poursuivre.",
          TextMapId = "Content_120101_3"
        },
        Content_120101_4 = {
          ContentFR = "À Huaxu, tout semble nouveau, étrangement vivant. Les quais résonnent de cris de marchands et de voyageurs.\nPrenez un moment pour explorer les environs avant de poursuivre.",
          TextMapId = "Content_120101_4"
        },
        Content_120101_5 = {
          ContentFR = "Snow a disparu l’espace d’un instant — partez vite à sa recherche !",
          TextMapId = "Content_120101_5"
        },
        Content_120101_6 = {
          ContentFR = "Vous arrivez juste à temps pour interrompre les vantardises de Snow devant Luno, la carpe.\nLe Roi Démon s’est visiblement trouvé une admiratrice, et son ego frôle déjà les nuages. Rattrapez-la avant qu’elle ne s’envole et rejoignez Zhiliu.",
          TextMapId = "Content_120101_6"
        },
        Content_120101_7 = {
          ContentFR = "Alors que tout semblait paisible à Huaxu, un tumulte éclate sur les quais.\nUne tentative de vol, vite réprimée par l’apparition soudaine d’un Bi’an, bête divine des vents. Zhiliu vous explique que le royaume vit sous la protection des Neuf Chapitres de Dafeng, où le crime est depuis longtemps éradiqué.\nMais à peine a-t-elle fini qu’un de ses agents accourt, essoufflé.\nPourquoi un criminel se risquerait-il ici ? Allez le découvrir.",
          TextMapId = "Content_120101_7"
        },
        Content_120101_8 = {
          ContentFR = "Rappelée à ses obligations, Zhiliu doit partir précipitamment.\nAvant de s’éloigner, elle vous avertit : quiconque entre dans le royaume de Huaxu doit respecter les Neuf Chapitres de Dafeng.\nPour faire preuve de civisme, suivez Luno, la carpe, jusqu’à la stèle des préceptes.",
          TextMapId = "Content_120101_8"
        },
        Content_120101_9 = {
          ContentFR = "Tandis que Luno, la carpe, vous expose les Neuf Chapitres de Dafeng, une vibration subtile résonne dans votre esprit... comme si la stèle cherchait à vous parler.",
          TextMapId = "Content_120101_9"
        },
        Content_200101_1 = {
          ContentFR = "Une silhouette familière vous attend devant le Sanctuaire, elle ressemble étrangement à Snow.",
          TextMapId = "Content_200101_1"
        },
        Content_200101_2 = {
          ContentFR = "Vous entendez quelqu’un vous appeler. Allez voir de qui il s’agit.",
          TextMapId = "Content_200101_2"
        },
        Content_200101_3 = {
          ContentFR = "Celle qui vous a interpellé se présente comme une forgeronne de la race des Luno.\nSelon Snow, il s’agirait d’une ancienne, bien que tous les Luno aient la même apparence juvénile, quel que soit leur âge.\nEn échange de ses talents, Luno, la forgeronne souhaite vous accompagner dans votre voyage.\nRamenez-la au Sanctuaire.",
          TextMapId = "Content_200101_3"
        },
        Content_200101_4 = {
          ContentFR = "Luno, la forgeronne ne cache pas sa fierté quant à ses talents.\nVoyez donc de quoi elle est capable.",
          TextMapId = "Content_200101_4"
        },
        Content_200102_1 = {
          ContentFR = "Se rendre à la Maison d’Hypnos pour prendre des nouvelles de Psyche, comme convenu.",
          TextMapId = "Content_200102_1"
        },
        Content_200102_2 = {
          ContentFR = "Les fleurs que Psyche a si tendrement cultivées se fanent peu à peu, comme privées de nutriments. Pour leur rendre vie, il faut rapporter une terre spéciale que l’on trouve dans une grotte, à l’extérieur de Givrelac.\nPour un Chasseur de Phoxène, une telle tâche n’est qu’une formalité.",
          TextMapId = "Content_200102_2"
        },
        Content_200102_3 = {
          ContentFR = "Selon Psyche, cette terre si particulière pousse dans une grotte, non loin du Lac du Deuil.\nPartez en quête de ce sol fertile pour elle.",
          TextMapId = "Content_200102_3"
        },
        Content_200102_4 = {
          ContentFR = "La terre à l’entrée de la grotte est trop sèche pour nourrir les fleurs.\nExplorez plus en profondeur afin de trouver une terre plus adaptée.",
          TextMapId = "Content_200102_4"
        },
        Content_200102_5 = {
          ContentFR = "Après bien des efforts, vous découvrez enfin, au cœur de la grotte, une terre souple et fertile.\nRapportez cette précieuse terre à la Maison d’Hypnos — Psyche en sera sûrement ravie.",
          TextMapId = "Content_200102_5"
        },
        Content_200201_1 = {
          ContentFR = "Près de l’entrée secondaire de Givrelac vit Sally, une vieille dame au cœur généreux.\nChaque passage à proximité s’accompagne du parfum onctueux de sa crème de champignons.\nMais depuis quelque temps, son regard semble plus lourd — allez donc lui parler.",
          TextMapId = "Content_200201_1"
        },
        Content_200201_2 = {
          ContentFR = "Sally paraît tourmentée à propos d’une certaine dame dont elle s’inquiète.\nSi vous croisez cette femme, peut-être pourrez-vous lui venir en aide.",
          TextMapId = "Content_200201_2"
        },
        Content_200201_3 = {
          ContentFR = "Dame Hilin attend, depuis de longues années, un Charon nommé Hamel Dickinson.\nLa solitude et l’inquiétude pèsent sur elle. Aidez-la à retrouver la trace de cet homme disparu.",
          TextMapId = "Content_200201_3"
        },
        Content_200201_4 = {
          ContentFR = "Pénétrez dans les profondeurs de la Fosse pour retrouver des traces de Hamel Dickinson.",
          TextMapId = "Content_200201_4"
        },
        Content_200201_5 = {
          ContentFR = "Dans les profondeurs de la fosse, vous découvrez un vieux journal sans titre.\nSur la page de garde est inscrit un nom : Hamel Dickinson.\nLe journal semble retracer certains événements liés à cet homme.\nIl est temps de retourner à Givrelac et de remettre ce carnet à Dame Hilin.",
          TextMapId = "Content_200201_5"
        },
        Content_200201_6 = {
          ContentFR = "Après avoir lu le journal, Dame Hilin souhaite rester seule un moment.\nAllez voir sa domestique, Sally, pour recevoir votre récompense et en apprendre davantage sur cette vieille histoire.",
          TextMapId = "Content_200201_6"
        },
        Content_200202_1 = {
          ContentFR = "Près des portes de la cité, un homme interpelle les passants — il semble vendre quelque chose.",
          TextMapId = "Content_200202_1"
        },
        Content_200202_2 = {
          ContentFR = "Martin, qui se présente comme le \"Roi des Récupérateurs\" de Givrelac, vous a confié quelques objets perdus. Il vous demande d’en retrouver les propriétaires.",
          TextMapId = "Content_200202_2"
        },
        Content_200202_3 = {
          ContentFR = "Des étoiles en papier pliées à partir de billets périmés... Peut-être s’agit-il des effets personnels de cette vendeuse de billets.\nElle paraît toutefois étrangement... courtoise.",
          TextMapId = "Content_200202_3"
        },
        Content_200203_1 = {
          ContentFR = "La voix de la femme était rauque et brisée, sa tenue pourtant soignée couverte de poussière.\nEncore et encore, elle arrêtait les passants, cherchant désespérément quelque chose...",
          TextMapId = "Content_200203_1"
        }
      }
    end
  },
  {
    MinKey = "Content_200203_12",
    MaxKey = "DUNGEON_NAME_80705",
    Loader = function()
      return {
        Content_200203_12 = {
          ContentFR = "Depuis que vous avez quitté cette femme éplorée, vous sentez un regard peser sur vous. Il semblerait qu’elle ait encore quelque chose à dire.",
          TextMapId = "Content_200203_12"
        },
        Content_200203_2 = {
          ContentFR = "La fille unique de Madame Elsa, Annie, a disparu.\nEn parcourant Givrelac, gardez l’œil ouvert : quelqu’un pourrait bien ressembler à Annie.",
          TextMapId = "Content_200203_2"
        },
        Content_200203_3 = {
          ContentFR = "Essayez de fouiller à l’extérieur de la ville, le docteur n’a sans doute pas encore quitté les environs. Avec un peu de chance, vous la retrouverez.",
          TextMapId = "Content_200203_3"
        },
        Content_200203_4 = {
          ContentFR = "Annie a tragiquement perdu la vie, tandis qu’Alice, la jeune fille qui avait revêtu ses vêtements, a survécu.\nBien qu’elle ait échappé à la mort dans les montagnes enneigées, la fièvre prolongée a effacé sa mémoire.\nÀ présent que la vérité est révélée, il ne reste plus qu’à la transmettre à celle qui doit l’entendre.",
          TextMapId = "Content_200203_4"
        },
        Content_200203_5 = {
          ContentFR = "Alice a retrouvé la mémoire.\nIl est temps d’annoncer à Madame Elsa la mort d’Annie...",
          TextMapId = "Content_200203_5"
        },
        Content_200204_1 = {
          ContentFR = "Vous avez reçu une invitation de Wells, un Chasseur de Phoxène.\nIl souhaite vous rencontrer aux Asphodèles.",
          TextMapId = "Content_200204_1"
        },
        Content_200204_2 = {
          ContentFR = "Wells, le Chasseur de Phoxène, a remarqué votre talent et souhaite accomplir une mission à vos côtés.",
          TextMapId = "Content_200204_2"
        },
        Content_200204_3 = {
          ContentFR = "Vous avez accepté l’invitation de Wells.\nPréparez-vous et partez avec lui pour cette mission.",
          TextMapId = "Content_200204_3"
        },
        Content_200204_4 = {
          ContentFR = "Wells semble en difficulté...\nTrouvez un moyen de le protéger de la colère de son beau-père.",
          TextMapId = "Content_200204_4"
        },
        Content_200206_1 = {
          ContentFR = "Comme à votre habitude, vous vous rendez aux Asphodèles pour consulter les nouvelles missions. Mais aujourd’hui, l’endroit semble plus animé qu’à l’ordinaire — une agitation inhabituelle flotte dans l’air.",
          TextMapId = "Content_200206_1"
        },
        Content_200206_2 = {
          ContentFR = "Un client surexcité s’est hissé sur une table, débitant un flot de paroles incompréhensibles — sans pour autant sentir l’alcool.\nDiscutez avec Camilla pour en savoir plus sur cet étrange personnage.",
          TextMapId = "Content_200206_2"
        },
        Content_200206_3 = {
          ContentFR = "Le dramaturge Buck manie les figures de style avec une éloquence certaine, comme pour rappeler à chacun son talent littéraire.\nPourtant, à cet instant, ce dont il a le plus besoin, c’est d’un traducteur plutôt que d’un public.\nTendez l’oreille, et essayez de comprendre ce qu’il tente d’exprimer.",
          TextMapId = "Content_200206_3"
        },
        Content_200206_8 = {
          ContentFR = "Vous découvrez enfin pourquoi Arnaud s’est introduit en secret, au cœur de la nuit, dans la demeure de Buck.\nMais seul le dramaturge peut décider de la suite à donner à cette affaire.\nRamenez Arnaud et vos conclusions aux Asphodèles.",
          TextMapId = "Content_200206_8"
        },
        Content_200207_2 = {
          ContentFR = "Emma, une étudiante venue déposer des fleurs au monument, semble ailleurs, perdue dans ses pensées.\nTandis qu’elle se tient seule, saisissez ce moment pour lui demander ce qui la préoccupe.",
          TextMapId = "Content_200207_2"
        },
        Content_200207_5 = {
          ContentFR = "Belinda remarque que vous avez perdu quelqu’un de cher et murmure que vos chemins se recroiseront un jour.\nElle souhaite vous offrir un présent, mais il lui faut un fil de fer imprégné de votre essence. Cherchez-en un dans la Fosse de Glevum.",
          TextMapId = "Content_200207_5"
        },
        Content_200207_6 = {
          ContentFR = "Vous avez repris le fil de fer volé par Lulu, pilleuse de loot.\nRetournez à Givrelac et remettez-le à Belinda — voyons quel présent elle compte vous offrir.",
          TextMapId = "Content_200207_6"
        },
        Content_200207_7 = {
          ContentFR = "Alors que vous récupériez le fil demandé par Belinda, Lulu, pilleuse de loot a surgi de nulle part pour vous le dérober. Peu importe les raisons d’une médium pour convoiter un objet marqué de votre essence : vous n’allez pas laisser passer ça. Rattrapez-la et donnez-lui une bonne leçon.",
          TextMapId = "Content_200207_7"
        },
        Content_200210_1 = {
          ContentFR = "Vous avez réussi à pénétrer dans le marché noir. Un marchand au regard mauvais attire votre attention. Allez lui parler pour en savoir plus.",
          TextMapId = "Content_200210_1"
        },
        Content_200210_2 = {
          ContentFR = "Suivez les instructions du contrebandier Harlowe jusqu’au lieu convenu, près du Lac du Deuil, afin d’accomplir la mission.",
          TextMapId = "Content_200210_2"
        },
        Content_200210_3 = {
          ContentFR = "La zone est envahie par des Souillés. Éliminez-les.",
          TextMapId = "Content_200210_3"
        },
        Content_200210_4 = {
          ContentFR = "Vous avez enfin purgé la zone de toute présence des Souillés.\nIl est temps d’aller réclamer votre juste récompense auprès de Harlowe.",
          TextMapId = "Content_200210_4"
        },
        Content_200210_7 = {
          ContentFR = "Des rumeurs évoquent l’existence d’un marché noir secret dans les Égouts de Givrelac, où se dérouleraient d’obscures transactions.\nEn tant que Chasseur de Phoxène vigilant et aguerri, il est de votre devoir d’aller y voir de plus près.",
          TextMapId = "Content_200210_7"
        },
        Content_200211_1 = {
          ContentFR = "Une jeune Sola semble persuadée d’être une Charon. Allez voir de quoi il retourne à Givrelac.",
          TextMapId = "Content_200211_1"
        },
        Content_200211_2 = {
          ContentFR = "Faye, humiliée par ses camarades, s’est enfuie en pleurant, les mains sur les oreilles. Retrouvez la fillette et tentez de la réconforter.",
          TextMapId = "Content_200211_2"
        },
        Content_200211_3 = {
          ContentFR = "Après avoir échangé quelques mots avec Faye, vous découvrez comment ses camarades la maltraitent. Il est temps de lui apprendre à se défendre : allez donner une leçon à ces élèves cruels.",
          TextMapId = "Content_200211_3"
        },
        Content_200211_4 = {
          ContentFR = "Après avoir échangé quelques mots avec Faye, vous découvrez comment ses camarades la maltraitent. Il est temps de lui apprendre à se défendre : allez donner une leçon à ces élèves cruels.",
          TextMapId = "Content_200211_4"
        },
        Content_200211_5 = {
          ContentFR = "Grâce à votre aide, Faye a enfin trouvé le courage de répondre à ses harceleurs.\nFurieux, ils ont rameuté des complices pour en découdre — en vain.\nAprès avoir dispersé cette bande de lâches, retournez voir Faye pour prendre de ses nouvelles.",
          TextMapId = "Content_200211_5"
        },
        Content_200212_3 = {
          ContentFR = "Comme prévu, Kevin a lui aussi reçu un rapport au sujet de cette affaire.\nEn {性别:citoyen dévoué|citoyenne dévouée} de Givrelac, vous acceptez de l’accompagner jusqu’à l’endroit où se trouvait Larry afin d’enquêter.",
          TextMapId = "Content_200212_3"
        },
        Content_200212_4 = {
          ContentFR = "À la vue de Kevin et de vous, Larry, membre des Veilleurs, perd ses moyens et attaque sous le coup de la panique. Vainquez-le pour calmer la situation.",
          TextMapId = "Content_200212_4"
        },
        Content_200212_5 = {
          ContentFR = "Vous parvenez à maîtriser Larry, qui retrouve enfin son calme.\nDiscutez avec lui, aux côtés de Kevin, pour découvrir le fin mot de l’histoire.",
          TextMapId = "Content_200212_5"
        },
        Content_200213_1 = {
          ContentFR = "Vous séjournez à Givrelac depuis un certain temps. Malgré le froid mordant, Margie continue d’y apporter chaleur et espoir, distribuant vivres et vêtements aux plus démunis.\nDepuis la chasse au voleur menée à ses côtés dans les égouts, vous n’avez guère eu l’occasion de lui reparler.\nAllez donc lui donner de vos nouvelles.",
          TextMapId = "Content_200213_1"
        },
        Content_200213_2 = {
          ContentFR = "Comme l’a dit Margie, les choses les plus dérisoires cachent parfois la plus grande valeur. Même les poubelles de la ville semblent abriter des récits oubliés.\nLes trois objets confiés par Margie — une ordonnance, une boîte d’étoiles en papier et une clé — sont autant de balises pointant chacune vers sa propre histoire.\nSuivez ces indices et découvrez les secrets qu’ils renferment !",
          TextMapId = "Content_200213_2"
        },
        Content_200213_3 = {
          ContentFR = "Après avoir levé le voile sur les mystères de ces trois objets, votre regard sur Givrelac a changé.\nRetournez voir Margie dans le quartier des taudis.",
          TextMapId = "Content_200213_3"
        },
        Content_200213_4 = {
          ContentFR = "Vous avez percé le secret des trois objets. Allez raconter vos découvertes à Margie.",
          TextMapId = "Content_200213_4"
        },
        Content_200215_2 = {
          ContentFR = "Pêchez une Phoxénotruite pour Marcus.\nCette fois, il recherche une saveur bien précise — espérons que sa recette soit enfin un succès !",
          TextMapId = "Content_200215_2"
        },
        Content_200215_3 = {
          ContentFR = "Vous avez enfin réuni les ingrédients nécessaires à la marinade. Apportez-les à Marcus.",
          TextMapId = "Content_200215_3"
        },
        Content_200215_4 = {
          ContentFR = "Bien que sa cuisine soit toujours difficile à avaler, vous avez accepté de devenir son partenaire culinaire.\nVoyons quelle nouvelle création il mijote cette fois.",
          TextMapId = "Content_200215_4"
        },
        Content_200215_5 = {
          ContentFR = "Excellent travail ! Personne ne manie la canne à pêche mieux que vous.\nApportez donc la Phoxénotruite fraîchement pêchée à Marcus.",
          TextMapId = "Content_200215_5"
        },
        Content_200215_6 = {
          ContentFR = "Excellent travail ! Personne ne manie la canne à pêche mieux que vous.\nApportez donc la Phoxénotruite fraîchement pêchée à Marcus.",
          TextMapId = "Content_200215_6"
        },
        Content_200215_7 = {
          ContentFR = "Cela fait un moment que vous n’avez pas revu Marcus.\nVous vous demandez comment se porte ce cuisinier aussi courageux qu’obstiné. On dit qu’il traîne souvent près des portes de Givrelac — allez donc prendre de ses nouvelles.",
          TextMapId = "Content_200215_7"
        },
        Content_200215_8 = {
          ContentFR = "Marcus est assis sur un banc près des portes de Givrelac. Allez lui parler.",
          TextMapId = "Content_200215_8"
        },
        Content_200216_1 = {
          ContentFR = "On raconte qu’un coffre suspect se trouve à l’extérieur de Givrelac.\nAllez voir de quoi il retourne.",
          TextMapId = "Content_200216_1"
        },
        Content_200216_2 = {
          ContentFR = "Vous trouvez bien un coffre au bord de la route — à première vue, rien de particulier. Autant l’ouvrir pour en avoir le cœur net.",
          TextMapId = "Content_200216_2"
        },
        Content_200216_3 = {
          ContentFR = "Alors que vous vous apprêtez à l’ouvrir, trois hommes surgissent du coffre, se proclamant « le trio de l’Empire » et revendiquant le butin.\nImpossible de discuter : il va falloir régler ça autrement.",
          TextMapId = "Content_200216_3"
        },
        Content_200216_4 = {
          ContentFR = "Après avoir calmé le trio de l’Empire, il semble qu’ils aient quelque chose à vous dire.",
          TextMapId = "Content_200216_4"
        },
        Content_200217_1 = {
          ContentFR = "Dean, le marchand de spiritueux posté à l’extérieur des Asphodèles, semble avoir quelques griefs contre Camilla. Allez découvrir de quoi il retourne.",
          TextMapId = "Content_200217_1"
        },
        Content_200218_1 = {
          ContentFR = "Vous voilà de retour en ce lieu silencieux.\nMême aujourd’hui, vous croyez encore entendre les dernières paroles tremblantes de Psyche flotter dans l’air glacé.\nUne femme mystérieuse murmure une prière — une psalmodie douce et funèbre, pareille à la compassion d’une mère divine.\nEt dans ce murmure, l’âme endeuillée qui hante ces falaises semble enfin trouver la paix...",
          TextMapId = "Content_200218_1"
        },
        Content_200219_1 = {
          ContentFR = "Aux abords de Givrelac, les trains vont et viennent sans fin, leurs sifflets saluant les voyageurs qui arrivent et ceux qui partent.\nMais aujourd’hui, quelque chose semble se tramer près du guichet — allez y jeter un œil.",
          TextMapId = "Content_200219_1"
        },
        Content_200219_2 = {
          ContentFR = "Un marchand venu d’ailleurs semble en difficulté. Allez lui parler.",
          TextMapId = "Content_200219_2"
        },
        Content_200219_3 = {
          ContentFR = "En bon Chasseur de Phoxène, vous ne sauriez ignorer un voyageur en détresse.\nConfrontez la vendeuse désagréable et réglez ce différend.",
          TextMapId = "Content_200219_3"
        },
        Content_200219_5 = {
          ContentFR = "Aidez la marchande étrangère à terminer son pliage — un souhait silencieux, né de mains attentives.",
          TextMapId = "Content_200219_5"
        },
        Content_200220_1 = {
          ContentFR = "En bon Chasseur de Phoxène, vous partez une nouvelle fois vers les Asphodèles afin d’y accepter de nouvelles missions. Peut-être y trouverez-vous quelque requête intrigante.",
          TextMapId = "Content_200220_1"
        },
        Content_200220_2 = {
          ContentFR = "Près du tableau des missions, un domestique inconnu marmonne pour lui-même. Approchez-vous et engagez la conversation.",
          TextMapId = "Content_200220_2"
        },
        Content_200220_3 = {
          ContentFR = "Après avoir formé une équipe de Chasseurs de Phoxène avec l’Étranger, Snow propose de décider du nom du groupe à travers une petite épreuve.",
          TextMapId = "Content_200220_3"
        },
        Content_200220_4 = {
          ContentFR = "Après la création de votre équipe, Snow suggère une épreuve pour déterminer qui en choisira le nom.\nD’après elle, Luno, l’exploratrice postée près du Sanctuaire, connaît bien ce genre de concours. Allez lui parler.",
          TextMapId = "Content_200220_4"
        },
        Content_200220_5 = {
          ContentFR = "Sous l’œil attentif de Luno, l’exploratrice, la première manche est sur le point de commencer !\nRendez-vous à l’endroit qu’elle a marqué pour débuter le défi.",
          TextMapId = "Content_200220_5"
        },
        Content_200220_6 = {
          ContentFR = "Le défi commence ! Suivez la piste de cercles lumineux indiquée par Luno, l’exploratrice jusqu’à la ligne d’arrivée.",
          TextMapId = "Content_200220_6"
        },
        Content_200220_7 = {
          ContentFR = "Sous la supervision de Luno, l’exploratrice, la seconde manche du défi est prête à débuter. Rendez-vous à l’endroit indiqué pour commencer.",
          TextMapId = "Content_200220_7"
        },
        Content_200220_8 = {
          ContentFR = "Le défi commence ! Ajustez votre visée et faites éclater les ballons fabriqués par Luno, la forgeronne.",
          TextMapId = "Content_200220_8"
        },
        Content_200220_9 = {
          ContentFR = "Le défi est terminé ! Vous avez triomphé de l’Étranger et remporté la victoire.\nIl ne vous reste plus qu’à trouver un nom digne de votre nouvelle équipe de Chasseurs de Phoxène.",
          TextMapId = "Content_200220_9"
        },
