        Mail_Content_100001 = {
          ContentFR = "Merci d’avoir pris le temps de partager votre avis. Ce présent vous est offert en gage de notre gratitude.",
          TextMapId = "Mail_Content_100001"
        },
        Mail_Content_100002 = {
          ContentFR = "Chasseur de Phoxène,\n\nMerci d’avoir pris le temps de répondre à notre sondage !\nVous trouverez votre récompense en pièce jointe.\n\nNous accordons une grande importance à chaque retour : vos commentaires nous aident à améliorer continuellement la qualité et l’expérience de jeu.",
          TextMapId = "Mail_Content_100002"
        },
        Mail_Content_100003 = {
          ContentFR = "Ceci est un e-mail de test contenant le coffret bonus 130004 (Lot intermédiaire de Sceaux démoniaques – combat rapproché).\nExpire dans 1 jour.",
          TextMapId = "Mail_Content_100003"
        },
        Mail_Content_100005 = {
          ContentFR = "Chasseur de Phoxène,\nVous avez des récompenses d’événement non réclamées. Elles ont été envoyées à votre messagerie. Puisse votre prochaine mission être couronnée de succès.",
          TextMapId = "Mail_Content_100005"
        },
        Mail_Content_10007 = {
          ContentFR = "Chasseur de Phoxène,\n\nVoici le détail de votre remboursement effectué durant la CBT2 :\n- Montant total rechargé : <H>{TotalRechargeMoney} ¥</> → Remboursé sous forme de <H>{Phoxene} Gemmes de Phoxène</>\n- Remboursement des Pass mensuels : <H>{MonthlyCard}</> Pass\n\nNous vous remercions sincèrement pour votre soutien et votre confiance envers Duet Night Abyss.",
          TextMapId = "Mail_Content_10007"
        },
        Mail_Content_10008 = {
          ContentFR = "Chasseur de Phoxène,\n\nMerci d’avoir pris le temps de répondre à notre sondage !\nVous trouverez votre récompense en pièce jointe.\n\nNous accordons une grande importance à chaque retour : vos commentaires nous aident à améliorer continuellement la qualité et l’expérience de jeu.",
          TextMapId = "Mail_Content_10008"
        },
        Mail_Content_10009 = {
          ContentFR = "Chasseur de Phoxène,\n\nVos <highlight>Sabliers prismatiques (temporaire)</> arrivent bientôt à expiration. Veuillez les utiliser sans tarder depuis la Myriade (Évocation).\nUne fois expirés, ils seront automatiquement convertis en une quantité équivalente de <highlight>Sabliers immaculés</>.",
          TextMapId = "Mail_Content_10009"
        },
        Mail_Content_10010 = {
          ContentFR = "Vous tentez actuellement de lier un compte.\nPour garantir la sécurité de votre compte, veuillez confirmer votre identité.\n\nVotre code de vérification : {Code}",
          TextMapId = "Mail_Content_10010"
        },
        Mail_Content_10011 = {
          ContentFR = "Chasseur de Phoxène,\n\nLes récompenses obtenues via les Twitch Drops ont été livrées sur votre compte. Veuillez les récupérer dans les pièces jointes.\n\nMerci pour votre soutien !\n\n— L’équipe de Duet Night Abyss",
          TextMapId = "Mail_Content_10011"
        },
        Mail_Content_10012 = {
          ContentFR = "Chasseur de Phoxène,\n\nLes récompenses obtenues via les CHZZK Drops ont été livrées sur votre compte de jeu. Veuillez les récupérer dans la pièce jointe ci-dessous.\n\nMerci pour votre soutien !\n\n— L’équipe de Duet Night Abyss",
          TextMapId = "Mail_Content_10012"
        },
        Mail_Content_10013 = {
          ContentFR = "« On va te dire un secret... aujourd’hui est un jour très important !\n On l’attendait depuis si longtemps ! Dès que minuit a sonné, on s’est précipités pour te préparer une surprise ! Après avoir marchandé, chassé les vils Souillés et bravé la nuit, on a enfin pu t’apporter ce cadeau !\n Parce qu’on voulait être les premiers à te dire...\nJoyeux anniversaire !\n Et maintenant, fais un vœu ! Que cette nouvelle année t’apporte encore plus d’aventures, de découvertes et de souvenirs à travers Atlasia, à nos côtés.\n Hein ? C’est plutôt nous qui venons de faire un vœu ! Bon, on recommence ! À toi, maintenant — dans ton cœur, formule ton souhait, il finira bien par se réaliser...\n\n — Les Lunariens d’Atlasia »",
          TextMapId = "Mail_Content_10013"
        }
      }
    end
