        MonsterRushEvent_Wuyou_DungeonDes01 = {
          ContentFR = "Un Wishen mystérieux, épée en main, surgit de nulle part et bloque votre route.\n  Peut-être a-t-il senti votre talent exceptionnel, car il semble prêt à vous enseigner une technique ou deux.\n  « Regarde bien... je ne montre ça qu’une seule fois. »",
          TextMapId = "MonsterRushEvent_Wuyou_DungeonDes01"
        },
        MonsterRushEvent_Wuyou_DungeonDes02 = {
          ContentFR = "À travers le feuillage, la lumière filtre doucement ; une brise légère effleure vos cheveux. Comme rappelé par quelque après-midi perdu dans une mémoire lointaine, le petit Wishen pousse un long bâillement.\n  « ... Les Wishen bâillent aussi ? »",
          TextMapId = "MonsterRushEvent_Wuyou_DungeonDes02"
