        },
        PartyReward_Name_530103 = {
          ContentFR = "Pièce des dieux",
          TextMapId = "PartyReward_Name_530103"
        },
        PartyTopic_Description_110101 = {
          ContentFR = "Elle versa d’étranges ingrédients au fond du chaudron, y mêlant la maladroite tendresse qu’elle portait au fond du cœur.",
          TextMapId = "PartyTopic_Description_110101"
        },
        PartyTopic_Description_110102 = {
          ContentFR = "Tu es la raison pour laquelle je ne peux fuir.",
          TextMapId = "PartyTopic_Description_110102"
        },
        PartyTopic_Description_110103 = {
          ContentFR = "Cette rive est tout mon monde — et le monde entier est ma cage.",
          TextMapId = "PartyTopic_Description_110103"
        },
        PartyTopic_Description_110104 = {
          ContentFR = "Tout est fini, et tout recommence.",
          TextMapId = "PartyTopic_Description_110104"
        },
        PartyTopic_Description_180101 = {
          ContentFR = "Elle confia ses souhaits magiques à ses amis.",
          TextMapId = "PartyTopic_Description_180101"
        },
        PartyTopic_Description_180102 = {
          ContentFR = "Elle offrit ses rêves oubliés aux étoiles.",
          TextMapId = "PartyTopic_Description_180102"
        },
        PartyTopic_Description_180103 = {
          ContentFR = "Elle enterra ses souvenirs dans des terres lointaines.",
          TextMapId = "PartyTopic_Description_180103"
        },
        PartyTopic_Description_180104 = {
          ContentFR = "Elle confia ses lendemains à venir aux pages blanches.",
          TextMapId = "PartyTopic_Description_180104"
        },
        PartyTopic_Description_210101 = {
          ContentFR = "Boum, boum... tu l’entends ? C’est le son de l’amour qui s’écoule.",
          TextMapId = "PartyTopic_Description_210101"
        },
        PartyTopic_Description_210102 = {
          ContentFR = "L’amour se mesure à l’épreuve. Face aux doux serments, une seule goutte de venin dit bien mieux la vérité. Soit la mort, soit l’éternité — pourquoi ne pas tenter votre chance ?",
          TextMapId = "PartyTopic_Description_210102"
        },
        PartyTopic_Description_210103 = {
          ContentFR = "Même les monstres ont leurs failles. Ce ne sont pas des démons sans âme, seulement des êtres à part, perdus parmi les hommes.",
          TextMapId = "PartyTopic_Description_210103"
        },
        PartyTopic_Description_210104 = {
          ContentFR = "L’amour est une lame qui tranche toutes les entraves — montagnes, océans ou poison.",
          TextMapId = "PartyTopic_Description_210104"
        },
        PartyTopic_Description_230101 = {
          ContentFR = "La dissimulation protège, l’imitation punit.",
          TextMapId = "PartyTopic_Description_230101"
        },
        PartyTopic_Description_230102 = {
          ContentFR = "Le bien et le mal se jugent par autrui, mais seul soi-même décide du juste et du faux.",
          TextMapId = "PartyTopic_Description_230102"
        },
        PartyTopic_Description_230103 = {
          ContentFR = "Ôtez la feuille qui vous cache les yeux, et le monde s’ouvre — plus vaste que vous ne l’imaginiez.",
          TextMapId = "PartyTopic_Description_230103"
        },
        PartyTopic_Description_230104 = {
          ContentFR = "Même dans le chaos, on peut encore trouver une chaleur.",
          TextMapId = "PartyTopic_Description_230104"
        },
        PartyTopic_Description_310101 = {
          ContentFR = "Vivre, c’est manger. Tant que vous savez manger pour vivre — et pas vivre seulement pour manger — ça ira.",
          TextMapId = "PartyTopic_Description_310101"
        },
        PartyTopic_Description_310102 = {
          ContentFR = "Il y a dans la haute tension une sorte de magie : elle vous attire, vous frôle, vous défie de toucher le danger pour en percer le secret.",
          TextMapId = "PartyTopic_Description_310102"
        },
        PartyTopic_Description_310103 = {
          ContentFR = "Plus cruel que perdre sa maison : la voir encore là, intacte... et savoir qu’on ne pourra jamais y revenir.",
          TextMapId = "PartyTopic_Description_310103"
        },
        PartyTopic_Description_310201 = {
          ContentFR = "Le rôle d’une servante couvre chaque aspect du quotidien — y compris, à l’occasion, des tâches qui laissent des taches de sang.",
          TextMapId = "PartyTopic_Description_310201"
        },
        PartyTopic_Description_310202 = {
          ContentFR = "On dit que la première impression décide de tout. Et la première impression, le plus souvent, c’est ce que vous portez. S’habiller, c’est une discipline qui ne vieillit jamais.",
          TextMapId = "PartyTopic_Description_310202"
        },
        PartyTopic_Description_310203 = {
          ContentFR = "Clic. Clic. Les ciseaux tranchent les fleurs fanées — et un peu des soucis du cœur.",
          TextMapId = "PartyTopic_Description_310203"
        },
        PartyTopic_Description_310204 = {
          ContentFR = "On a beau se cacher dans le moindre recoin... « grandir » finit toujours par nous retrouver.",
          TextMapId = "PartyTopic_Description_310204"
        },
        PartyTopic_Description_310301 = {
          ContentFR = "Il ne parle que d’explosifs et d’étincelles. C’est une façon... percutante de se présenter.",
          TextMapId = "PartyTopic_Description_310301"
        },
        PartyTopic_Description_310302 = {
          ContentFR = "Une visite qui tombe sans prévenir. Une conversation qui ne tombe pas juste.",
          TextMapId = "PartyTopic_Description_310302"
        },
        PartyTopic_Description_310303 = {
          ContentFR = "Parfois, « l’ennemi juré » n’est qu’une autre moitié de soi.",
          TextMapId = "PartyTopic_Description_310303"
        },
        PartyTopic_Description_310304 = {
          ContentFR = "Prisonniers d’une même nuit, ils n’avaient qu’une seule chandelle : la chaleur de l’autre.",
          TextMapId = "PartyTopic_Description_310304"
        },
        PartyTopic_Description_330101 = {
          ContentFR = "Un bon moral décuple l’efficacité.\nEt garder le moral, c’est aussi savoir quand prendre soin de soi.",
          TextMapId = "PartyTopic_Description_330101"
        },
        PartyTopic_Description_330102 = {
          ContentFR = "Même ce que les autres jettent — ce qu’ils fuient, ce qu’ils dédaignent — peut renaître autrement.",
          TextMapId = "PartyTopic_Description_330102"
        },
        PartyTopic_Description_330103 = {
          ContentFR = "Ce qui se transmet entre un père et sa fille, ce n’est pas seulement les habitudes, le métier, le sang, la manière de regarder le monde.\nParfois, c’est aussi le même surnom idiot.",
          TextMapId = "PartyTopic_Description_330103"
        },
        PartyTopic_Description_330104 = {
          ContentFR = "Le chemin d’un humain et celui d’un chat finissent toujours par diverger.\nOn se sépare en souriant, et l’on espère une prochaine rencontre.",
          TextMapId = "PartyTopic_Description_330104"
        },
        PartyTopic_Description_420201 = {
          ContentFR = "Chez les gens intelligents, chaque phrase sert un but. On pourrait appeler cela de la manipulation. Ou, si l’on est indulgent, une forme de franchise.",
          TextMapId = "PartyTopic_Description_420201"
        },
        PartyTopic_Description_420202 = {
          ContentFR = "Un « gentleman », ça ne tombe pas du ciel.\nOn le façonne : éducation, milieu... et la pression discrète de la famille.",
          TextMapId = "PartyTopic_Description_420202"
        },
        PartyTopic_Description_420203 = {
          ContentFR = "Ah, père...",
          TextMapId = "PartyTopic_Description_420203"
        },
        PartyTopic_Description_420204 = {
          ContentFR = "Une danse, avant que la cloche ne sonne.",
          TextMapId = "PartyTopic_Description_420204"
        },
        PartyTopic_Description_430101 = {
          ContentFR = "La vraie loyauté se mesure sous une lame suspendue, dont on ignore l’instant où elle tombera.",
          TextMapId = "PartyTopic_Description_430101"
        },
        PartyTopic_Description_430102 = {
          ContentFR = "La guerre n’est jamais partie. Elle a seulement changé de forme.",
          TextMapId = "PartyTopic_Description_430102"
        },
        PartyTopic_Description_430103 = {
          ContentFR = "Depuis que l’humanité errait entre forêt et steppe, l’outil n’a eu qu’un rôle : nous permettre de survivre.\nRien n’a changé — qu’il serve à créer ou à détruire.",
          TextMapId = "PartyTopic_Description_430103"
        },
        PartyTopic_Description_510101 = {
          ContentFR = "Manger est le besoin le plus simple des vivants. Mais bien manger, ça, c’est tout un art.",
          TextMapId = "PartyTopic_Description_510101"
        },
        PartyTopic_Description_510102 = {
          ContentFR = "« Le gaspillage, c’est la honte » — on nous le répète dès l’enfance.\nEt pourtant, plus on grandit, plus il devient difficile d’honorer cette règle.",
          TextMapId = "PartyTopic_Description_510102"
        },
        PartyTopic_Description_510103 = {
          ContentFR = "Aux yeux du monde, un cochon n’a rien d’exceptionnel. Mais Filbert, pour Truffle, est irremplaçable.",
          TextMapId = "PartyTopic_Description_510103"
        },
        PartyTopic_Description_510104 = {
          ContentFR = "Chacun a sa propre idée du bonheur. Mais quoi qu’il arrive, il faut un ventre plein pour avoir la force de le poursuivre.",
          TextMapId = "PartyTopic_Description_510104"
        },
        PartyTopic_Description_530101 = {
          ContentFR = "La valeur d’une vie ne se compte pas en jours, mais en la manière dont ces jours sont vécus.",
          TextMapId = "PartyTopic_Description_530101"
        },
        PartyTopic_Description_530102 = {
          ContentFR = "La mort n’est pas ce qu’il y a de plus effrayant.\nCe qui glace vraiment, c’est l’oubli.",
          TextMapId = "PartyTopic_Description_530102"
        },
        PartyTopic_Description_530103 = {
          ContentFR = "On ne sait jamais si demain viendra avant l’accident...\nMais pour demain, nous affrontons chaque imprévu.",
          TextMapId = "PartyTopic_Description_530103"
        },
        PartyTopic_Name_110101 = {
          ContentFR = "Saveur",
          TextMapId = "PartyTopic_Name_110101"
        },
        PartyTopic_Name_110102 = {
          ContentFR = "Blessures",
          TextMapId = "PartyTopic_Name_110102"
        },
        PartyTopic_Name_110103 = {
          ContentFR = "Au-delà",
          TextMapId = "PartyTopic_Name_110103"
        },
        PartyTopic_Name_110104 = {
          ContentFR = "Demain",
          TextMapId = "PartyTopic_Name_110104"
        }
      }
    end
