        Pet_IpDes_402 = {
          ContentFR = "Géniemon aquatique flottant dans les airs. Son corps translucide, recroquevillé sur lui-même, se fond si bien dans la lumière qu’on le distingue à peine.\nCe n’est qu’à la pluie qu’il s’éveille et s’étire, s’épanouissant dans le ciel tel une fleur de cristal.",
          TextMapId = "Pet_IpDes_402"
        },
        Pet_IpDes_403 = {
          ContentFR = "Géniemon dont plus de la moitié du corps est composée de glandes thermiques. Il suffit qu’il entrouvre la bouche pour qu’en jaillisse une vague brûlante, capable d’embraser l’air lui-même.",
          TextMapId = "Pet_IpDes_403"
        },
        Pet_IpDes_404 = {
          ContentFR = "Géniemon des hauts plateaux, célèbre pour son pelage à la conductivité exceptionnelle. Longtemps chassé par les humains, il s’est fait méfiant : à la moindre présence, il se roule en boule et dévale les pentes à la vitesse de l’éclair.",
          TextMapId = "Pet_IpDes_404"
        },
        Pet_IpDes_405 = {
          ContentFR = "Géniemon parasite qui s’installe dans les champignons, prenant peu à peu le contrôle de leur organisme.\nLe son « pouf pouf » qu’il émet provient du vent s’engouffrant dans la cavité entre son corps et l’enveloppe de son hôte.",
          TextMapId = "Pet_IpDes_405"
        },
        Pet_IpDes_406 = {
          ContentFR = "Géniemon vif, malicieux et cupide, passionné par tout ce qui brille.\nSi vous avez la chance d’en apercevoir un... vérifiez aussitôt si vos bijoux sont toujours à leur place.",
          TextMapId = "Pet_IpDes_406"
        },
        Pet_IpDes_407 = {
          ContentFR = "Géniemon charognard à tendance casanière.\nIl collectionne compulsivement tout ce qu’il peut entasser : une partie sert de nourriture, le reste de matériaux pour tapisser son nid intérieur.",
          TextMapId = "Pet_IpDes_407"
        },
        Pet_IpDes_408 = {
          ContentFR = "Géniemon d’une agilité remarquable. Ses longues oreilles, loin d’entraver ses mouvements, lui donnent une silhouette fuselée et parfaitement aérodynamique.",
          TextMapId = "Pet_IpDes_408"
        },
        Pet_IpDes_409 = {
          ContentFR = "Géniemon paresseux mais à la longévité légendaire.\nOn estime qu’il passe plus de 98 % de sa vie à dormir — un repos qui, semble-t-il, lui assure une éternelle jeunesse.",
          TextMapId = "Pet_IpDes_409"
        },
        Pet_IpDes_492 = {
          ContentFR = "Géniemon en forme d’appareil à impulsion électromagnétique, fruit de l’esprit fantasque d’alchimistes amoureux des Géniemon.\nPuisant dans la Résonance de soutien, il déchaîne autour de lui des ondes capables de blesser tous les ennemis à proximité.",
          TextMapId = "Pet_IpDes_492"
        },
