        PhantomTalk_1101_Chest = {
          ContentFR = "Il y a un coffre ici. On l’ouvre ensemble ?",
          TextMapId = "PhantomTalk_1101_Chest"
        },
        PhantomTalk_1102_Chest = {
          ContentFR = "Ces richesses inattendues... peut-être sont-elles la récompense que vous méritez.",
          TextMapId = "PhantomTalk_1102_Chest"
        },
        PhantomTalk_1103_Chest = {
          ContentFR = "Toutes les surprises ne se cachent pas derrière le rideau. Certaines se tiennent juste sous la lumière.",
          TextMapId = "PhantomTalk_1103_Chest"
        },
        PhantomTalk_1104_Chest = {
          ContentFR = "Il y a peut-être un trésor ici. Allez, {性别:mon jeune ami|ma jeune amie} : ajoute-le à notre collection.",
          TextMapId = "PhantomTalk_1104_Chest"
        },
        PhantomTalk_1501_Chest = {
          ContentFR = "Un coffre ? Éclate-le, Samaël ! — Non, attends... on se calme, pas aujourd’hui.",
          TextMapId = "PhantomTalk_1501_Chest"
        },
        PhantomTalk_1502_Chest = {
          ContentFR = "Sans doute un souvenir laissé par une âme partie vers la Mer du Silence, mon enfant...",
          TextMapId = "PhantomTalk_1502_Chest"
        },
        PhantomTalk_1503_Chest = {
          ContentFR = "Ce trésor mérite mieux que de pourrir sous terre. Allez, prends-le.",
          TextMapId = "PhantomTalk_1503_Chest"
        },
        PhantomTalk_160101_Chest = {
          ContentFR = "Pas mal... une belle surprise.",
          TextMapId = "PhantomTalk_160101_Chest"
        },
        PhantomTalk_1601_Chest = {
          ContentFR = "Pas mal... une belle surprise.",
          TextMapId = "PhantomTalk_1601_Chest"
        },
        PhantomTalk_1801_Chest = {
          ContentFR = "Les amis de Fina disent qu’ils ont flairé le trésor du roi...",
          TextMapId = "PhantomTalk_1801_Chest"
        },
        PhantomTalk_2101_Chest = {
          ContentFR = "L’amour a besoin de surprises pour s’épanouir. Tiens, en voilà une.",
          TextMapId = "PhantomTalk_2101_Chest"
        },
        PhantomTalk_2301_Chest = {
          ContentFR = "Oh ! Un joli butin ! Hihi... tu veux deviner ce qu’il y a dedans ?",
          TextMapId = "PhantomTalk_2301_Chest"
        },
        PhantomTalk_2401_Chest = {
          ContentFR = "Une telle trouvaille, en pleine nature... Cela suffira peut-être pour se procurer de quoi soigner ?",
          TextMapId = "PhantomTalk_2401_Chest"
        },
        PhantomTalk_3101_Chest = {
          ContentFR = "Une trouvaille ? On dirait que la chance commence à tourner.",
          TextMapId = "PhantomTalk_3101_Chest"
        },
        PhantomTalk_3102_Chest = {
          ContentFR = "Je l’avais dit : un peu de nettoyage, et les surprises ne tardent jamais.",
          TextMapId = "PhantomTalk_3102_Chest"
        },
        PhantomTalk_3103_Chest = {
          ContentFR = "Euh... Oliver dit qu’il y a... un truc brillant... pas loin.",
          TextMapId = "PhantomTalk_3103_Chest"
        },
        PhantomTalk_3201_Chest = {
          ContentFR = "Un bien sans maître ? Garde-le, ce n’est pas un crime contre la justice.",
          TextMapId = "PhantomTalk_3201_Chest"
        },
        PhantomTalk_3202_Chest = {
          ContentFR = "La chance aussi fait partie de la richesse. Accepte ces trouvailles sans honte.",
          TextMapId = "PhantomTalk_3202_Chest"
        },
        PhantomTalk_3301_Chest = {
          ContentFR = "Je sens l’odeur d’un trésor ! Quelle belle journée pour la récolte !",
          TextMapId = "PhantomTalk_3301_Chest"
        },
        PhantomTalk_4101_Chest = {
          ContentFR = "Zone périphérique dégagée. J’ai trouvé quelques fournitures abandonnées.",
          TextMapId = "PhantomTalk_4101_Chest"
        },
        PhantomTalk_4102_Chest = {
          ContentFR = "Terre en bas, feu au-dessus — l’or naît du labeur. Les signes ne m’ont pas trompé.",
          TextMapId = "PhantomTalk_4102_Chest"
        },
        PhantomTalk_4201_Chest = {
          ContentFR = "Ces biens terrestres me sont inutiles. Ils t’appartiennent désormais.",
          TextMapId = "PhantomTalk_4201_Chest"
        },
        PhantomTalk_4202_Chest = {
          ContentFR = "Un trésor ? Super. Tu crois qu’on peut l’échanger contre un jour de congé ?",
          TextMapId = "PhantomTalk_4202_Chest"
        },
        PhantomTalk_4301_Chest = {
          ContentFR = "Des trésors éparpillés ? Hmpf. Je n’ai aucun goût pour les proies qui ne se défendent pas.",
          TextMapId = "PhantomTalk_4301_Chest"
        },
        PhantomTalk_5101_Chest = {
          ContentFR = "Filbert ! Qu’est-ce que tu mâchouilles ? Attends... ça ne se mange pas ! Recrache !",
          TextMapId = "PhantomTalk_5101_Chest"
        },
        PhantomTalk_5102_Chest = {
          ContentFR = "Regarde ce que j’ai trouvé ! Comme toujours, moitié-moitié ?",
