        READ_DESC_2000008 = {
          ContentFR = "Un livre à la reliure exotique, usé par d’innombrables lectures. Ses pages sont si fragiles qu’un souffle suffirait à les détacher. En y regardant de plus près, on distingue qu’il traite de pharmacologie.",
          TextMapId = "READ_DESC_2000008"
        },
        READ_DESC_2000009 = {
