          ContentFR = "Forgé d’acier selon les plans de Lumière éphémère, ce corps éclatant dresse la lueur de l’instant contre l’ombre éternelle.",
          TextMapId = "RESOURCE_DESC_12063"
        },
        RESOURCE_DESC_12064 = {
          ContentFR = "Forgé selon les plans de Lumière éphémère, le canon ardent pulse d’un éclat fugace — aussi bref qu’inoubliable.",
          TextMapId = "RESOURCE_DESC_12064"
        },
        RESOURCE_DESC_12065 = {
          ContentFR = "Assemblé selon les plans de Plume du tisseur de rêves, ce verrou symbolise la déchirure du rêve au moment du tir — un battement d’ailes figé dans le feu.",
          TextMapId = "RESOURCE_DESC_12065"
        },
        RESOURCE_DESC_12066 = {
          ContentFR = "Forgé d’acier selon les plans de Plume du tisseur de rêves, son corps d’arme semble flotter, léger comme la brise, même lorsqu’il pèse d’un métal lourd.",
          TextMapId = "RESOURCE_DESC_12066"
        },
        RESOURCE_DESC_12067 = {
          ContentFR = "Forgé selon les plans de Plume du tisseur de rêves, le canon expulse sa lumière avec la douceur d’une aurore — brûlant d’un feu pur et silencieux.",
          TextMapId = "RESOURCE_DESC_12067"
        },
        RESOURCE_DESC_12068 = {
          ContentFR = "Conçu selon les plans du Trancheur d’ambre, ce manche poli reflète la lumière — un équilibre parfait entre élégance et efficacité.",
          TextMapId = "RESOURCE_DESC_12068"
        },
        RESOURCE_DESC_12069 = {
          ContentFR = "Forgée d’après les plans du Trancheur d’ambre, sa lame miroitante tranche l’air avec une grâce mortelle, née uniquement pour le duel.",
          TextMapId = "RESOURCE_DESC_12069"
        },
        RESOURCE_DESC_12070 = {
          ContentFR = "Façonné selon les plans de l’Écho du passé, ce manche relie passé et présent — un prolongement naturel de la mémoire du porteur.",
          TextMapId = "RESOURCE_DESC_12070"
        },
        RESOURCE_DESC_12071 = {
          ContentFR = "Forgée selon les plans d’Écho du passé, cette lame silencieuse fend le temps lui-même — chaque coup une réminiscence.",
          TextMapId = "RESOURCE_DESC_12071"
        },
        RESOURCE_DESC_19000 = {
          ContentFR = "Là où s’entrelacent d’innombrables traces d’âme, une Empreinte prend forme — Polyphonie.",
          TextMapId = "RESOURCE_DESC_19000"
        },
        RESOURCE_DESC_19001 = {
