        UI_CHAR_DATA_TEXT_21010301 = {
          ContentFR = "Rebecca se moque éperdument de ce que les gens pensent d’elle. Elle ne vit que pour deux choses :\nCréer des parfums envoûtants, imprégnés d’amour, et les offrir à celles et ceux qui rêvent d’aimer. Et trouver son être destiné, capable d’accepter la beauté... et le venin.",
          TextMapId = "UI_CHAR_DATA_TEXT_21010301"
