        },
        UI_DUNGEON_CONTENT_EXTERMINATE_1 = {
          ContentFR = "« La vie trouve toujours la place qui lui revient. Mais lorsque certaines formes de vie dépassent leurs limites, la solution est simple : les chasser toutes, sans exception. Rien de plus. »\n — D’un commanditaire anonyme",
          TextMapId = "UI_DUNGEON_CONTENT_EXTERMINATE_1"
