        voice_ch_char_Lise_vo_topic_06_2 = {
          ContentFR = "Oui... Le travail remplit mes journées, il donne du sens à ce que je fais. Il me rend un peu plus heureuse. Quand je travaille, j’ai l’impression que même le soleil brille plus fort.",
          TextMapId = "voice_ch_char_Lise_vo_topic_06_2"
        },
        voice_ch_char_Lise_vo_topic_07_1 = {
          ContentFR = "Mieux que si j’étais restée, en tout cas. La Légion, c’était mon refuge... le lieu où j’avais placé tous mes rêves. Mais ces rêves se sont révélés être des mensonges, et ce que je croyais être un abri s’est transformé en enfer, celui qui a englouti ma famille à jamais...",
          TextMapId = "voice_ch_char_Lise_vo_topic_07_1"
        },
        voice_ch_char_Lise_vo_topic_07_2 = {
          ContentFR = "Je ne peux pas tourner la page sur cette haine. Mais je n’arrive pas non plus à pointer mon arme sur ceux qui ont été mes camarades. Je suis perdue... Dis-moi, qu’est-ce que je suis censée faire ?",
          TextMapId = "voice_ch_char_Lise_vo_topic_07_2"
        },
        voice_ch_char_Lise_vo_topic_07_3 = {
          ContentFR = "...Pardon, je me suis laissée emporter. Tu as raison, c’est à moi de trouver la réponse, pas à toi de la porter. Même si ça risque de prendre longtemps...",
          TextMapId = "voice_ch_char_Lise_vo_topic_07_3"
        },
        voice_ch_char_Lise_vo_victory = {
          ContentFR = "C’est dommage que ce soit fini si vite. Pour la prochaine, pense à moi. Tu remporteras la victoire, et moi j’aurai de quoi faire.",
          TextMapId = "voice_ch_char_Lise_vo_victory"
        },
        voice_ch_char_Lise_vo_welcome = {
          ContentFR = "Patrouille terminée aujourd’hui. Aucun danger, aucune fissure dans les murs, aucun signe suspect. Menaces écartées. La vie peut reprendre son cours normal. Rapport terminé.",
          TextMapId = "voice_ch_char_Lise_vo_welcome"
        },
        voice_ch_char_Maer_vo_appear = {
          ContentFR = "Alors, où est-ce qu’on va fouiller aujourd’hui ?",
          TextMapId = "voice_ch_char_Maer_vo_appear"
        },
        voice_ch_char_Maer_vo_battleidle = {
          ContentFR = "Allez, allez ! Il y a encore de la place !",
          TextMapId = "voice_ch_char_Maer_vo_battleidle"
        },
        voice_ch_char_Maer_vo_battlestart = {
          ContentFR = "Voyons voir ce qu’on trouve cette fois !",
          TextMapId = "voice_ch_char_Maer_vo_battlestart"
        },
        voice_ch_char_Maer_vo_be_hit_heavy_r01 = {
          ContentFR = "Ça fait vraiment mal !",
          TextMapId = "voice_ch_char_Maer_vo_be_hit_heavy_r01"
        },
        voice_ch_char_Maer_vo_be_hit_heavy_r02 = {
          ContentFR = "Ouille ouille ouille !",
          TextMapId = "voice_ch_char_Maer_vo_be_hit_heavy_r02"
        },
        voice_ch_char_Maer_vo_birthday = {
          ContentFR = "Joyeux anniversaire ! Tiens, je t’ai fabriqué une petite poupée de Touffu, assortie à la mienne. Je sais que tu meurs d’envie de la serrer dans tes bras depuis longtemps, hein ? Hé hé, cette détective taciturne t’a bien cernée !",
          TextMapId = "voice_ch_char_Maer_vo_birthday"
        },
        voice_ch_char_Maer_vo_companio_01 = {
          ContentFR = "Regardez le tissu, l’écusson et le cuir ! Mme Hilda, vous êtes mon ange ! Si vous avez d’autres biens mal acquis qui doivent être \"éliminés correctement\", je suis votre associée ! Vous pouvez compter sur moi pour m’en occuper !",
          TextMapId = "voice_ch_char_Maer_vo_companio_01"
        },
        voice_ch_char_Maer_vo_companio_02 = {
          ContentFR = "Petite Fina ! Ça fait une éternité. Tu veux connaître la suite de \"Flocon la Ranger et Gingembre la Détective\" ? Parfait ! Je vais chercher un petit tabouret et te raconter ça tranquillement !",
          TextMapId = "voice_ch_char_Maer_vo_companio_02"
        },
        voice_ch_char_Maer_vo_companio_03 = {
          ContentFR = "Je vous assure, Madame la geôlière, que je suis une citoyenne respectable ! Je n’ai jamais volé ni pillé, je me contente juste de récupérer quelques restes dans les poubelles pour calmer ma faim. Même votre zèle ne va pas jusqu’aux ordures, si ?",
          TextMapId = "voice_ch_char_Maer_vo_companio_03"
        },
        voice_ch_char_Maer_vo_companio_04 = {
          ContentFR = "Tu vis au jour le jour, n’est-ce pas... ? C’est vraiment difficile. Tu veux venir avec moi faire un peu de récupération ? Ça pourrait te faciliter un peu les choses...",
          TextMapId = "voice_ch_char_Maer_vo_companio_04"
        },
        voice_ch_char_Maer_vo_companio_05 = {
          ContentFR = "Camilla ! Toi, la plus gentille et la plus belle de Givrelac ! J’ai un tout petit, tout petit problème, tu pourrais m’aider à m’en sortir... Promis, c’est la dernière fois ! Euh, enfin, la dernière fois ce mois-ci ! Tu vas pas me dire non, hein ?",
          TextMapId = "voice_ch_char_Maer_vo_companio_05"
        },
        voice_ch_char_Maer_vo_die_r01 = {
          ContentFR = "Moi aussi... je vais me faire recycler ?",
          TextMapId = "voice_ch_char_Maer_vo_die_r01"
        },
        voice_ch_char_Maer_vo_die_r02 = {
          ContentFR = "Si seulement je pouvais voir Givrelac... une dernière fois...",
          TextMapId = "voice_ch_char_Maer_vo_die_r02"
        },
        voice_ch_char_Maer_vo_fail = {
          ContentFR = "Ce n’est pas grave, l’important c’est de rester en vie !",
          TextMapId = "voice_ch_char_Maer_vo_fail"
        },
        voice_ch_char_Maer_vo_gain_1 = {
          ContentFR = "Salut toi, bienvenue à Givrelac ! Tu te sens un peu {性别:perdu|perdue} ? Laisse-moi, Margie, te faire découvrir les merveilles de cet endroit. Premier arrêt... une multitude de poubelles surprises !",
          TextMapId = "voice_ch_char_Maer_vo_gain_1"
        },
        voice_ch_char_Maer_vo_gain_2 = {
          ContentFR = "Quoi ? Tu as déjà entendu parler de mon titre de \"Princesse des Déchets\" ?! Euh... Ne va surtout pas répéter ça ! Appelle-moi juste Margie. Pas de princesse ici, juste une petite récupératrice de Givrelac !",
          TextMapId = "voice_ch_char_Maer_vo_gain_2"
        },
        voice_ch_char_Maer_vo_idle = {
          ContentFR = "Hmm, quel temps magnifique, parfait pour cueillir des fleurs et sentir bon après ! {nickname}, tu veux venir te balader avec moi ?",
          TextMapId = "voice_ch_char_Maer_vo_idle"
        },
        voice_ch_char_Maer_vo_lvup = {
          ContentFR = "Ouah ! Mes poches à butin sont maintenant super spacieuses !",
          TextMapId = "voice_ch_char_Maer_vo_lvup"
        },
        voice_ch_char_Maer_vo_revive = {
          ContentFR = "Recyclée... et prête à briller !",
          TextMapId = "voice_ch_char_Maer_vo_revive"
        },
        voice_ch_char_Maer_vo_skill01_r01 = {
          ContentFR = "Recyclable !",
          TextMapId = "voice_ch_char_Maer_vo_skill01_r01"
        },
        voice_ch_char_Maer_vo_skill01_r02 = {
          ContentFR = "Non recyclable !",
          TextMapId = "voice_ch_char_Maer_vo_skill01_r02"
        },
        voice_ch_char_Maer_vo_skill02_r01 = {
          ContentFR = "Il est temps d’incinérer quelques déchets !",
          TextMapId = "voice_ch_char_Maer_vo_skill02_r01"
        },
        voice_ch_char_Maer_vo_skill02_r02 = {
          ContentFR = "À votre place, parmi les déchets !",
          TextMapId = "voice_ch_char_Maer_vo_skill02_r02"
        },
        voice_ch_char_Maer_vo_skill02_r03 = {
          ContentFR = "Brûlez ! Enterrez ! Explosez !",
          TextMapId = "voice_ch_char_Maer_vo_skill02_r03"
        },
        voice_ch_char_Maer_vo_topic_01_1 = {
          ContentFR = "Eh bien... il s’agit de donner une nouvelle vie aux objets mis au rebut et d’offrir à ceux qui en ont besoin une chance d’avoir une vie meilleure.",
          TextMapId = "voice_ch_char_Maer_vo_topic_01_1"
        },
        voice_ch_char_Maer_vo_topic_01_2 = {
          ContentFR = "Par exemple, transformer les rideaux de velours dont se sont débarrassés des aristocrates en manteaux d’hiver chauds pour les personnes âgées, ou apporter des poupées jetées à des enfants dont les seuls jouets sont des cailloux. Ils seront ravis pendant longtemps.",
          TextMapId = "voice_ch_char_Maer_vo_topic_01_2"
        },
        voice_ch_char_Maer_vo_topic_01_3 = {
          ContentFR = "Une fois, j’ai ramené une vieille bibliothèque toute cassée que quelqu’un avait jetée. J’étais épuisée à en tomber, mais rapidement, elle est devenue un terrain de jeu pour une portée de chatons et leurs mamans, ils y ont joué du lever au coucher du soleil. Ça en valait vraiment la peine !",
          TextMapId = "voice_ch_char_Maer_vo_topic_01_3"
        },
        voice_ch_char_Maer_vo_topic_01_4 = {
          ContentFR = "Voilà ce que c’est que d’être récupérateur ! Ce sont surtout des petites choses, mais on ne s’ennuie jamais. Alors, qu’en dis-tu, tu veux te joindre à nous ?",
          TextMapId = "voice_ch_char_Maer_vo_topic_01_4"
        },
        voice_ch_char_Maer_vo_topic_02_1 = {
          ContentFR = "Chuuuut ! C’est un secret bien gardé des récupérateurs ! Je ne t’aurais pas mis dans la confidence si tu ne m’avais pas aidé à attraper ce voleur !",
          TextMapId = "voice_ch_char_Maer_vo_topic_02_1"
        },
        voice_ch_char_Maer_vo_topic_02_2 = {
          ContentFR = "Cette carte répertorie toutes les poubelles de Givrelac. J’y ai noté celles où on trouve les meilleurs trésors. J’ai même remplacé ces poubelles par des modèles plus grands, et je calcule précisément le meilleur moment pour y passer.",
          TextMapId = "voice_ch_char_Maer_vo_topic_02_2"
        },
        voice_ch_char_Maer_vo_topic_02_3 = {
          ContentFR = "Mais ce métier dépend beaucoup de la chance, la plupart du temps c’est du hasard...",
          TextMapId = "voice_ch_char_Maer_vo_topic_02_3"
        },
        voice_ch_char_Maer_vo_topic_02_4 = {
          ContentFR = "Ah, je savais que tu comprendrais !",
          TextMapId = "voice_ch_char_Maer_vo_topic_02_4"
        },
        voice_ch_char_Maer_vo_topic_03_1 = {
          ContentFR = "Non, pas du tout ! J’adore Givrelac de tout mon cœur. C’est juste que je repense souvent aux jours où je parcourais les champs de neige avec mon père, c'est comme ça que j’ai grandi.",
          TextMapId = "voice_ch_char_Maer_vo_topic_03_1"
        },
        voice_ch_char_Maer_vo_topic_03_2 = {
          ContentFR = "D’ici, les champs de neige ressemblent à un paysage de conte de fées. Mais une fois que tu y mets les pieds, tu te sens tout petit. Tu peux marcher pendant des heures dans la neige jusqu’aux genoux, cette sensation ne te quitte jamais.",
          TextMapId = "voice_ch_char_Maer_vo_topic_03_2"
        },
        voice_ch_char_Maer_vo_topic_03_3 = {
          ContentFR = "Je ne déteste pas ces moments-là. Mais Givrelac animé et vivant est mon préféré !",
          TextMapId = "voice_ch_char_Maer_vo_topic_03_3"
        },
        voice_ch_char_Maer_vo_topic_04_1 = {
          ContentFR = "C’est le gros chat qui me suivait dans les champs enneigés quand j’étais petite ! On l’appelait Touffu. Il ne me laissait le caresser qu’en échange d’un peu de viande séchée, mais sa fourrure... oh là là, c’est un vrai bonheur, hé hé hé, la meilleure qui soit !",
          TextMapId = "voice_ch_char_Maer_vo_topic_04_1"
        },
        voice_ch_char_Maer_vo_topic_04_2 = {
          ContentFR = "Mmmh... ce n’est sûrement pas un « miaou miaou », c’est plutôt un « grrraou, grrraou, grrraou » !",
          TextMapId = "voice_ch_char_Maer_vo_topic_04_2"
        },
        voice_ch_char_Maer_vo_topic_04_3 = {
          ContentFR = "Touffu préfère la vie sauvage à la vie urbaine. Nous avons dû nous séparer, mais elle fera toujours partie de moi, tout comme les champs de neige.",
          TextMapId = "voice_ch_char_Maer_vo_topic_04_3"
        },
        voice_ch_char_Maer_vo_topic_05_1 = {
          ContentFR = "Non, non ! Ce n’est pas moi ! Je suis trop grande pour un titre aussi enfantin ! Je t’en supplie, ne m’appelle plus jamais comme ça !",
          TextMapId = "voice_ch_char_Maer_vo_topic_05_1"
        },
        voice_ch_char_Maer_vo_topic_05_2 = {
          ContentFR = "Hem hem... Dame Margie, la détective taciturne, la solitaire qui arpente les ruelles de la ville ! Aujourd’hui encore, je vais te révéler les secrets cachés de Givrelac !",
          TextMapId = "voice_ch_char_Maer_vo_topic_05_2"
        },
        voice_ch_char_Maer_vo_topic_05_3 = {
          ContentFR = "Hé hé, voilà un titre qui me va bien !",
          TextMapId = "voice_ch_char_Maer_vo_topic_05_3"
        },
        voice_ch_char_Maer_vo_victory = {
          ContentFR = "Quelle belle récolte aujourd’hui !",
          TextMapId = "voice_ch_char_Maer_vo_victory"
        },
        voice_ch_char_Maer_vo_welcome = {
          ContentFR = "En arrivant, j’ai dégagé la neige devant ta porte ! Touchée ? Ça mérite bien un chocolat chaud, non ?",
          TextMapId = "voice_ch_char_Maer_vo_welcome"
        },
        voice_ch_char_Nifu_vo_appear = {
          ContentFR = "Je ferai tout ce qui est en mon modeste pouvoir pour t’aider.",
          TextMapId = "voice_ch_char_Nifu_vo_appear"
        },
        voice_ch_char_Nifu_vo_battleidle = {
          ContentFR = "Si la fatigue t’accable, viens te reposer ici un instant. Ne te force pas.",
          TextMapId = "voice_ch_char_Nifu_vo_battleidle"
        },
        voice_ch_char_Nifu_vo_battlestart = {
          ContentFR = "À la lisière du fleuve de la vie, je demeure, veilleuse des âmes naissantes.",
          TextMapId = "voice_ch_char_Nifu_vo_battlestart"
        },
        voice_ch_char_Nifu_vo_be_hit_heavy_r01 = {
          ContentFR = "... Ce n’est rien.",
          TextMapId = "voice_ch_char_Nifu_vo_be_hit_heavy_r01"
        },
        voice_ch_char_Nifu_vo_be_hit_heavy_r02 = {
          ContentFR = "Cela passera.",
          TextMapId = "voice_ch_char_Nifu_vo_be_hit_heavy_r02"
        },
        voice_ch_char_Nifu_vo_birthday = {
          ContentFR = "{性别:Mon enfant, une année de plus s’est écoulée, et je me réjouis sincèrement de ta survie.\nFais un vœu, car c’est en rêvant que l’on éclaire l’avenir. À ton âge, il est naturel de porter en soi des espoirs qui guident le chemin devant toi.\nAucune rivière ne coule sans rencontrer de tourments. Même blessé par les épines ou égaré par le courant, je souhaite que tu avances avec courage.\nEt lorsque tu atteindras enfin ta rive, puisses-tu chérir chaque instant du voyage qui t’y a mené.\nSi jamais le doute t’étreint, ou que tu cherches simplement une histoire, viens trouver refuge dans mes bras. Je prierai pour toi, je te bénirai, aujourd’hui, demain, et pour toujours.|Ma chère enfant, une année de plus s’est écoulée, et je me réjouis sincèrement de ta survie. \nFais un vœu, car c’est en rêvant que l’on éclaire l’avenir. À ton âge, il est naturel de porter en soi des espoirs qui guident le chemin devant toi. \nAucune rivière ne coule sans rencontrer de tourments. Même blessée par les épines ou égarée par le courant, je souhaite que tu avances avec courage.Et lorsque tu atteindras enfin ta rive, puisses-tu chérir chaque instant du voyage qui t’y a menée. Si jamais le doute t’étreint, ou que tu cherches simplement une histoire, viens trouver refuge dans mes bras. Je prierai pour toi, je te bénirai, aujourd’hui, demain, et pour toujours.}",
          TextMapId = "voice_ch_char_Nifu_vo_birthday"
        },
        voice_ch_char_Nifu_vo_companio_01 = {
          ContentFR = "Nul ne peut fuir son passé éternellement. Un jour viendra où tu devras lui faire face... Puisses-tu alors trouver la force qui te permettra de le combattre.",
          TextMapId = "voice_ch_char_Nifu_vo_companio_01"
        },
        voice_ch_char_Nifu_vo_companio_02 = {
          ContentFR = "Ma chère enfant, il y a en toi une essence qui me semble familière... Serait-ce que toi aussi, tu viens d’une époque révolue, comme moi ?",
          TextMapId = "voice_ch_char_Nifu_vo_companio_02"
        },
        voice_ch_char_Nifu_vo_companio_03 = {
          ContentFR = "Bonjour, mon enfant. Hé hé... Oui, nos cornes se ressemblent, et peut-être que nos chemins se sont croisés jadis. Mais ce passé n’est pas le tien, laisse-le de côté, et suis ce que ton cœur désire.",
          TextMapId = "voice_ch_char_Nifu_vo_companio_03"
        },
        voice_ch_char_Nifu_vo_companio_04 = {
          ContentFR = "Ma mémoire s’étend sur des siècles, et pourtant elle est emplie de tant de cruauté. Peut-être qu’oublier est une forme de bénédiction... Sache que ce qui importe vraiment finira toujours par revenir, même si tu l’oublies.",
          TextMapId = "voice_ch_char_Nifu_vo_companio_04"
        },
        voice_ch_char_Nifu_vo_die_r01 = {
          ContentFR = "N’aie crainte... comme toujours, je serai bientôt...",
          TextMapId = "voice_ch_char_Nifu_vo_die_r01"
        },
        voice_ch_char_Nifu_vo_die_r02 = {
          ContentFR = "Je profite de cette courte trêve, sans douleur ni tourment.",
          TextMapId = "voice_ch_char_Nifu_vo_die_r02"
        },
        voice_ch_char_Nifu_vo_fail = {
          ContentFR = "Peu importe. Victoires et défaites ne sont que des instants passagers. Reprends-toi, et repars plus fort.",
          TextMapId = "voice_ch_char_Nifu_vo_fail"
        },
        voice_ch_char_Nifu_vo_gain = {
          ContentFR = "Je suis Nifle, la Passeuse de la Mer du Silence. N’aie crainte, la Mer du Silence ne s’étend pas ici, et tu n’es pas une âme errante. Considère-moi simplement comme une passante... ou peut-être une témoin de ton voyage.",
          TextMapId = "voice_ch_char_Nifu_vo_gain"
        },
        voice_ch_char_Nifu_vo_idle = {
          ContentFR = "Dans ce monde en proie aux tourments, trouver un havre de paix pour s’accorder un bref répit est déjà un doux privilège.",
          TextMapId = "voice_ch_char_Nifu_vo_idle"
        },
        voice_ch_char_Nifu_vo_lvup = {
          ContentFR = "Telle la douce renaissance du printemps, où les herbes percent la terre et les branches s’ornent de bourgeons.",
          TextMapId = "voice_ch_char_Nifu_vo_lvup"
        },
        voice_ch_char_Nifu_vo_revive = {
          ContentFR = "Il semble que mon heure ne soit pas encore venue.",
          TextMapId = "voice_ch_char_Nifu_vo_revive"
        },
        voice_ch_char_Nifu_vo_skill01_r01 = {
          ContentFR = "Battement du samsara.",
          TextMapId = "voice_ch_char_Nifu_vo_skill01_r01"
        },
        voice_ch_char_Nifu_vo_skill01_r02 = {
          ContentFR = "La nuit s’efface devant l’aurore.",
          TextMapId = "voice_ch_char_Nifu_vo_skill01_r02"
        },
        voice_ch_char_Nifu_vo_skill02_r01 = {
          ContentFR = "Tels les larmes pleines de grâce de la Mère Embla.",
          TextMapId = "voice_ch_char_Nifu_vo_skill02_r01"
        },
        voice_ch_char_Nifu_vo_skill02_r02 = {
          ContentFR = "Toutes les rivières retournent à la Mer du Silence.",
          TextMapId = "voice_ch_char_Nifu_vo_skill02_r02"
        },
        voice_ch_char_Nifu_vo_skill02_r03 = {
          ContentFR = "Âme perdue, sois tranquille, suis ma voix qui te guide.",
          TextMapId = "voice_ch_char_Nifu_vo_skill02_r03"
        },
        voice_ch_char_Nifu_vo_topic_01_1 = {
          ContentFR = "Oui, durant ces longues années, j’ai parcouru Atlasia, ou plutôt, j’ai erré. J’ai foulé chaque parcelle de terre et caressé chaque grain de sable du bout des doigts.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_01_1"
        },
        voice_ch_char_Nifu_vo_topic_01_2 = {
          ContentFR = "Quand on a vu assez d’endroits, on réalise que les paysages sont semblables. Qu’il s'agisse des neiges du Nord, des sables de Khemet ou de la fumée qui s’échappe des cheminées de Huaxu, ce ne sont que différentes nuances de brume flottant à travers la forêt.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_01_2"
        },
        voice_ch_char_Nifu_vo_topic_01_3 = {
          ContentFR = "Si je devais choisir... Ce serait les montagnes et les forêts d’Aethyrie. L’air qui y règne m'apporte la paix.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_01_3"
        },
        voice_ch_char_Nifu_vo_topic_01_4 = {
          ContentFR = "Peut-être le feras-tu.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_01_4"
        },
        voice_ch_char_Nifu_vo_topic_01_5 = {
          ContentFR = "Si un jour tu te rends dans la Forêt d’Émeraude, pourrais-tu m’y rapporter un bouquet d’asters blancs ? Sous cette terre reposent mes amis, mon enfant, et une part de moi-même d’autrefois.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_01_5"
        },
        voice_ch_char_Nifu_vo_topic_02_1 = {
          ContentFR = "La Mer du Silence est le berceau et le dernier refuge de tous les êtres vivants. C’est vers elle que retournent les défunts, et c’est d’elle que naît à nouveau la vie... Tout comme les nuages mourants se fondent en pluie dans la terre, pour que les fleurs y renaissent.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_02_1"
        },
        voice_ch_char_Nifu_vo_topic_02_2 = {
          ContentFR = "Je le crois, mon enfant. Un jour, tu rejoindras la Mer du Silence, dans les bras de la Déesse Embla.",
          TextMapId = "voice_ch_char_Nifu_vo_topic_02_2"
        },
        voice_ch_char_Nifu_vo_topic_02_3 = {
          ContentFR = "Mon enfant, aime tout ce que la vie offre, mais n’aie pas peur de la fin. La mort n’est pas une souffrance, mais la conclusion inévitable.",
